DROP TABLE backgrounds;
CREATE TABLE backgrounds (
  id INTEGER PRIMARY KEY,
  name TEXT NOT NULL,
  skill_proficiencies TEXT,
  tool_proficiencies TEXT,
  languages TEXT,
  equiptment TEXT
);

DROP TABLE background_features;
CREATE TABLE background_features (
  id INTEGER PRIMARY KEY,
  backgrounds_id INTEGER NOT NULL,
  name TEXT NOT NULL,
  description TEXT NOT NULL,
  FOREIGN KEY (backgrounds_id) REFERENCES backgrounds(id)
);


