DROP TABLE class;
CREATE TABLE class (
	id INTEGER PRIMARY KEY,
	name TEXT NOT NULL
);

DROP TABLE archetype;
CREATE TABLE archetype (
	id INTEGER PRIMARY KEY,
	class_id INTEGER NOT NULL,
	name TEXT NOT NULL,
	description TEXT,
	FOREIGN KEY (class_id) REFERENCES class(id)
);

DROP TABLE class_feature;
CREATE TABLE class_feature (
	id INTEGER PRIMARY KEY,
	class_id INTEGER NOT NULL,
	name TEXT NOT NULL,
	summary TEXT,
	description TEXT,
	level INTEGER,
	FOREIGN KEY (class_id) REFERENCES class(id)
);

DROP TABLE archetype_feature;
CREATE TABLE archetype_feature (
	id INTEGER PRIMARY KEY,
	archetype_id INTEGER NOT NULL,
	name TEXT NOT NULL,
	summary TEXT,
	description TEXT,
	level INTEGER,
	FOREIGN KEY (archetype_id) REFERENCES archetype(id)
);

DROP TABLE archetype_sub_feature;
CREATE TABLE archetype_sub_feature (
	id INTEGER PRIMARY KEY,
	archetype_feature_id INTEGER NOT NULL,
	name TEXT NOT NULL,
	prerequisite TEXT,
	description TEXT,
	FOREIGN KEY	 (archetype_feature_id) REFERENCES archetype_feature(id)
);

DROP TABLE class_sub_feature;
CREATE TABLE class_sub_feature (
	id INTEGER PRIMARY KEY,
	class_feature_id INTEGER NOT NULL,
	name TEXT NOT NULL,
	prerequisite TEXT,
	description TEXT,
	FOREIGN KEY	 (class_feature_id) REFERENCES class_feature(id)
);


