INSERT INTO class_sub_feature (id, class_feature_id, name, prerequisite, description) VALUES (NULL, 63, "Fiends", NULL, NULL);
INSERT INTO class_sub_feature (id, class_feature_id, name, prerequisite, description) VALUES (NULL, 63, "Giants", NULL, NULL);
