INSERT INTO class_sub_features VALUES (NULL, 89, "Dragons", NULL, NULL, 0); -- 79
INSERT INTO class_sub_features VALUES (NULL, 89, "Elementals", NULL, NULL, 0); -- 80
INSERT INTO class_sub_features VALUES (NULL, 89, "Fey", NULL, NULL, 0); -- 81
INSERT INTO class_sub_features VALUES (NULL, 89, "Fiends", NULL, NULL, 0); -- 82
INSERT INTO class_sub_features VALUES (NULL, 89, "Giants", NULL, NULL, 0); -- 83
INSERT INTO class_sub_features VALUES (NULL, 89, "Monstrosities", NULL, NULL, 0); -- 84
INSERT INTO class_sub_features VALUES (NULL, 89, "Oozes", NULL, NULL, 0); -- 85
INSERT INTO class_sub_features VALUES (NULL, 89, "Plants", NULL, NULL, 0); -- 86
INSERT INTO class_sub_features VALUES (NULL, 89, "Undead", NULL, NULL, 0); -- 87
INSERT INTO class_sub_features VALUES (NULL, 89, "Humanoids", NULL, NULL, 0); -- 88
INSERT INTO class_features VALUES (NULL, 8, "Natural Explorer", "When in familiar terrain Int and Wis checks double proficiency bonus. After 1 hour gain: <br><ul><li>Difficult terrain doesn't slow you.</li><li>Can't become lost except by magical means.</li><li>Always alert to danger.</li><li>If alone, you move stealthily at a normal pace.</li><li>You find twice as much food as normally.</li><li>While tracking, you learn all details.</li></ul>", "You are particularly familiar with one type of natural environment and are adept at traveling and surviving in such regions.<br>When you make an Intelligence or Wisdom check related to your favored terrain, your proficiency bonus is doubled if you are using a skill that you're proficient in.<br>While traveling for an hour or more in your favored terrain, you gain the following benefits:<br><ul><li>Difficult terrain doesn't slow your group's travel.</li><li>Your group can't become lost except by magical means.</li><li>Even when you are engaged in another activity while traveling (such as foraging, navigating, or tracking), you remain alert to danger.</li><li>If you are traveling alone, you can move stealthily at a normal pace.</li><li>When you forage, you find twice as much food as you normally would.</li><li>While tracking other creatures, you also learn their exact number, their sizes, and how long ago they passed through the area.</li></ul><br>You choose additional favored terrain types at 6th and 10th level.", 1, 0); -- 90
INSERT INTO class_sub_features VALUES (NULL, 90, "Arctic", NULL, NULL, 0); -- 89
INSERT INTO class_sub_features VALUES (NULL, 90, "Coast", NULL, NULL, 0); -- 90
INSERT INTO class_sub_features VALUES (NULL, 90, "Desert", NULL, NULL, 0); -- 91
INSERT INTO class_sub_features VALUES (NULL, 90, "Forest", NULL, NULL, 0); -- 92
INSERT INTO class_sub_features VALUES (NULL, 90, "Grassland", NULL, NULL, 0); -- 93
INSERT INTO class_sub_features VALUES (NULL, 90, "Mountain", NULL, NULL, 0); -- 94
INSERT INTO class_sub_features VALUES (NULL, 90, "Swamp", NULL, NULL, 0); -- 95
INSERT INTO class_sub_features VALUES (NULL, 90, "Underdark", NULL, NULL, 0); -- 96
INSERT INTO class_features VALUES (NULL, 8, "Fighting Style", NULL, "You adopt a particular style of fighting as your specialty. Choose one of the following options. You can't take a Fighting Style option more than once, even if you later get to choose again.", 2, 0); -- 91
INSERT INTO class_sub_features VALUES (NULL, 91, "Archery", NULL, "You gain a +2 bonus to attack rolls you make with ranged weapons.", 0); -- 97
INSERT INTO class_sub_features VALUES (NULL, 91, "Defense", NULL, "While you are wearing armor, you gain a +1 bonus to AC.", 0); -- 98
INSERT INTO class_sub_features VALUES (NULL, 91, "Dueling", NULL, "When you are wielding a melee weapon in one hand and no other weapons, you gain a +2 bonus to damage rolls with that weapon.", 0); -- 99
INSERT INTO class_sub_features VALUES (NULL, 91, "Two-Weapon Fighting", NULL, "When you engage in two-weapon fighting, you can add your ability modifier to the damage of the second attack.", 0); -- 100
INSERT INTO class_sub_features VALUES (NULL, 91, "Blind Fighting", NULL, "You have blindsight with a range of 10 feet. Within that range, you can effectively see anything that isn't behind total cover, even if you're blinded or in darkness. Moreover, you can see an invisible creature within that range, unless the creature successfully hides from you.", 1); -- 101
INSERT INTO class_sub_features VALUES (NULL, 91, "Druidic Warrior", NULL, "You learn two cantrips of your choice from the druid spell list. They count as ranger spells for you, and Wisdom is your spellcasting ability for them. Whenever you gain a level in this class, you can replace one of these cantrips with another cantrip from the druid spell list.", 1); -- 102
INSERT INTO class_sub_features VALUES (NULL, 91, "Thrown Weapon Fighting", NULL, "You can draw a weapon that has the thrown property as part of the attack you make with the weapon.<br>In addition, when you hit with a ranged attack using a thrown weapon, you gain a +2 bonus to the damage roll.", 1); -- 103
INSERT INTO class_features VALUES (NULL, 8, "Spellcasting", "Spell save DC = 8 + prof. + WIS<br>Spell attack modifier = prof. + WIS", "You have learned to use the magical essence of nature to cast spells. See chapter 10 for the general rules of spellcasting and chapter 11 for the ranger spell list.<br><br><b>Spell Slots</b><br>The Ranger table shows how many spell slots you have to cast your spells of 1st level and higher. To cast one of your ranger spells of 1st level or higher, you must expend a spell slot. You regain all expended spell slots when you finish a long rest.<br>For example, if you know the 1st-level spell <i>animal friendship</i> and have a 1st-level and a 2nd-level spell slot available, you can cast <i>animal friendship</i> using either slot.<br><br><b>Spells Known of 1st level and Higher</b><br>You know two 1st-level spells of your choice from the ranger spell list.<br>The Spells Known column of the Ranger table shows when you learn more ranger spells of your choice of your choice. A spell you choose must be of a level no higher than what's shown in the table's Slot level column for your level. For instance, when you reach 5th level in this class, you can learn one new spell of 1st or 2nd level.<br>Additionally, when you gain a level in this class, you can choose one of the ranger spells you know and replace it with another spell from the ranger spell list, which also must be of a level for which you have spell slots.<br><br><b>Spellcasting Ability</b><br>Wisdom is your spellcasting ability for your ranger spells, since your magic draws on your attunement to nature. You use your Wisdom modifier when setting the saving throw DC for a ranger spell you cast and when making an attack roll with one.<br>Spell save DC = 8 + proficiency bonus + your Wisdom modifier<br>Spell attack modifier = your proficiency bonus + your Wisdom modifier<br><br><b>Spellcasting Focus</b><br>You can use an arcane focus (found in chapter 5) as a spellcasting focus for your ranger spells.", 2, 0); -- 92
INSERT INTO class_features VALUES (NULL, 8, "Ranger Archetype", NULL, "You choose an archetype that you strive to emulate. Your choice grants you features at 3rd level and again at 7th, 11th, and 15th level.", 3, 0); -- 93
INSERT INTO class_sub_features VALUES (NULL, 93, "Hunter", NULL, "Emulating the Hunter archetype means accepting your place as a bulwark between civilization and the terrors of the wilderness. As you walk the Hunter's path, you learn specialized techniques for fighting the threats you face, from rampaging ogres and hordes of orcs to towering giants and terrifying dragons.", 0); -- 104
INSERT INTO class_sub_features VALUES (NULL, 93, "Beast Master", NULL, "The Beast Master archetype embodies a friendship between the civilized races and the beasts of the world. United in focus, beast and ranger work as one to fight the monstrous foes that threaten civilization and the wilderness alike. Emulating the Beast Master archetype means committing yourself to this ideal, working in partnership with an animal as its companion and friend.", 0); -- 105
INSERT INTO class_sub_features VALUES (NULL, 93, "Gloom Stalker", NULL, "Gloom Stalkers are at home in the darkest places: deep under the earth, in gloomy alleyways, in primeval forests, and wherever else the light dims. Most folk enter such places with trepidation, but a Gloom Stalker ventures boldly into the darkness, seeking to ambush threats before they can reach the broader world. Such rangers are often found in the Underdark, but they will go any place where evil lurks in the shadows.", 0); -- 106
INSERT INTO class_sub_features VALUES (NULL, 93, "Horizon Walker", NULL, "Horizon Walkers guard the world against threats that originate from other planes or that seek to ravage the mortal realm with otherworldly magic. They seek out planar portals and keep watch over them, venturing to the Inner Planes and the Outer Planes as needed to pursue their foes. These rangers are also friends to any forces in the multiverse — especially benevolent dragons, fey, and elementals — that work to preserve life and the order of the planes.", 0); -- 107
INSERT INTO class_sub_features VALUES (NULL, 93, "Monster Slayer", NULL, "You have dedicated yourself to hunting down creatures of the night and wielders of grim magic. A Monster Slayer seeks out vampires, dragons, evil fey, fiends, and other magical threats. Trained in supernatural techniques to overcome such monsters, slayers are experts at unearthing and defeating mighty, mystical foes.", 0); -- 108
INSERT INTO class_sub_features VALUES (NULL, 93, "Fey Wanderer", NULL, "A fey mystique surrounds you, thanks to the boon of an archfey, the shining fruit you ate from a talking tree, the magic spring you swam in, or some other auspicious event. However you acquired your fey magic, you are now.a Fey Wanderer, a ranger who represents both the mortal and the fey realms. As you wander the multiverse, your joyful laughter brightens the hearts of the downtrodden, and your martial prowess strikes terror in your foes, for great is the mirth of the fey and dreadful is their fury.", 0); -- 109
INSERT INTO class_sub_features VALUES (NULL, 93, "Swarmkeeper", NULL, "Feeling a deep connection to the environment around them, some rangers reach out through their magical connection to the world and bond with a swarm of nature spirits. The swarm becomes a potent force in battle, as well as helpful company for the ranger. Some Swarmkeepers are outcasts or hermits, keeping to themselves and their attendant swarms rather than dealing with the discomfort of others. Other Swarmkeepers enjoy building vibrant communities that work for the mutual benefit of all those they consider part of their swarm.", 0); -- 110
INSERT INTO class_features VALUES (NULL, 8, "Primeval Awareness", "Action to expend a spell slot to sense aberrations, celestials, dragons, elementals, fey, fiends, and undead within 1 mile of you (6 miles in favored terrain).", "You can use your action and expend one ranger spell slot to focus your awareness on the region around you. For 1 minute per level of the spell slot you expend, you can sense whether the following types of creatures are present within 1 mile of you (or within up to 6 miles if you are in your favored terrain): aberrations, celestials, dragons, elementals, fey, fiends, and undead. This feature doesn't reveal the creatures' location or number.", 3, 0); -- 94
INSERT INTO class_features VALUES (NULL, 8, "Extra Attack", "You can attack twice, instead of once, whenever you take the Attack action on your turn.", "You can attack twice, instead of once, whenever you take the Attack action on your turn.", 5, 0); -- 95
INSERT INTO class_features VALUES (NULL, 8, "Land's Stride", "Nonmagical difficult terrain doesn't slow you, nor harm you. You have advantage on saving throws against magical plants that impede movement.", "Moving through nonmagical difficult terrain costs you no extra movement. You can also pass through nonmagical plants without being slowed by them and without taking damage from them if they have thorns, spines, or a similar hazard.<br>In addition, you have advantage on saving throws against plants that are magically created or manipulated to impede movement, such those created by the <i>entangle</i> spell.", 8, 0); -- 96
INSERT INTO class_features VALUES (NULL, 8, "Hide in Plain Sight", "Spend 1 minute to camouflage yourself naturally, hide by not moving to gain a +10 bonus to Stealth.", "You can spend 1 minute creating camouflage for yourself. You must have access to fresh mud, dirt, plants, soot, and other naturally occurring materials with which to create your camouflage.<br>Once you are camouflaged in this way, you can try to hide by pressing yourself up against a solid surface, such as a tree or wall, that is at least as tall and wide as you are. You gain a +10 bonus to Dexterity (Stealth) checks as long as you remain there without moving or taking actions. Once you move or take an action or a reaction, you must camouflage yourself again to gain this benefit.", 10, 0); -- 97
INSERT INTO class_features VALUES (NULL, 8, "Vanish", "Hide as a bonus action.", "You can use the Hide action as a bonus action on your turn. Also, you can't be tracked by nonmagical means, unless you choose to leave a trail.", 14, 0); -- 98
INSERT INTO class_features VALUES (NULL, 8, "Feral Senses", "You don't have disadvantage against hidden creatures.<br>You are aware of invisible creatures within 30ft.", "You gain preternatural senses that help you fight creatures you can't see. When you attack a creature you can't see, your inability to see it doesn't impose disadvantage on your attack rolls against it.<br>You are also aware of the location of any invisible creature within 30 feet of you, provided that the creature isn't hidden from you and you aren't blinded or deafened.", 18, 0); -- 99
INSERT INTO class_features VALUES (NULL, 8, "Foe Slayer", "Once per turn add your WIS modifier to the attack or damage roll.", "You become an unparalleled hunter of your enemies. Once on each of your turns, you can add your Wisdom modifier to the attack roll or the damage roll of an attack you make against one of your favored enemies. You can choose to use this feature before or after the roll, but before any effects of the roll are applied.", 20, 0); -- 100
INSERT INTO class_features VALUES (NULL, 8, "Deft Explorer", "This feature replaces the Natural Explorer feature. You gain abilities at 1st, 6th, and 10th level.", "You are an unsurpassed explorer and survivor, both in the wilderness and in dealing with others on your travels. You gain the Canny benefit below, and you gain an additional benefit below when you reach 6th level and 10th level in this class.", 1, 1); -- 101
INSERT INTO class_sub_features VALUES (NULL, 101, "Canny", "Choose one skill proficiency to double the proficiency bonus. You learn two additional languages.", "Choose one of your skill proficiencies. Your proficiency bonus is doubled for any ability check you make that uses the chosen skill.<br><br>You can also speak, read, and write two additional languages of your choice.", 0); --
INSERT INTO class_sub_features VALUES (NULL, 101, "Roving", "Climbing speed increases by 5ft, you gain climbing and swimming speed equal to your walking speed.", "Your walking speed increases by 5, and you gain a climbing speed and a swimming speed equal to your walking speed.", 0); --
INSERT INTO class_sub_features VALUES (NULL, 101, "Tireless", "Action to gain 1d8 + Wisdom modifier (min 1) temp HP. Use number of times equal to proficiency, reset on long rest.<br>Exhaustion decreases by 1 on a short rest.", "As an action, you can give yourself a number of temporary hit points equal to 1d8 + your Wisdom modifier (minimum of 1 temporary hit point). You can use this action a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.<br><br>In addition, whenever you finish a short rest, your exhaustion level, if any, is decreased by 1 .", 0); --
INSERT INTO class_features VALUES (NULL, 8, "Favored Foe", "This replaces the Favored Enemy feature. When you hit a create with an attack roll, mark target for 1 min (requires concentration), each turn you hit the marked target increase damage bu 1d4 (1d6 at 6th level, 1d8 at 14th). Use feature number of times equal to proficiency bonus, reset on long rest.", "When you hit a creature with an attack roll, you can call on your mystical bond with nature to mark the target as your favored enemy for 1 minute or until you lose your concentration (as if you were concentrating on a spell).<br><br>The first time on each of your turns that you hit the favored enemy and deal damage to it, including when you mark it, you can increase that damage by 1d4.<br><br>You can use this feature to mark a favored enemy a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.<br><br>This feature's extra damage increases when you reach certain levels in this class: to 1d6 at 6th level and to 1d8 at 14th level.", 1, 1); -- 102
INSERT INTO class_features VALUES (NULL, 8, "Spellcasting Focus", "You can use a druidic focus for your spells.", "You can use a druidic focus as a spellcasting focus for your ranger spells. A druidic focus might be a sprig of mistletoe or holly, a wand or rod made of yew or another special wood, a staff drawn whole from a living tree, or an object incorporating feathers, fur, bones, and teeth from sacred animals.", 2, 1); -- 103
INSERT INTO class_features VALUES (NULL, 8, "Primal Awareness", "You learn additional spells at 3rd, 5th, 9th, 13th, and 17th level. They don't count against the number of ranger spells you know.", "You can focus your awareness through the interconnections of nature: you learn additional spells when you reach certain levels in this class if you don't already know them, as shown in the Primal Awareness Spells table. These spells don't count against the number of ranger spells you know.<br><br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Level</th><th>Spell</th></tr></thead><tbody><tr><td>3rd</td><td>Speak with Animals</td></tr><tr><td>5th</td><td>Beast Sense</td></tr><tr><td>9th</td><td>Speak with Plants</td></tr><tr><td>13th</td><td>Locate Creature</td></tr><tr><td>17th</td><td>Commune with Nature</td></tr></tbody></table></div><br><br>You can cast each of these spells once without expending a spell slot. Once you cast a spell in this way, you can't do so again until you finish a long rest.", 3, 1); -- 104
INSERT INTO class_features VALUES (NULL, 8, "Martial Versatility", "When you gain an ASI you can also replace one fighting style.", "Whenever you reach a level in this class that grants the Ability Score Improvement feature, you can replace a fighting style you know with another fighting style available to rangers. This replacement represents a shift of focus in your martial practice.", 4, 1); -- 105
INSERT INTO class_features VALUES (NULL, 8, "Nature's Veil", "This replaces the Hide in Plain Sight feature.<br>Bonus action to become invisible until the start of your next turn. Use this a number of times equal to proficiency bonus, reset on long rest.", "Replaces the Hide in Plain Sight feature.<br><br>You draw on the powers of nature to hide yourself from view briefly. As a bonus action, you can magically become invisible, along with any equipment you are wearing or carrying, until the start of your next turn.<br><br>You can use this feature a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.", 10, 1); -- 106
INSERT INTO archetype_features VALUES (NULL, 57, "Hunter's Prey", NULL, "You gain one of the following features of your choice.", 3); -- 294
INSERT INTO archetype_sub_features VALUES (NULL, 294, "Colossus Slayer", NULL, "Your tenacity can wear down the most potent foes. When you hit a creature with a weapon attack, the creature takes an extra 1d8 damage if it's below its hit point maximum. You can deal this extra damage only once per turn.", 0); -- 77
INSERT INTO archetype_sub_features VALUES (NULL, 294, "Giant Killer", NULL, "When a Large or larger creature within 5 feet of you hits or misses you with an attack, you can use your reaction to attack that creature immediately after its attack, provided that you can see the creature.", 0); -- 78
INSERT INTO archetype_sub_features VALUES (NULL, 294, "Horde Breaker", NULL, "Once on each of your turns when you make a weapon attack, you can make another attack with the same weapon against a different creature that is within 5 feet of the original target and within range of your weapon.", 0); -- 79
INSERT INTO archetype_features VALUES (NULL, 57, "Defensive Tactics", NULL, "You gain one of the following features of your choice.", 7); -- 295
INSERT INTO archetype_sub_features VALUES (NULL, 295, "Escape the Horde", NULL, "Opportunity attacks against you are made with disadvantage.", 0); -- 80
INSERT INTO archetype_sub_features VALUES (NULL, 295, "Multiattack Defense", NULL, "When a creature hits you with an attack, you gain a +4 bonus to AC against all subsequent attacks made by that creature for the rest of the turn.", 0); -- 81
INSERT INTO archetype_sub_features VALUES (NULL, 295, "Steel Will", NULL, "You have advantage on saving throws against being frightened.", 0); -- 82
INSERT INTO archetype_features VALUES (NULL, 57, "Multiattack", NULL, "You gain one of the following features of your choice.", 11); -- 296
INSERT INTO archetype_sub_features VALUES (NULL, 296, "Volley", NULL, "You can use your action to make a ranged attack against any number of creatures within 10 feet of a point you can see within your weapon's range. You must have ammunition for each target, as normal, and you make a separate attack roll for each target.", 0); -- 83
INSERT INTO archetype_sub_features VALUES (NULL, 296, "Whirlwind Attack", NULL, "You can use your action to make a melee attack against any number of creatures within 5 feet of you, with a separate attack roll for each target.", 0); -- 84
INSERT INTO archetype_features VALUES (NULL, 57, "Superior Hunter's Defense", NULL, "You gain one of the following features of your choice.", 15); -- 297
INSERT INTO archetype_sub_features VALUES (NULL, 297, "Evasion", NULL, "You can nimbly dodge out of the way of certain area effects, such as a red dragon's fiery breath or a lightning bolt spell. When you are subjected to an effect that allows you to make a Dexterity saving throw to take only half damage, you instead take no damage if you succeed on the saving throw, and only half damage if you fail.", 0); -- 85
INSERT INTO archetype_sub_features VALUES (NULL, 297, "Stand Against the Tide", NULL, "When a hostile creature misses you with a melee attack, you can use your reaction to force that creature to repeat the same attack against another creature (other than itself) of your choice.", 0); -- 86
INSERT INTO archetype_sub_features VALUES (NULL, 297, "Uncanny Dodge", NULL, "When an attacker that you can see hits you with an attack, you can use your reaction to halve the attack's damage against you.", 0); -- 87
INSERT INTO archetype_features VALUES (NULL, 58, "Ranger's Companion", "Choose a medium beast of CR 1/4 or lower.<br>Add your prof bonus to it's AC, attack and damage rolls, and it's proficiencies.<br>HP equals normal max, or 4 times ranger level.", "You gain a beast companion that accompanies you on your adventures and is trained to fight alongside you. Choose a beast that is no larger than Medium and that has a challenge rating of 1/4 or lower (appendix D presents statistics for the hawk, mastiff, and panther as examples). Add your proficiency bonus to the beast's AC, attack rolls, and damage rolls, as well as to any saving throws and skills it is proficient in. Its hit point maximum equals its normal maximum or four times your ranger level, whichever is higher.<br>The beast obeys your commands as best as it can. It takes its turn on your initiative, though it doesn't take an action unless you command it to. On your turn, you can verbally command the beast where to move (no action required by you). You can use your action to verbally command it to take the Attack, Dash, Disengage, Dodge, or Help action. Once you have the Extra Attack feature, you can make one weapon attack yourself when you command the beast to take the Attack action.<br>While traveling through your favored terrain with only the beast, you can move stealthily at a normal pace.<br>If the beast dies, you can obtain another one by spending 8 hours magically bonding with another beast that isn't hostile to you, either the same type of beast as before or a different one.", 3); -- 298
INSERT INTO archetype_features VALUES (NULL, 58, "Exceptional Training", "If your beast doesn't attack, bonus action to Dash, Disengage, Dodge, or Help.", "On any of your turns when your beast companion doesn't attack, you can use a bonus action to command the beast to take the Dash, Disengage, Dodge, or Help action on its turn.", 7); -- 299
INSERT INTO archetype_features VALUES (NULL, 58, "Bestial Fury", "Your beast companion can make two attacks when you command it to use the Attack action.", "Your beast companion can make two attacks when you command it to use the Attack action.", 11); -- 300
INSERT INTO archetype_features VALUES (NULL, 58, "Share Spells", "Spells that target yourself also affect your beast if within 30ft.", "When you cast a spell targeting yourself, you can also affect your beast companion with the spell if the beast is within 30 feet of you.", 15); -- 301
INSERT INTO archetype_features VALUES (NULL, 58, "Primal Companion", "Magically summon a primal beast. In combat acts during your turn, can move and use its reaction, will use the Dodge action unless you bonus action command it. You can also sacrifice your attack to allow it to take the Attack action.", "You magically summon a primal beast, which draws strength from your bond with nature. The beast is friendly to you and your companions and obeys your commands. Choose its stat block-Beast of the Land, Beast of the Sea, or Beast of the Sky-which uses your proficiency bonus (PB) in several places. You also determine the kind of animal the beast is, choosing a kind appropriate for the stat block. Whatever kind you choose, the beast bears primal markings, indicating its mystical origin.<br><br>In combat, the beast acts during your turn. It can move and use its reaction on its own, but the only action it takes is the Dodge action, unless you take a bonus action on your turn to command it to take another action. That action can be one in its stat block or some other action. You can also sacrifice one of your attacks when you take the Attack action to command the beast to take the Attack action. If you are incapacitated, the beast can take any action of its choice, not just Dodge.<br><br>If the beast has died within the last hour, you can use your action to touch it and expend a spell slot of 1st level or higher. The beast returns to life after 1 minute with all its hit points restored.<br><br>When you finish a long rest, you can summon a different primal beast. The new beast appears in an unoccupied space within 5 feet of you, and you choose its stat block and appearance. If you already have a beast from this feature, it vanishes when the new beast appears. The beast also vanishes if you die.", 3); -- 302
INSERT INTO archetype_features VALUES (NULL, 59, "Gloom Stalker Magic", "You learn additional spells at 3rd, 5th, 9th, 13th, and 17th level. They don't count against the number of spells you know.", "You learn an additional spell when you reach certain levels in this class, as shown in the Gloom Stalker Spells table. The spell counts as a ranger spell for you, but it doesn't count against the number of ranger spells you know.<br><br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Level</th><th>Spell</th></tr></thead><tbody><tr><td>3rd</td><td>Disguise Self</td></tr><tr><td>5th</td><td>Rope Trick</td></tr><tr><td>9th</td><td>Fear</td></tr><tr><td>13th</td><td>Greater Invisibility</td></tr><tr><td>17th</td><td>Seeming</td></tr></tbody></table></div>", 3); -- 303
INSERT INTO archetype_features VALUES (NULL, 59, "Dread Ambusher", "Add Wisdom modifier to initiative rolls.<br>First turn of combat 10ft extra speed, if you take an attack action take one additional attack dealing an extra 1d8.", "You master the art of the ambush. You can give yourself a bonus to your initiative rolls equal to your Wisdom modifier.<br>At the start of your first turn of each combat, your walking speed increases by 10 feet, which lasts until the end of that turn. If you take the Attack action on that turn, you can make one additional weapon attack as part of that action. If that attack hits, the target takes an extra 1d8 damage of the weapon's damage type.", 3); -- 304
INSERT INTO archetype_features VALUES (NULL, 59, "Umbral Sight", "Darkvision to 60ft, or 30+ current range. While in darkness you are invisible to creatures that rely on darkvision.", "You gain darkvision out to a range of 60 feet. If you already have darkvision from your race, its range increases by 30 feet.<br>You are also adept at evading creatures that rely on darkvision. While in darkness, you are invisible to any creature that relies on darkvision to see you in that darkness.", 3); -- 305
INSERT INTO archetype_features VALUES (NULL, 59, "Iron Mind", "You gain proficiency in Wisdom saving throws. Otherwise Intelligence or Charisma.", "You have honed your ability to resist the mind-altering powers of your prey. You gain proficiency in Wisdom saving throws. If you already have this proficiency, you instead gain proficiency in Intelligence or Charisma saving throws (your choice).", 7); -- 306
INSERT INTO archetype_features VALUES (NULL, 59, "Stalker's Flurry", "Once per turn when you miss an attack, you make another attack.", "You learn to attack with such unexpected speed that you can turn a miss into another strike. Once on each of your turns when you miss with a weapon attack, you can make another weapon attack as part of the same action.", 11); -- 307
INSERT INTO archetype_features VALUES (NULL, 59, "Shadowy Dodge", "Use your reaction to impose disadvantage on a non-advantaged attack.", "You can dodge in unforeseen ways, with wisps of supernatural shadow around you. Whenever a creature makes an attack roll against you and doesn't have advantage on the roll, you can use your reaction to impose disadvantage on it. You must use this feature before you know the outcome of the attack roll.", 15); -- 308
INSERT INTO archetype_features VALUES (NULL, 60, "Horizon Walker Magic", "Learn additional spells at levels 3, 5, 9, 13 and 17. These don't count against the number of spells you know.", "You learn an additional spell when you reach certain levels in this class, as shown in the Horizon Walker Spells table. The spell counts as a ranger spell for you, but it doesn't count against the number of ranger spells you know.<br><br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Level</th><th>Spell</th></tr></thead><tbody><tr><td>3rd</td><td>Protection from Evil and Good</td></tr><tr><td>5th</td><td>Misty Step</td></tr><tr><td>9th</td><td>Haste</td></tr><tr><td>13th</td><td>Banishment</td></tr><tr><td>17th</td><td>Teleportation Circle</td></tr></tbody></table></div>", 3); -- 309
INSERT INTO archetype_features VALUES (NULL, 60, "Detect Portal", "As an action, once per rest, detect the distance and direction to the closest planar portal within 1 mile.", "You gain the ability to magically sense the presence of a planar portal. As an action, you detect the distance and direction to the closest planar portal within 1 mile of you.<br>Once you use this feature, you can't use it again until you finish a short or long rest.<br>See the “Planar Travel” section in chapter 2 of the Dungeon Master's Guide for examples of planar portals.", 3); -- 310
INSERT INTO archetype_features VALUES (NULL, 60, "Planar Warrior", "Bonus action, choose a creature within 30ft, on next hit all damage is force dmg and deal an extra 1d8 (2d8 at 11th level).", "You learn to draw on the energy of the multiverse to augment your attacks.<br>As a bonus action, choose one creature you can see within 30 feet of you. The next time you hit that creature on this turn with a weapon attack, all damage dealt by the attack becomes force damage, and the creature takes an extra 1d8 force damage from the attack. When you reach 11th level in this class, the extra damage increases to 2d8.", 3); -- 311
INSERT INTO archetype_features VALUES (NULL, 60, "Ethereal Step", "Once per rest, as a bonus action, cast <i>etherealness</i> without expending a spell slot that last until the end of your turn.", "You learn to step through the Ethereal Plane. As a bonus action, you can cast the etherealness spell with this feature, without expending a spell slot, but the spell ends at the end of the current turn.<br>Once you use this feature, you can't use it again until you finish a short or long rest.", 7); -- 312
INSERT INTO archetype_features VALUES (NULL, 60, "Distant Strike", "On an attack you can teleport up to 10ft before each attack. If you attack two different creatures with the action you can make an additional attack against a third creature.", "You gain the ability to pass between the planes in the blink of an eye. When you take the Attack action, you can teleport up to 10 feet before each attack to an unoccupied space you can see.<br>If you attack at least two different creatures with the action, you can make one additional attack with it against a third creature.", 11); -- 313
INSERT INTO archetype_features VALUES (NULL, 60, "Spectral Defense", "When you take damage from an attack, you can use your reaction to give yourself resistance to all of that attack's damage on this turn.", "Your ability to move between planes enables you to slip through the planar boundaries to lessen the harm done to you during battle. When you take damage from an attack, you can use your reaction to give yourself resistance to all of that attack's damage on this turn.", 15); -- 314
INSERT INTO archetype_features VALUES (NULL, 61, "Monster Slayer Magic", "Learn additional spells at levels 3, 5, 9, 13 and 17. These don't count against the number of spells you know.", "You learn an additional spell when you reach certain levels in this class, as shown in the Monster Slayer Spells table. The spell counts as a ranger spell for you, but it doesn't count against the number of ranger spells you know.<div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Level</th><th>Spell</th></tr></thead><tbody><tr><td>3rd</td><td>Protection from Evil and Good</td></tr><tr><td>5th</td><td>Zone of Truth</td></tr><tr><td>9th</td><td>Magic Circle</td></tr><tr><td>13th</td><td>Banishment</td></tr><tr><td>17th</td><td>Hold Monster</td></tr></tbody></table></div>", 3); -- 315
INSERT INTO archetype_features VALUES (NULL, 61, "Hunter's Sense", "Use an Action (times = Wis modifier), choose creature within 60ft. Learn it's immunities, resistances, and vulnerabilities.", "You gain the ability to peer at a creature and magically discern how best to hurt it. As an action, choose one creature you can see within 60 feet of you. You immediately learn whether the creature has any damage immunities, resistances, or vulnerabilities and what they are. If the creature is hidden from divination magic, you sense that it has no damage immunities, resistances, or vulnerabilities.<br>You can use this feature a number of times equal to your Wisdom modifier (minimum of once). You regain all expended uses of it when you finish a long rest.", 3); -- 316
INSERT INTO archetype_features VALUES (NULL, 61, "Slayer's Prey", "Bonus action to designate a creature within 60ft, first hit each turn deals an extra 1d6 dmg.", "You can focus your ire on one foe, increasing the harm you inflict on it. As a bonus action, you designate one creature you can see within 60 feet of you as the target of this feature. The first time each turn that you hit that target with a weapon attack, it takes an extra 1d6 damage from the weapon.<br>This benefit lasts until you finish a short or long rest. It ends early if you designate a different creature.", 3); -- 317
INSERT INTO archetype_features VALUES (NULL, 61, "Supernatural Defense", "Whenever the target of your Slayer's Prey forces you to make a saving throw and whenever you make an ability check to escape that target's grapple, add 1d6 to your roll.", "You gain extra resilience against your prey's assaults on your mind and body. Whenever the target of your Slayer's Prey forces you to make a saving throw and whenever you make an ability check to escape that target's grapple, add 1d6 to your roll.", 7); -- 318
INSERT INTO archetype_features VALUES (NULL, 61, "Magic-User's Nemesis", "Stop a creature casting a spell or teleporting within 60ft, reaction to magically foil it. Creature must succeed on a Wisdom saving throw, or its spell or teleport fails and is wasted.", "You gain the ability to thwart someone else's magic. When you see a creature casting a spell or teleporting within 60 feet of you, you can use your reaction to try to magically foil it. The creature must succeed on a Wisdom saving throw against your spell save DC, or its spell or teleport fails and is wasted.<br>Once you use this feature, you can't use it again until you finish a short or long rest.", 11); -- 319
INSERT INTO archetype_features VALUES (NULL, 61, "Slayer's Counter", "If the target of your Slayer's Prey forces you to make a saving throw, reaction to make one weapon attack against them. You make this attack immediately before making the saving throw. If you hit, your save succeeds, in addition to the attack's normal effects.", "You gain the ability to counterattack when your prey tries to sabotage you. If the target of your Slayer's Prey forces you to make a saving throw, you can use your reaction to make one weapon attack against the quarry. You make this attack immediately before making the saving throw. If your attack hits, your save automatically succeeds, in addition to the attack's normal effects.", 15); -- 320
INSERT INTO archetype_features VALUES (NULL, 62, "Dreadful Strikes", "When you hit a creature with a weapon, deal 1d4 extra psychic damage. The target can only take this damage once per turn.", "You can augment your weapon strikes with mind-scarring magic, drawn from the gloomy hollows of the Feywild. When you hit a creature with a weapon, you can deal an extra ld4 psychic damage to the target, which can take this extra damage only once per turn.<br><br>The extra damage increases to ld6 when you reach 11th level in this class.", 3); -- 321
INSERT INTO archetype_features VALUES (NULL, 62, "Fey Wanderer Magic", "You learn additional spells at 3rd, 5th, 9th, 13th, and 17th level. These spells don't count against the number of ranger spells you know.", "You learn an additional spell when you reach certain levels in this class, as shown in the Fey Wanderer Spells table. Each spell counts as a ranger spell for you, but it doesn't count against the number of ranger spells you know.<br><br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Level</th><th>Spell</th></tr></thead><tbody><tr><td>3rd</td><td>Char Person</td></tr><tr><td>5th</td><td>Misty Step</td></tr><tr><td>9th</td><td>Dispel Magic</td></tr><tr><td>13th</td><td>Dimension Door</td></tr><tr><td>17th</td><td>Mislead</td></tr></tbody></table></div><br><br>You also possess a preternatural blessing from a fey ally or a place of fey power. Choose your blessing from the Feywild Gifts table or determine it randomly.", 3); -- 322
INSERT INTO archetype_features VALUES (NULL, 62, "Otherworldly Gifts", "Bonus to Charisma checks equal to Wisdom modifier (min 1).<br>Gain proficiency in one of: Deception, Performance, or Persuasion.", "Your fey qualities give you a supernatural charm. As a result, whenever you make a Charisma check, you gain a bonus to the check equal to your Wisdom modifier (minimum of +l).<br><br>In addition, you gain proficiency in one of the following skills of your choice: Deception, Performance, or Persuasion.", 3); -- 323
INSERT INTO archetype_features VALUES (NULL, 62, "Beguiling Twist", "Advantage on saves againt being Frightened or Charmed.<br>When a creature within 120ft makes a save against being Frightened or Charmed, use reaction to force a different creature within 120ft to make a Wisom save (against spell DC), on fail target is charmed or frightened of you for 1 min. Target can repeat save at end of each turn.", "The magic of the Feywild guards your mind. You have advantage on saving throws against being charmed or frightened.<br><br>In addition, whenever you or a creature you can see within 120 feet of you succeeds on a saving throw against being charmed or frightened, you can use your reaction to force a different creature you can see within 120 feet of you to make a Wisdom saving throw against your spell save DC. If the save fails, the target is charmed or frightened by you (your choice) for 1 minute. The target can repeat the saving throw at the end of each of its turns, ending the effect on itself on a successful save.", 7); -- 324
INSERT INTO archetype_features VALUES (NULL, 62, "Fey Reinforcements", "You know the Summon Fey spell, it doesn't count against your known spells. You can cast it once per long rest. You can cast the spell without concentration with a casting duration of 1 min.", "The royal courts of the Feywild have blessed you with the assistance of fey beings: you know summon fey (a spell in chapter 3). It doesn't count against the number of ranger spells you know, and you can cast it without a material component. You can also cast it once without a spell slot, and you regain the ability to do so when you finish a long rest.<br><br>Whenever you start casting the spell, you can modify it so that it doesn't require concentration. If you do so, the spell's duration becomes 1 minute for that casting.", 11); -- 325
INSERT INTO archetype_features VALUES (NULL, 62, "Misty Wanderer", "You can cast misty step without expending a spell slot. You can do this a number of times equal to your Wisdom modifier, reset on long rest. You can bring one willing creature within 5ft with you.", "You can slip in and out of the Feywild to move in a blink of an eye: you can cast misty step without expending a spell slot. You can do so a number of times equal to your Wisdom modifier (minimum of once), and you regain all expended uses when you finish a long rest.<br><br>In addition, whenever you cast misty step, you can bring along one willing creature you can see within 5 feet of you. That creature teleports to an unoccupied space of your choice within 5 feet of your destination space.", 15); -- 326
INSERT INTO archetype_features VALUES (NULL, 63, "Gathered Swarm", "Intangible spirits swarm around you, immediately after you hit a creature with an attack, once on each turn you can:<br><ul><li>Deal extra ld6 piercing damage.</li><li>Target must succeed on a Strength save or be moved up to 15ft horizontally in a direction of your choice.</li><li>You are moved 5ft horizontally in a direction of your choice.</li></ul>", "A swarm of intangible nature spirits has bonded itself to you and can assist you in battle. Until you die, the swarm remains in your space, crawling on you or flying and skittering around you within your space. You determine its appearance, or you generate its appearance by rolling on the Swarm Appearance table.<br><br>Once on each of your turns, you can cause the swarm to assist you in one of the following ways, immediately after you hit a creature with an attack:<br><br><ul><li>The attack's target takes ld6 piercing damage from the swarm .</li><li>The attack's target must succeed on a Strength saving throw against your spell save DC or be moved by the swarm up to 15 feet horizontally in a direction of your choice.</li><li>You are moved by the swarm 5 feet horizontally in a direction of your choice.</li></ul>", 3); -- 327
INSERT INTO archetype_features VALUES (NULL, 63, "Swarmkeeper Magic", "You learn additional spells at 3rd, 5th, 9th, 13th, and 17th level. These spells don't count against the number of spells you know.", "You learn the mage hand cantrip if you don't already know it. When you cast it, the hand takes the form of your swarming nature spirits.<br><br>You also learn an additional spell of 1st level or higher when you reach certain levels in this class, as shown in the Swarmkeeper Spells table. Each spell counts as a ranger spell for you, but it doesn't count against the number of ranger spells you know.<br><br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Level</th><th>Spells</th></tr></thead><tbody><tr><td>3rd</td><td>Faerie Fire, Mage Hand</td></tr><tr><td>5th</td><td>Web</td></tr><tr><td>9th</td><td>Gaseous Form</td></tr><tr><td>13th</td><td>Arcane Eye</td></tr><tr><td>17th</td><td>Insect Plague</td></tr></tbody></table></div>", 3); -- 328
INSERT INTO archetype_features VALUES (NULL, 63, "Writhing Tide", "Bonus action to gain fly speed of 10ft and you can hover for 1 min. Use this feature number of times equal to proficiency bonus, reset on long rest.", "You can condense part of your swarm into a focused mass that lifts you up. As a bonus action, you gain a flying speed of 10 feet and can hover. This effect lasts for 1 minute or until you are incapacitated.<br><br>You can use this feature a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.", 7); -- 329
INSERT INTO archetype_features VALUES (NULL, 63, "Mighty Swarm", "Gathered Swarm extra features:<br><ul><li>Damage increases to ld8.</li><li>Creature fails save against being moved, knock target prone.</li><li>When you are moved, gain half cover until the start of next turn.</li></ul>", "Your Gathered Swarm grows mightier in the following ways:<br><br><ul><li>The damage of Gathered Swarm increases to ld8.</li><li>If a creature fails its saving throw against being moved by Gathered Swarm, you can also cause the swarm to knock the creature prone.</li><li>When you are moved by Gathered Swarm, it gives you half cover until the start of your next turn.</li></ul>", 11); -- 330
INSERT INTO archetype_features VALUES (NULL, 63, "Swarming Dispersal", "When you take damage, reaction to gain resistance. You vanish and teleport upto 30ft. You can use this feature a number of times equal to proficiency bonus, reset on long rest.", "You can discorporate into your swarm, avoiding danger. When you take damage, you can use your reaction to give yourself resistance to that damage. You vanish into your swarm and then teleport to an unoccupied space that you can see within 30 feet of you, where you reappear with the swarm.<br><br>You can use this feature a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.", 15); -- 331
INSERT INTO class_features VALUES (NULL, 9, "Expertise", "You gain extra proficiency with two skills, and another two skills at 6th level.", "Choose two of your skill proficiencies, or one of your skill proficiencies and your proficiency with thieves' tools. Your proficiency bonus is doubled for any ability check you make that uses either of the chosen proficiencies.<br>At 6th level, you can choose two more of your proficiencies (in skills or with thieves' tools) to gain this benefit.", 1, 0); -- 107
INSERT INTO class_features VALUES (NULL, 9, "Sneak Attack", "Once per turn you can deal extra sneak attack damage if you have advantage or an ally is within 5ft. of the creature.", "You know how to strike subtly and exploit a foe's distraction. Once per turn, you can deal an extra 1d6 damage to one creature you hit with an attack if you have advantage on the attack roll. The attack must use a finesse or a ranged weapon.<br>You don't need advantage on the attack roll if another enemy of the target is within 5 feet of it, that enemy isn't incapacitated, and you don't have disadvantage on the attack roll.<br> The amount of the extra damage increases as you gain levels in this class, as shown in the Sneak Attack column of the Rogue table.", 1, 0); -- 108
INSERT INTO class_features VALUES (NULL, 9, "Thieves' Cant", "You know thieves' cant, a secret mix of dialect, jargon, and code that allows you to hide messages in seemingly normal conversation.", "During your rogue training you learned thieves' cant, a secret mix of dialect, jargon, and code that allows you to hide messages in seemingly normal conversation. Only another creature that knows thieves' cant understands such messages. It takes four times longer to convey such a message than it does to speak the same idea plainly.<br>In addition, you understand a set of secret signs and symbols used to convey short, simple messages, such as whether an area is dangerous or the territory of a thieves' guild, whether loot is nearby, or whether the people in an area are easy marks or will provide a safe house for thieves on the run.", 1, 0); -- 109
INSERT INTO class_features VALUES (NULL, 9, "Cunning Action", "Bonus action to Dash, Disengage, or Hide.", "Your quick thinking and agility allow you to move and act quickly. You can take a bonus action on each of your turns in combat. This action can be used only to take the Dash, Disengage, or Hide action.", 2, 0); -- 110
INSERT INTO class_features VALUES (NULL, 9, "Roguish Archetype", NULL, "You choose an archetype that you emulate in the exercise of your rogue abilities. Your archetype choice grants you features at 3rd level and then again at 9th, 13th, and 17th level.", 3, 0); -- 111
INSERT INTO class_sub_features VALUES (NULL, 111, "Thief", NULL, "You hone your skills in the larcenous arts. Burglars, bandits, cutpurses, and other criminals typically follow this archetype, but so do rogues who prefer to think of themselves as professional treasure seekers, explorers, delvers, and investigators. In addition to improving your agility and stealth, you learn skills useful for delving into ancient ruins, reading unfamiliar languages, and using magic items you normally couldn't employ.", 0); -- 111
INSERT INTO class_sub_features VALUES (NULL, 111, "Assassin", NULL, "You focus your training on the grim art of death. Those who adhere to this archetype are diverse: hired killers, spies, bounty hunters, and even specially anointed priests trained to exterminate the enemies of their deity. Stealth, poison, and disguise help you eliminate your foes with deadly efficiency.", 0); -- 112
INSERT INTO class_sub_features VALUES (NULL, 111, "Arcane Trickster", NULL, "Some rogues enhance their fine-honed skills of stealth and agility with magic, learning tricks of enchantment and illusion. These rogues include pickpockets and burglars, but also pranksters, mischief-makers, and a significant number of adventurers.", 0); -- 113
INSERT INTO class_sub_features VALUES (NULL, 111, "Inquisitive", NULL, "As an archetypal Inquisitive, you excel at rooting out secrets and unraveling mysteries. You rely on your sharp eye for detail, but also on your finely honed ability to read the words and deeds of other creatures to determine their true intent. You excel at defeating creatures that hide among and prey upon ordinary folk, and your mastery of lore and your keen deductions make you well equipped to expose and end hidden evils.", 0); -- 114
INSERT INTO class_sub_features VALUES (NULL, 111, "Mastermind", NULL, "Your focus is on people and on the influence and secrets they have. Many spies, courtiers, and schemers follow this archetype, leading lives of intrigue. Words are your weapons as often as knives or poison, and secrets and favors are some of your favorite treasures.", 0); -- 115
INSERT INTO class_sub_features VALUES (NULL, 111, "Scout", NULL, "You are skilled in stealth and surviving far from the streets of a city, allowing you to scout ahead of your companions during expeditions. Rogues who embrace this archetype are at home in the wilderness and among barbarians and rangers, and many Scouts serve as the eyes and ears of war bands. Ambusher, spy, bounty hunter — these are just a few of the roles that Scouts assume as they range the world.", 0); -- 116
INSERT INTO class_sub_features VALUES (NULL, 111, "Swashbuckler", NULL, "You focus your training on the art of the blade, relying on speed, elegance, and charm in equal parts. While some warriors are brutes clad in heavy armor, your method of fighting looks almost like a performance. Duelists and pirates typically belong to this archetype.<br><br>A Swashbuckler excels in single combat, and can fight with two weapons while safely darting away from an opponent.", 0); -- 117
INSERT INTO class_sub_features VALUES (NULL, 111, "Phantom", NULL, "Many rogues walk a fine line between life and death, risking their own lives and taking the lives of others. While adventuring on that line, some rogues discover a mystical connection to death itself. These rogues take knowledge from the dead and become immersed in negative energy, eventually becoming like ghosts. Thieves' guilds value them as highly effective information gatherers and spies.<br><br>Many shadar-kai of the Shadowfell are masters of these macabre techniques, and some are willing to teach this path. In places like Thay in the Forgotten Realms and Karrnath in Eberron, where many necromancers practice their craft, a Phantom can become a wizard's confidant and right hand. In temples of gods of death, the Phantom might work as an agent to track down those who try to cheat death and to recover knowledge that might otherwise be lost to the grave.<br><br>How did you discover this grim power? Did you sleep in a graveyard and awaken to your new abilities? Or did you cultivate them in a temple or thieves' guild dedicated to a deity of death?", 0); -- 118
INSERT INTO class_sub_features VALUES (NULL, 111, "Soulknife", NULL, "Most assassins strike with physical weapons, and many burglars and spies use thieves' tools to infiltrate secure locations. In contrast, a Soulknife strikes and infiltrates with the mind, cutting through barriers both physical and psychic. These rogues discover psionic power within themselves and channel it to do their roguish work. They find easy employment as members of thieves' guilds, though they are often mistrusted by rogues who are leery of anyone using strange mind powers to conduct their business. Most governments would also be happy to employ a Soulknife as a spy.<br><br>Amid the trees of ancient forests on the Material Plane and in the Feywild, some wood elves walk the path of the Soulknife, serving as silent, lethal guardians of their woods. In the endless war among the gith, a githzerai is encouraged to become a Soulknife when stealth is required against the githyanki foe.<br><br>As a Soulknife, your psionic abilities might have haunted you since you were a child, only revealing their full potential as you experienced the stress of adventure. Or you might have sought out a reclusive order of psychic adepts and spent years learning how to manifest your power.", 0); -- 119
INSERT INTO class_features VALUES (NULL, 9, "Uncanny Dodge", "Use reaction to half damage from an attack you can see.", "When an attacker that you can see hits you with an attack, you can use your reaction to halve the attack's damage against you.", 5, 0); -- 112
INSERT INTO class_features VALUES (NULL, 9, "Evasion", "If you make a DEX save to avoid an effect, on success no damage, or half on fail.", "You can nimbly dodge out of the way of certain area effects, such as a red dragon's fiery breath or an ice storm spell. When you are subjected to an effect that allows you to make a Dexterity saving throw to take only half damage, you instead take no damage if you succeed on the saving throw, and only half damage if you fail.", 7, 0); -- 113
INSERT INTO class_features VALUES (NULL, 9, "Reliable Talent", "When you make a ability check you can add your proficiency too your minimum roll is a 10.", "You have refined your chosen skills until they approach perfection. Whenever you make an ability check that lets you add your proficiency bonus, you can treat a d20 roll of 9 or lower as a 10.", 11, 0); -- 114
INSERT INTO class_features VALUES (NULL, 9, "Blindsense", "Sense invisible or hidden creatures within 10ft.", "If you are able to hear, you are aware of the location of any hidden or invisible creature within 10 feet of you.", 14, 0); -- 115
INSERT INTO class_features VALUES (NULL, 9, "Slippery Mind", "You are proficient in Wisdom saving throws.", "You have acquired greater mental strength. You gain proficiency in Wisdom saving throws.", 15, 0); -- 116
INSERT INTO class_features VALUES (NULL, 9, "Elusive", "No attack roll has advantage against you while you aren't incapacitated.", "You are so evasive that attackers rarely gain the upper hand against you. No attack roll has advantage against you while you aren't incapacitated.", 18, 0); -- 117
INSERT INTO class_features VALUES (NULL, 9, "Stroke of Luck", "Once per short rest make an attack hit or an ability check be a natural 20.", "You have an uncanny knack for succeeding when you need to. If your attack misses a target within range, you can turn the miss into a hit. Alternatively, if you fail an ability check, you can treat the d20 roll as a 20.<br>Once you use this feature, you can't use it again until you finish a short or long rest.", 20, 0); -- 118
INSERT INTO class_features VALUES (NULL, 9, "Steady Aim", "Bonus action to gain advantage on your next attack roll (on the current turn), if you haven't moved and after using this your speed becomes 0.", "As a bonus action, you give yourself advantage on your next attack roll on the current turn. You can use this bonus action only if you haven't moved during this turn, and after you use the bonus action, your speed is 0 until the end of the current turn.", 3, 1); -- 119
INSERT INTO archetype_features VALUES (NULL, 64, "Fast Hands", "Cunning Action to make a Dexterity (Sleight of Hand) check, use your thieves' tools to disarm a trap or open a lock, or take the Use an Object action.", "You can use the bonus action granted by your Cunning Action to make a Dexterity (Sleight of Hand) check, use your thieves' tools to disarm a trap or open a lock, or take the Use an Object action.", 3); -- 332
INSERT INTO archetype_features VALUES (NULL, 64, "Second-Story Work", "Climbing no longer costs you extra movement. You can also jump further.", "You gain the ability to climb faster than normal; climbing no longer costs you extra movement.<br>In addition, when you make a running jump, the distance you cover increases by a number of feet equal to your Dexterity modifier.", 3); -- 333
INSERT INTO archetype_features VALUES (NULL, 64, "Supreme Sneak", "Advantage on a Dexterity (Stealth) check if you move no more than half your speed on the same turn.", "You have advantage on a Dexterity (Stealth) check if you move no more than half your speed on the same turn.", 9); -- 334
INSERT INTO archetype_features VALUES (NULL, 64, "Use Magic Device", "You ignore all class, race, and level requirements on the use of magic items", "You have learned enough about the workings of magic that you can improvise the use of items even when they are not intended for you. You ignore all class, race, and level requirements on the use of magic items.", 13); -- 335
INSERT INTO archetype_features VALUES (NULL, 64, "Thief's Reflexes", "Take two turns during the first round. Your first turn is normal initiative, your second turn is your initiative minus 10.", "You have become adept at laying ambushes and quickly escaping danger. You can take two turns during the first round of any combat. You take your first turn at your normal initiative and your second turn at your initiative minus 10. You can't use this feature when you are surprised.", 17); -- 336
INSERT INTO archetype_features VALUES (NULL, 65, "Bonus Proficiencies", "You are proficient with the disguise kit and the poisoner's kit.", "You gain proficiency with the disguise kit and the poisoner's kit.", 3); -- 337
INSERT INTO archetype_features VALUES (NULL, 65, "Assassinate", "You have advantage on attack rolls against any creature that hasn't taken a turn in the combat yet.<br>In addition, any hit you score against a creature that is surprised is a critical hit.", "You are at your deadliest when you get the drop on your enemies. You have advantage on attack rolls against any creature that hasn't taken a turn in the combat yet. In addition, any hit you score against a creature that is surprised is a critical hit.", 3); -- 338
INSERT INTO archetype_features VALUES (NULL, 65, "Infiltration Expertise", "You can unfailingly create false identities for yourself.", "You can unfailingly create false identities for yourself. You must spend seven days and 25 gp to establish the history, profession, and affiliations for an identity. You can't establish an identity that belongs to someone else. For example, you might acquire appropriate clothing, letters of introduction, and official looking certification to establish yourself as a member of a trading house from a remote city so you can insinuate yourself into the company of other wealthy merchants.<br>Thereafter, if you adopt the new identity as a disguise, other creatures believe you to be that person until given an obvious reason not to.", 9); -- 339
INSERT INTO archetype_features VALUES (NULL, 65, "Impostor", "You gain the ability to unerringly mimic another person's speech, writing, and behavior.", "You gain the ability to unerringly mimic another person's speech, writing, and behavior. You must spend at least three hours studying these three components of the person's behavior, listening to speech, examining handwriting, and observing mannerisms.<br>Your ruse is indiscernible to the casual observer. If a wary creature suspects something is amiss, you have advantage on any Charisma (Deception) check you make to avoid detection.", 13); -- 340
INSERT INTO archetype_features VALUES (NULL, 65, "Death Strike", "When you attack and hit a creature that is surprised, it must make a Constitution saving throw (DC 8 + your Dexterity modifier + your proficiency bonus). On a failed save, double the damage of your attack against the creature.", "You become a master of instant death. When you attack and hit a creature that is surprised, it must make a Constitution saving throw (DC 8 + your Dexterity modifier + your proficiency bonus). On a failed save, double the damage of your attack against the creature.", 17); -- 341
INSERT INTO archetype_features VALUES (NULL, 66, "Spellcasting", "Spell save DC = 8 + prof. + INT<br>Spell attack modifier = prof. + INT", "You gain the ability to cast spells. See chapter 10 for the general rules of spellcasting and chapter 11 for the wizard spell list.<br><br><b>Cantrips</b>. You learn three cantrips: mage hand and two other cantrips of your choice from the wizard spell list. You learn another wizard cantrip of your choice at 10th level.<br><br><b>Spell Slots</b>. The Arcane Trickster Spellcasting table shows how many spell slots you have to cast your spells of 1st level and higher. To cast one of these spells, you must expend a slot of the spell's level or higher. You regain all expended spell slots when you finish a long rest.<br>For example, if you know the 1st-level spell charm person and have a 1st-level and a 2nd-level spell slot available, you can cast charm person using either slot.<br><br><b>Spells Known of 1st-level and Higher</b>. You know three 1st-level wizard spells of your choice, two of which you must choose from the enchantment and illusion spells on the wizard spell list.<br>The Spells Known column of the Arcane Trickster Spellcasting table shows when you learn more wizard spells of 1st level or higher. Each of these spells must be an enchantment or illusion spell of your choice, and must be of a level for which you have spell slots. For instance, when you reach 7th level in this class, you can learn one new spell of 1st or 2nd level.<br>The spells you learn at 8th, 14th, and 20th level can come from any school of magic.<br>Whenever you gain a level in this class, you can replace one of the wizard spells you know with another spell of your choice from the wizard spell list. The new spell must be of a level for which you have spell slots, and it must be an enchantment or illusion spell, unless you're replacing the spell you gained at 8th, 14th, or 20th level.<br><br><b>Spellcasting Ability</b>. Intelligence is your spellcasting ability for your wizard spells, since you learn your spells through dedicated study and memorization. You use your Intelligence whenever a spell refers to your spellcasting ability. In addition, you use your Intelligence modifier when setting the saving throw DC for a wizard spell you cast and when making an attack roll with one.<br><br>Spell save DC = 8 + proficiency bonus + your Wisdom modifier<br>Spell attack modifier = your proficiency bonus + your Wisdom modifier", 3); -- 342
INSERT INTO archetype_features VALUES (NULL, 66, "Mage Hand Legerdemain", "Mage hand is invisible, and can: <br><ul><li>Stow one object in a container worn or carried by another creature.</li><li>Retrieve an object in a container worn or carried by another creature.</li><li>Use thieves' tools to pick locks and disarm traps at range.</li></ul>", "When you cast mage hand, you can make the spectral hand invisible, and you can perform the following additional tasks with it:<br><ul><li>You can stow one object the hand is holding in a container worn or carried by another creature.</li><li>You can retrieve an object in a container worn or carried by another creature.</li><li>You can use thieves' tools to pick locks and disarm traps at range.</li></ul><br>You can perform one of these tasks without being noticed by a creature if you succeed on a Dexterity (Sleight of Hand) check contested by the creature's Wisdom (Perception) check.<br>In addition, you can use the bonus action granted by your Cunning Action to control the hand.", 3); -- 343
INSERT INTO archetype_features VALUES (NULL, 66, "Magical Ambush", "If you are hidden and cast a spell that requires a saving throw the creature has disadvantage.", "If you are hidden from a creature when you cast a spell on it, the creature has disadvantage on any saving throw it makes against the spell this turn.", 9); -- 344
INSERT INTO archetype_features VALUES (NULL, 66, "Versatile Trickster", "Bonus action to distract a target within 5ft of your mage hand. You gain advantage on attack rolls until the end of your turn.", "You gain the ability to distract targets with your mage hand. As a bonus action on your turn, you can designate a creature within 5 feet of the spectral hand created by the spell. Doing so gives you advantage on attack rolls against that creature until the end of the turn.", 13); -- 345
INSERT INTO archetype_features VALUES (NULL, 66, "Spell Thief", "Once per long rest when a creature casts a spell that targets or includes you, use a reaction to force them to make a saving throw.<br>On fail the spell doesn't affect you, and you can use the spell for the next 8 hours.", "You gain the ability to magically steal the knowledge of how to cast a spell from another spellcaster.<br>Immediately after a creature casts a spell that targets you or includes you in its area of effect, you can use your reaction to force the creature to make a saving throw with its spellcasting ability modifier. The DC equals your spell save DC. On a failed save, you negate the spell's effect against you, and you steal the knowledge of the spell if it is at least 1st level and of a level you can cast (it doesn't need to be a wizard spell). For the next 8 hours, you know the spell and can cast it using your spell slots. The creature can't cast that spell until the 8 hours have passed.<br>Once you use this feature, you can't use it again until you finish a long rest.", 17); -- 346
INSERT INTO archetype_features VALUES (NULL, 67, "Ear for Deceit", "On Wisdom (Insight) checks to determine lying, treat a roll of 7 or lower as an 8.", "You develop a talent for picking out lies. Whenever you make a Wisdom (Insight) check to determine whether a creature is lying, treat a roll of 7 or lower on the d20 as an 8.", 3); -- 347
INSERT INTO archetype_features VALUES (NULL, 67, "Eye for Detail", "Bonus action to make a Wisdom (Perception) check to spot a hidden creature or object or to make an Intelligence (Investigation) check to uncover or decipher clues.", "You can use a bonus action to make a Wisdom (Perception) check to spot a hidden creature or object or to make an Intelligence (Investigation) check to uncover or decipher clues.", 3); -- 348
INSERT INTO archetype_features VALUES (NULL, 67, "Insightful Fighting", "Bonus action to make a Wisdom (Insight) check contested by a Charisma (Deception) check. On success, for 1 minute, you can use your sneak attack against the target, regardless of the situation.", "You gain the ability to decipher an opponent's tactics and develop a counter to them. As a bonus action, you can make a Wisdom (Insight) check against a creature you can see that isn't incapacitated, contested by the target's Charisma (Deception) check. If you succeed, you can use your Sneak Attack against that target even if you don't have advantage on the attack roll, but not if you have disadvantage on it.<br>This benefit lasts for 1 minute or until you successfully use this feature against a different target.", 3); -- 349
INSERT INTO archetype_features VALUES (NULL, 67, "Steady Eye", "Advantage on any Wisdom (Perception) or Intelligence (Investigation) check if you move no more than half your speed.", "You have advantage on any Wisdom (Perception) or Intelligence (Investigation) check if you move no more than half your speed on the same turn.", 9); -- 350
INSERT INTO archetype_features VALUES (NULL, 67, "Unerring Eye", "Action to sense illusions and shapechangers within 30ft. You sense that an effect is attempting to trick you. Use WIS modifier number of times, per long rest.", "Your senses are almost impossible to foil. As an action, you sense the presence of illusions, shapechangers not in their original form, and other magic designed to deceive the senses within 30 feet of you, provided you aren't blinded or deafened. You sense that an effect is attempting to trick you, but you gain no insight into what is hidden or into its true nature.<br>You can use this feature a number of times equal to your Wisdom modifier (minimum of once), and you regain all expended uses of it when you finish a long rest.", 13); -- 351
INSERT INTO archetype_features VALUES (NULL, 67, "Eye for Weakness", "When Insightful Fighting applies, sneak attack deals 3d6 extra dmg.", "You learn to exploit a creature's weaknesses by carefully studying its tactics and movement. While your Insightful Fighting feature applies to a creature, your Sneak Attack damage against that creature increases by 3d6.", 17); -- 352
INSERT INTO archetype_features VALUES (NULL, 68, "Master of Intrigue", "You unerringly mimic speech of a creature you hear for at least 1 minute.", "You gain proficiency with the disguise kit, the forgery kit, and one gaming set of your choice. You also learn two languages of your choice.<br>Additionally, you can unerringly mimic the speech patterns and accent of a creature that you hear speak for at least 1 minute, enabling you to pass yourself off as a native speaker of a particular land, provided that you know the language.", 3); -- 353
INSERT INTO archetype_features VALUES (NULL, 68, "Master of Tactics", "Use Help as a bonus action. Aid ally attack within 30ft.", "You can use the Help action as a bonus action. Additionally, when you use the Help action to aid an ally in attacking a creature, the target of that attack can be within 30 feet of you, rather than within 5 feet of you, if the target can see or hear you.", 3); -- 354
INSERT INTO archetype_features VALUES (NULL, 68, "Insightful Manipulator", "Spend 1 minute observing another creature outside combat to learn various traits about it.", "If you spend at least 1 minute observing or interacting with another creature outside combat, you can learn certain information about its capabilities compared to your own. The DM tells you if the creature is your equal, superior, or inferior in regard to two of the following characteristics of your choice:<br><ul><li>Intelligence score</li><li>Wisdom score</li><li>Charisma score</li><li>classes levels (if any)</li></ul><br>At the DM's option, you might also realize you know a piece of the creature's history or one of its personality traits, if it has any.", 9); -- 355
INSERT INTO archetype_features VALUES (NULL, 68, "Misdirection", "When targeted and there is a creature within 5ft granting you cover against it, use reaction to have attack target that creature instead.", "You can sometimes cause another creature to suffer an attack meant for you. When you are targeted by an attack while a creature within 5 feet of you is granting you cover against that attack, you can use your reaction to have the attack target that creature instead of you.", 13); -- 356
INSERT INTO archetype_features VALUES (NULL, 68, "Soul of Deceit", "Thoughts can't be read unless you permit it, magic to detect truth always indicates truth. Charisma (Deception) check contested by reader's Wisdom (Insight) check to present false truths.", "Your thoughts can't be read by telepathy or other means, unless you allow it. You can present false thoughts by succeeding on a Charisma (Deception) check contested by the mind reader's Wisdom (Insight) check.<br>Additionally, no matter what you say, magic that would determine if you are telling the truth indicates you are being truthful if you so choose, and you can't be compelled to tell the truth by magic.", 17); -- 357
INSERT INTO archetype_features VALUES (NULL, 69, "Skirmisher", "When enemy ends its turn within 5ft, reaction to move half speed, doesn't provoke opportunity.", "You are difficult to pin down during a fight. You can move up to half your speed as a reaction when an enemy ends its turn within 5 feet of you. This movement doesn't provoke opportunity attacks.", 3); -- 358
INSERT INTO archetype_features VALUES (NULL, 69, "Survivalist", "Proficiency bonus is doubled for Nature and Survival checks.", "You gain proficiency in the Nature and Survival skills if you don't already have it. Your proficiency bonus is doubled for any ability check you make that uses either of those proficiencies.", 3); -- 359
INSERT INTO archetype_features VALUES (NULL, 69, "Superior Mobility", "Speed increased by 10ft (for all movement).", "Your walking speed increases by 10 feet. If you have a climbing or swimming speed, this increase applies to that speed as well.", 9); -- 360
INSERT INTO archetype_features VALUES (NULL, 69, "Ambush Master", "You have advantage on initiative rolls. The first creature you hit, all future hits have advantage until the start of your next turn.", "You excel at leading ambushes and acting first in a fight.<br>You have advantage on initiative rolls. In addition, the first creature you hit during the first round of a combat becomes easier for you and others to strike; attack rolls against that target have advantage until the start of your next turn.", 13); -- 361
INSERT INTO archetype_features VALUES (NULL, 69, "Sudden Strike", "When you take the Attack action, you make an additional attack as a bonus action.", "You can strike with deadly speed. If you take the Attack action on your turn, you can make one additional attack as a bonus action. This attack can benefit from your Sneak Attack even if you have already used it this turn, but you can't use your Sneak Attack against the same target more than once in a turn.", 17); -- 362
INSERT INTO archetype_features VALUES (NULL, 70, "Fancy Footwork", "Creatures you hit can't make opportunity attacks against you that turn.", "You learn how to land a strike and then slip away without reprisal. During your turn, if you make a melee attack against a creature, that creature can't make opportunity attacks against you for the rest of your turn.", 3); -- 363
INSERT INTO archetype_features VALUES (NULL, 70, "Rakish Audacity", "Bonus to initiative = CHA modifier.<br>Can use Sneak Attack if no other creatures are within 5ft, and you don't have disadvantage.", "Your confidence propels you into battle. You can give yourself a bonus to your initiative rolls equal to your Charisma modifier.<br>You also gain an additional way to use your Sneak Attack; you don't need advantage on the attack roll to use your Sneak Attack against a creature if you are within 5 feet of it, no other creatures are within 5 feet of you, and you don't have disadvantage on the attack roll. All the other rules for Sneak Attack still apply to you.", 3); -- 364
INSERT INTO archetype_features VALUES (NULL, 70, "Panache", "Action to make a Charisma (Persuasion) check contested by a creature's Wisdom (Insight) check.<br>On success, if hostile it has disadvantage against other targets and can't make opportunity attacks. This lasts 1 min, or until attacked or a spell is cast against it.<br>If not hostile, is charmed for 1 minute, regards you as a friendly acquaintance.", "Your charm becomes extraordinarily beguiling. As an action, you can make a Charisma (Persuasion) check contested by a creature's Wisdom (Insight) check. The creature must be able to hear you, and the two of you must share a language.<br>If you succeed on the check and the creature is hostile to you, it has disadvantage on attack rolls against targets other than you and can't make opportunity attacks against targets other than you. This effect lasts for 1 minute, until one of your companions attacks the target or affects it with a spell, or until you and the target are more than 60 feet apart.<br>If you succeed on the check and the creature isn't hostile to you, it is charmed by you for 1 minute. While charmed, it regards you as a friendly acquaintance. This effect ends immediately if you or your companions do anything harmful to it.", 9); -- 365
INSERT INTO archetype_features VALUES (NULL, 70, "Elegant Maneuver", "Bonus action to gain advantage on the next Dexterity (Acrobatics) or Strength (Athletics) check you make during the same turn.", "You can use a bonus action on your turn to gain advantage on the next Dexterity (Acrobatics) or Strength (Athletics) check you make during the same turn.", 13); -- 366
INSERT INTO archetype_features VALUES (NULL, 70, "Master Duelist", "If you miss with an attack roll, you can roll it again with advantage. Once you do so, you can't use this feature again until you finish a short or long rest.", "Your mastery of the blade lets you turn failure into success in combat. If you miss with an attack roll, you can roll it again with advantage. Once you do so, you can't use this feature again until you finish a short or long rest.", 17); -- 367
INSERT INTO archetype_features VALUES (NULL, 71, "Whispers of the Dead", "After a rest choose one skill or tool proficiency to gain. You lose this proficiency when you choose a different one.", "Echoes of those who have died cling to you. Whenever you finish a short or long rest, you can choose one skill or tool proficiency that you lack and gain it, as a ghostly presence shares its knowledge with you. You lose this proficiency when you use this feature to choose a different proficiency that you lack.", 3); -- 368
INSERT INTO archetype_features VALUES (NULL, 71, "Wails from the Grave", "Immediatly after dealing Sneak Attack damage, target second creature within 30ft. Deal half Sneak Attack dice (rounded up) as necrotic damage. Use feature number of times equal to proficiency bonus, reset on long rest.", "As you nudge someone closer to the grave, you can channel the power of death to harm someone else as well. Immediately after you deal your Sneak Attack damage to a creature on your turn, you can target a second creature that you can see within 30 feet of the first creature. Roll half the number of Sneak Attack dice for your level (round up), and the second creature takes necrotic damage equal to the roll's total, as wails of the dead sound around them for a moment.<br><br>You can use this feature a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.", 3); -- 369
INSERT INTO archetype_features VALUES (NULL, 71, "Tokens of the Departed", "When a creature dies within 30ft, reaction to create soul trinket. Have number of trinkets equal to proficiency bonus. Use a trinket in the following ways:<br><ul><li>While you have a Sould Trinket, advantage on death saves and Constitution saves.</li><li>On Sneak Attack, consume Trinket and use Wails from the Grave without spending a use.</li><li>Action to expend trinket to ask the spirit one question. Responds in language it knew, and dones't have to be truthfull.</li></ul>", "When a life ends in your presence, you're able to snatch a token from the departing soul, a sliver of its life essence that takes physical form: as a reaction when a creature you can see dies within 30 feet of you, you can open your free hand and cause a Tiny trinket to appear there, a soul trinket. The DM determines the trinket's form or has you roll on the Trinkets table in the Player's Handbook to generate it.<br><br>You can have a maximum number of soul trinkets equal to your proficiency bonus, and you can't create one while at your maximum.<br><br>You can use soul trinkets in the following ways:<br><br><ul><li>While a soul trinket is on your person, you have advantage on death saving throws and Constitution saving throws, for your vitality is enhanced by the life essence within the object.</li><li>When you deal Sneak Attack damage on your turn, you can destroy one of your soul trinkets that's on your person and then immediately use Wails from the Grave, without expending a use of that feature.</li><li>As an action, you can destroy one of your soul trinkets, no matter where it's located. When you do so, you can ask the spirit associated with the trinket one question. The spirit appears to you and answers in a language it knew in life. It's under no obligation to be truthful, and it answers as concisely as possible, eager to be free. The spirit knows only what it knew in life, as determined by the DM.</li></ul>", 9); -- 370
INSERT INTO archetype_features VALUES (NULL, 71, "Ghost Walk", "Once per long rest (or by expending a Trinket), use bonus action to become spectral for 10 mins (end as bonus action). Flying speed of 10ft, with hover, attack rolls against you have disadvantage. You can move through creatures (take 1d10 if you end your turn inside a creature or object).", "You can phase partially into the realm of the dead, becoming like a ghost. As a bonus action, you assume a spectral form. While in this form, you have a flying speed of 10 feet, you can hover, and attack rolls have disadvantage against you. You can also move through creatures and objects as if they were difficult terrain, but you take ldlO force damage if you end your turn inside a creature or an object.<br><br>You stay in this form for 10 minutes or until you end it as a bonus action. To use this feature again, you must finish a long rest or destroy one of your soul trinkets as part of the bonus action you use to activate Ghost Walk.", 13); -- 371
INSERT INTO archetype_features VALUES (NULL, 71, "Death's Friend", "You gain the following:<br><ul><li>Wails from the Grave applies to the initial and targeted creature.</li><li>If you have none, gain a Soul Trinket at the end of a long rest.</li></ul>", "Your association with death has become so close that you gain the following benefits:<br><br><ul><li>When you use your Wails from the Grave, you can deal the necrotic damage to both the first and the second creature.</li><li>At the end of a long rest, a soul trinket appears in your hand if you don't have any soul trinkets, as the spirits of the dead are drawn to you.</li></ul>", 17); -- 372
INSERT INTO archetype_features VALUES (NULL, 72, "Psionic Power", "You gain Psionic Energy die equal to twice your proficiency. You regain expended dice on a long rest, and once per rest you can use a bonus action to regain one Energy die. They are a d6 at 3rd level, d8 at 5th, d10 at 11th, d12 at 17th. You have the following abilities:<br>><strong>Psi-Bolstered Knack.</strong> if you fail a ability check you're proficient in roll and add Energy die result (die expended on success).<br><strong>Psychic Whispers.</strong> Action choose creatures (up to proficiency bonus), roll Energy die for hours equal to roll chosen creatures can speak telepathically with each other within 1 mile. Doesn't expend a die the first time each long rest, does each time after.", "You harbor a wellspring of psionic energy within yourself. This· energy is represented by your Psionic Energy dice, which are each a d6. You have a number of these dice equal to twice your proficiency bonus, and they fuel various psionic powers you have, which are detailed below.<br><br>Some of your powers expend the Psionic Energy die they use, as specified in a power's description, and you can't use a power if it requires you to use a die when your dice are all expended. You regain all your expended Psionic Energy dice when you finish a long rest. In addition, as a bonus action, you can regain one expended Psionic Energy die, but you can't do so again until you finish a short or long rest.<br><br>When you reach certain levels in this class, the size of your Psionic Energy dice increases: at 5th level (d8), 11th level (dl0), and 17th level (d12).<br><br>The powers below use your Psionic Energy dice.<br><br><strong>Psi-Bolstered Knack.</strong> When your nonpsionic training fails you, your psionic power can help: if you fail an ability check using a skill or tool with which you have proficiency, you can roll one Psionic Energy die and add the number rolled to the check, potentially turning failure into success. You expend the die only if the roll succeeds.<br><br><strong>Psychic Whispers.</strong> You can establish telepathic communication between yourself and others-perfect for quiet infiltration. As an action, choose one or more creatures you can see, up to a number of creatures equal to your proficiency bonus, and then roll one Psionic Energy die. For a number of hours equal to the number rolled, the chosen creatures can speak telepathically with you, and you can speak telepathically with them. To send or receive a message (no action required), you and the other creature must be within 1 mile of each other. A creature can't use this telepathy if it can't speak any languages, and a creature can end the telepathic connection at any time (no action required). You and the creature don't need to speak a common language to understand each other.<br>The first time you use this power after each long rest, you don't expend the Psionic Energy die. All other times you use the power, you expend the die.", 3); -- 373
INSERT INTO archetype_features VALUES (NULL, 72, "Psychic Blades", "Whenever you attack, manifest psychic blade in off-hand and make an attack. Range 60ft, 1d6 psychic damage + Attack Ability modifier. After you attack make a melee or ranged weapon attack with a second blade as a bonus action provided your other hand is free, this does 1d4 damage.", "You can manifest your psionic power as shimmering blades of psychic energy. Whenever you take the Attack action, you can manifest a psychic blade from your free hand and make the attack with that blade. This magic blade is a simple melee weapon with the finesse and thrown properties. It has a normal range of 60 feet and no long range, and on a hit, it deals psychic damage equal to ld6 plus the ability modifier you used for the attack roll. The blade vanishes immediately after it hits or misses its target, and it leaves no mark on its target if it deals damage.<br><br>After you attack with the blade, you can make a melee or ranged weapon attack with a second psychic blade as a bonus action on the same turn, provided your other hand is free to create it. The damage die of this bonus attack is ld4, instead of ld6.", 3); -- 374
INSERT INTO archetype_features VALUES (NULL, 72, "Soul Blades", "You gain additional Psionic Energy die features:<br><strong>Homing Strikes.</strong> If you miss with a Psychic Blade, roll Energy die and add result to attack roll (expended on hit).<br><strong>Psychic Teleportation.</strong> Bonus action to manifest Psychic Blade throw 10 x number rolled feet and teleport to that space.", "Your Psychic Blades are now an expression of your psi-suffused soul, giving you these powers that use your Psionic Energy dice:<br><br><strong>Homing Strikes.</strong> If you make an attack roll with your Psychic Blades and miss the target, you can roll one Psionic Energy die and add the number rolled to the attack roll. If this causes the attack to hit, you expend the Psionic Energy die.<br><br><strong>Psychic Teleportation.</strong> As a bonus action, you manifest one of your Psychic Blades, expend one Psionic Energy die and roll it, and throw the blade at an unoccupied space you can see, up to a number of feet away equal to 10 times the number rolled. You then teleport to that space, and the blade vanishes.", 9); -- 375
INSERT INTO archetype_features VALUES (NULL, 72, "Psychic Veil", "Once per long rest (or by expending an Energy die), use an action to become invisible for 1 hour, until dismissed, you deal damage, or force a creature to make a save.", "You can weave a veil of psychic static to mask yourself. As an action, you can magically become invisible, along with anything you are wearing or carrying, for 1 hour or until you dismiss this effect (no action required). This invisibility ends early immediately after you deal damage to a creature or you force a creature to make a saving throw.<br><br>Once you use this feature, you can't do so again until you finish a long rest, unless you expend a Psionic Energy die to use this feature again.", 13); -- 376
INSERT INTO archetype_features VALUES (NULL, 72, "Rend Mind", "Once per long rest (or be expending an Energy die), when you deal Sneak Attack damage force target to make a Wisdom save (DC = 8 + proficiency + Dexterity modifier). On fail stunned for 1 min, target can repeat save at end of their turn.", "You can sweep your Psychic Blades directly through a creature's mind. When you use your Psychic Blades to deal Sneak Attack damage to a creature, you can force that target to make a Wisdom saving throw (DC equal to 8 + your proficiency bonus + your Dexterity modifier). If the save fails, the target is stunned for 1 minute. The stunned target can repeat the saving throw at the end of each of its turns, ending the effect on itself on a success.<br><br>Once you use this feature, you can't do so again until you finish a long rest, unless you expend three Psionic Energy dice to use it again.", 17); -- 377
INSERT INTO class_features VALUES (NULL, 10, "Spellcasting", "Spell save DC = 8 + prof. + CHA<br>Spell attack modifier = prof. + CHA", "An event in your past, or in the life of a parent or ancestor, left an indelible mark on you, infusing you with arcane magic. This font of magic, whatever its origin, fuels your spells. See chapter 10 for the general rules of spellcasting and chapter 11 for the sorcerer spell list.<br><br><b>Cantrips</b><br>At 1st level, you know four cantrips of your choice from the sorcerer spell list. You learn additional sorcerer cantrips of your choice at higher levels, as shown in the Cantrips Known column of the Sorcerer table.<br><br><b>Spell Slots</b><br>The Sorcerer table shows how many spell slots you have to cast your spells of 1st level and higher. To cast one of these spells, you must expend a slot of the spell's level or higher. You regain all expended spell slots when you finish a long rest.<br>For example, if you know the 1st-level spell <i>burning hands</i> and have a 1st-level and a 2nd-level spell slot available, you can cast <i>burning hands</i> using either slot.<br><br><b>Spells Known of 1st level and Higher</b><br>You know two 1st-level spells of your choice from the sorcerer spell list.<br>The Spells Known column of the Sorcerer table shows when you learn more sorcerer spells of your choice. Each of these spells must be of a level for which you have spell slots, as shown on the table. For instance, when you reach 3rd level in this class, you can learn one new spell of 1st or 2nd level.<br>Additionally, when you gain a level in this class, you can choose one of the sorcerer spells you know and replace it with another spell from the sorcerer spell list, which also must be of a level for which you have spell slots.<br><br><b>Spellcasting Ability</b><br>Charisma is your spellcasting ability for your sorcerer spells, since the power of your magic relies on your ability to project your will into the world. You use your Charisma whenever a spell refers to your spellcasting ability. In addition, you use your Charisma modifier when setting the saving throw DC for a sorcerer spell you cast and when making an attack roll with one.<br>Spell save DC = 8 + proficiency bonus + your Charisma modifier<br>Spell attack modifier = your proficiency bonus + your Charisma modifier<br><br><b>Spellcasting Focus</b><br>You can use an arcane focus (found in chapter 5) as a spellcasting focus for your sorcerer spells.", 1, 0); -- 120
INSERT INTO class_features VALUES (NULL, 10, "Sorcerous Origin", NULL, "A Sorcerous origin describes the source of your innate magical power. Your choice grants you features when you choose it at 1st level and again at 6th, 14th, and 18th level.", 1, 0); -- 121
INSERT INTO class_sub_features VALUES (NULL, 121, "Draconic Bloodline", NULL, "Your innate magic comes from draconic magic that was mingled with your blood or that of your ancestors. Most often, sorcerers with this origin trace their descent back to a mighty sorcerer of ancient times who made a bargain with a dragon or who might even have claimed a dragon parent. Some of these bloodlines are well established in the world, but most are obscure. Any given sorcerer could be the first of a new bloodline, as a result of a pact or some other exceptional circumstance.", 0); -- 120
INSERT INTO class_sub_features VALUES (NULL, 121, "Wild Magic", NULL, "Your innate magic comes from the w ild forces of chaos that underlie the order of creation. You might have endured exposure to some form of raw magic, perhaps through a planar portal leading to Limbo, the Elemental Planes, or the mysterious Far Realm. Perhaps you were blessed by a powerful fey creature or marked by a demon. Or your magic could be a fluke of your birth, with no apparent cause or reason. However it came to be, this chaotic magic churns within you, waiting for any outlet.", 0); -- 121
INSERT INTO class_sub_features VALUES (NULL, 121, "Divine Soul", NULL, "Sometimes the spark of magic that fuels a sorcerer comes from a divine source that glimmers within the soul. Having such a blessed soul is a sign that your innate magic might come from a distant but powerful familial connection to a divine being. Perhaps your ancestor was an angel, transformed into a mortal and sent to fight in a god's name. Or your birth might align with an ancient prophecy, marking you as a servant of the gods or a chosen vessel of divine magic.<br><br>A Divine Soul, with a natural magnetism, is seen as a threat by some religious hierarchies. As an outsider who commands sacred power, a Divine Soul can undermine an existing order by claiming a direct tie to the divine.<br><br>In some cultures, only those who can claim the power of a Divine Soul may command religious power. In these lands, ecclesiastical positions are dominated by a few bloodlines and preserved over generations.", 0); -- 122
INSERT INTO class_sub_features VALUES (NULL, 121, "Shadow Magic", NULL, "You are a creature of shadow, for your innate magic comes from the Shadowfell itself. You might trace your lineage to an entity from that place, or perhaps you were exposed to its fell energy and transformed by it.<br><br>The power of shadow magic casts a strange pall over your physical presence. The spark of life that sustains you is muffled, as if it struggles to remain viable against the dark energy that imbues your soul. At your option, you can pick from or roll on the Shadow Sorcerer Quirks table to create a quirk for your character.", 0); -- 123
INSERT INTO class_sub_features VALUES (NULL, 121, "Storm Sorcery", NULL, "Your innate magic comes from the power of elemental air. Many with this power can trace their magic back to a near-death experience caused by the Great Rain, but perhaps you were born during a howling gale so powerful that folk still tell stories of it, or your lineage might include the influence of potent air creatures such as djinn. Whatever the case, the magic of the storm permeates your being.<br><br>Storm sorcerers are invaluable members of a ship's crew. Their magic allows them to exert control over wind and weather in their immediate area. Their abilities also prove useful in repelling attacks by sahuagin, pirates, and other waterborne threats.", 0); -- 124
INSERT INTO class_sub_features VALUES (NULL, 121, "Aberrant Mind", NULL, "An alien influence has wrapped its tendrils around your mind, giving you psionic power. You can now touch other minds with that power and alter the world around you by using it to control the magical energy of the multiverse. Will this power shine from you as a hopeful beacon to others? Or will you be a source of terror to those who feel the stab of your mind and witness the strange manifestations of your might?<br><br>As an Aberrant Mind sorcerer, you decide how you acquired your powers. Were you born with them? Or did an event later in life leave you shining with psionic awareness? Consult the Aberrant Origins table for a possible origin of your power.", 0); -- 125
INSERT INTO class_sub_features VALUES (NULL, 121, "Clockwork Soul", NULL, "The cosmic force of order has suffused you with magic. That power arises from Mechanus or a realm like it-a plane of existence shaped entirely by clockwork efficiency. You, or someone from your lineage, might have become entangled in the machinations of the modrons, the orderly beings who inhabit Mechanus. Perhaps your ancestor even took part in the Great Modron March. Whatever its origin within you, the power of order can seem strange to others, but for you, it is part of a vast and glorious system.", 0); -- 126
INSERT INTO class_features VALUES (NULL, 10, "Font of Magic", "You have sorcery points based on your level, which recover after a long rest.<br><b>Flexible Casting</b>You can spend sorcery points to gain spell slots, or sacrifice slots to gain points.", "You tap into a deep wellspring of magic within yourself. This wellspring is represented by sorcery points, which allow you to create a variety of magical effects.<br><br><b>Sorcery Points</b><br>You have 2 sorcery points, and you gain more as you reach higher levels, as shown in the Sorcery Points column of the Sorcerer table. You can never have more sorcery points than shown on the table for your level. You regain all spent sorcery points when you finish a long rest.<br><br><b>Felxible Casting</b><br>You can use your sorcery points to gain additional spell slots, or sacrifice spell slots to gain additional sorcery points. You learn other ways to use your sorcery points as you reach higher levels.<br><b>Creating Spell Slots</b>. You can transform unexpended sorcery points into one spell slot as a bonus action on your turn. The Creating Spell Slots table shows the cost of creating a spell slot of a given level. You can create spell slots no higher in level than 5th.<br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Spell Slot level</th><th>Sorcery Point Cost</th></tr></thead><tbody><tr><td>1st</td><td>2</td></tr><tr><td>2nd</td><td>3</td></tr><tr><td>3rd</td><td>5</td></tr><tr><td>4th</td><td>6</td></tr><tr><td>5th</td><td>7</td></tr></tbody></table><br><b>Converting a Spell Slot to Sorcery Points</b>. As a bonus action on your turn, you can expend one spell slot and gain a number of sorcery points equal to the slot's level.", 2, 0); -- 122
INSERT INTO class_features VALUES (NULL, 10, "Metamagic", "You can alter your spells, only using one option per spell when you cast it (unless otherwise noted).", "You gain the ability to twist your spells to suit your needs. You gain two of the following Metamagic options of your choice. You gain another one at 10th and 17th level.<br>You can use only one Metamagic option on a spell when you cast it, unless otherwise noted.", 3, 0); -- 123
INSERT INTO class_sub_features VALUES (NULL, 123, "Careful Spell", NULL, "When you cast a spell that forces other creatures to make a saving throw, you can protect some of those creatures from the spell's full force. To do so, you spend 1 sorcery point and choose a number of those creatures up to your Charisma modifier (minimum of one creature). A chosen creature automatically succeeds on its saving throw against the spell.", 0); -- 127
INSERT INTO class_sub_features VALUES (NULL, 123, "Distant Spell", NULL, "When you cast a spell that has a range of 5 feet or greater, you can spend 1 sorcery point to double the range of the spell.<br>When you cast a spell that has a range of touch, you can spend 1 sorcery point to make the range of the spell 30 feet.", 0); -- 128
INSERT INTO class_sub_features VALUES (NULL, 123, "Empowered Spell", NULL, "When you roll damage for a spell, you can spend 1 sorcery point to reroll a number of the damage dice up to your Charisma modifier (minimum of one). You must use the new rolls.<br>You can use Empowered Spell even if you have already used a different Metamagic option during the casting of the spell.", 0); -- 129
INSERT INTO class_sub_features VALUES (NULL, 123, "Extended Spell", NULL, "When you cast a spell that has a duration of 1 minute or longer, you can spend 1 sorcery point to double its duration, to a maximum duration of 24 hours.", 0); -- 130
INSERT INTO class_sub_features VALUES (NULL, 123, "Heightened Spell", NULL, "When you cast a spell that forces a creature to make a saving throw to resist its effects, you can spend 3 sorcery points to give one target of the spell disadvantage on its first saving throw made against the spell.", 0); -- 131
INSERT INTO class_sub_features VALUES (NULL, 123, "Quickened Spell", NULL, "When you cast a spell that has a casting time of 1 action, you can spend 2 sorcery points to change the casting time to 1 bonus action for this casting.", 0); -- 132
INSERT INTO class_sub_features VALUES (NULL, 123, "Subtle Spell", NULL, "When you cast a spell, you can spend 1 sorcery point to cast it without any somatic or verbal components.", 0); -- 133
INSERT INTO class_sub_features VALUES (NULL, 123, "Twinned Spell", NULL, "When you cast a spell that targets only one creature and doesn't have a range of self, you can spend a number of sorcery points equal to the spell's level to target a second creature in range with the same spell (1 sorcery point if the spell is a cantrip).", 0); -- 134
INSERT INTO class_sub_features VALUES (NULL, 123, "Seeking Spell", NULL, "If you make an attack roll for a spell and miss, you can spend 2 sorcery points to reroll the d20, and you must use the new roll.<br>You can use Seeking Spell even if you have already used a different Metamagic option during the casting of the spell.", 1); -- 135
INSERT INTO class_sub_features VALUES (NULL, 123, "Transmuted Spell", NULL, "When you cast a spell that deals a type of damage from the following list, you can spend 1 sorcery point to change that damage type to one of the other listed types: acid, cold, fire, lightning, poison, thunder.", 1); -- 136
INSERT INTO class_features VALUES (NULL, 10, "Sorcerous Restoration", "You regain 4 expended sorcery points whenever you finish a short rest.", "You regain 4 expended sorcery points whenever you finish a short rest.", 20, 0); -- 124
INSERT INTO class_features VALUES (NULL, 10, "Sorcerous Versatility", "When you gain an ASI in addition you may also: replace one metamagic feature and replace one cantrip.", "Whenever you reach a level in this class that grants the Ability Score Improvement feature, you can do one of the following, representing the magic within you flowing in new ways:<br><br><ul><li>Replace one of the options you chose for the Metamagic feature with a different Metamagic option available to you.</li><li>Replace one cantrip you learned from this class's Spellcasting feature with another cantrip from the sorcerer spell list.</li></ul>", 4, 1); -- 125
INSERT INTO class_features VALUES (NULL, 10, "Magical Guidance", "When you fail an ability check, spend 1 sorcery point to reroll.", "You can tap into your inner wellspring of magic to try to conjure success from failure. When you make an ability check that fails, you can spend 1 sorcery point to reroll the d20, and you must use the new roll, potentially turning the failure into a success.", 5, 1); -- 126
INSERT INTO archetype_features VALUES (NULL, 73, "Dragon Ancestor", "You can speak, read, and write Draconic.<br>Prof bonus doubled on Charisma checks against dragons.", "You choose one type of dragon as your ancestor. The damage type associated with each dragon is used by features you gain later.<br>You can speak, read, and write Draconic. Additionally, whenever you make a Charisma check when interacting with dragons, your proficiency bonus is doubled if it applies to the check.", 1); -- 378
INSERT INTO archetype_sub_features VALUES (NULL, 378, "Black", NULL, "Acid", 0); -- 88
INSERT INTO archetype_sub_features VALUES (NULL, 378, "Blue", NULL, "Lightning", 0); -- 89
INSERT INTO archetype_sub_features VALUES (NULL, 378, "Brass", NULL, "Fire", 0); -- 90
INSERT INTO archetype_sub_features VALUES (NULL, 378, "Bronze", NULL, "Lightning", 0); -- 91
INSERT INTO archetype_sub_features VALUES (NULL, 378, "Copper", NULL, "Acid", 0); -- 92
INSERT INTO archetype_sub_features VALUES (NULL, 378, "Gold", NULL, "Fire", 0); -- 93
INSERT INTO archetype_sub_features VALUES (NULL, 378, "Green", NULL, "Poison", 0); -- 94
INSERT INTO archetype_sub_features VALUES (NULL, 378, "Red", NULL, "Fire", 0); -- 95
INSERT INTO archetype_sub_features VALUES (NULL, 378, "Silver", NULL, "Cold", 0); -- 96
INSERT INTO archetype_sub_features VALUES (NULL, 378, "White", NULL, "Cold", 0); -- 97
INSERT INTO archetype_features VALUES (NULL, 73, "Draconic Resilience", "Gain an extra hit point per level.<br>When unarmored AC = 13 + Dex mod.", "As magic flows through your body, it causes physical traits of your dragon ancestors to emerge. Your hit point maximum increases by 1 and increases by 1 again whenever you gain a level in this class.<br>Additionally, parts of your skin are covered by a thin sheen of dragon-like scales. When you aren't wearing armor, your AC equals 13 + your Dexterity modifier.", 1); -- 379
INSERT INTO archetype_features VALUES (NULL, 73, "Elemental Affinity", "Add CHA mod to damage that matches your ancestry damage.<br>Spend 1 sorcery point to gain resistance to that damage for 1 hour.", "When you cast a spell that deals damage of the type associated with your draconic ancestry, add your Charisma modifier to that damage. At the same time, you can spend 1 sorcery point to gain resistance to that damage type for 1 hour.", 6); -- 380
INSERT INTO archetype_features VALUES (NULL, 73, "Dragon Wings", "Bonus action gain flying speed equal to current speed. Dismiss them as a bonus action.", "You gain the ability to sprout a pair of dragon wings from your back, gaining a flying speed equal to your current speed. You can create these wings as a bonus action on your turn. They last until you dismiss them as a bonus action on your turn.<br>You can't manifest your wings while wearing armor unless the armor is made to accommodate them, and clothing not made to accommodate your wings might be destroyed when you manifest them.", 14); -- 381
INSERT INTO archetype_features VALUES (NULL, 73, "Draconic Presence", "Action, spend 5 sorcery points, exude aura of awe or fear to 60ft. For 1 min, concentrating, hostile creatures that start turn inside WIS save or charmed or frightened (based on aura) until aura ends. Success they are immune for 24 hours.", "You can channel the dread presence of your dragon ancestor, causing those around you to become awestruck or frightened. As an action, you can spend 5 sorcery points to draw on this power and exude an aura of awe or fear (your choice) to a distance of 60 feet. For 1 minute or until you lose your concentration (as if you were casting a concentration spell), each hostile creature that starts its turn in this aura must succeed on a Wisdom saving throw or be charmed (if you chose awe) or frightened (if you chose fear) until the aura ends. A creature that succeeds on this saving throw is immune to your aura for 24 hours.", 18); -- 382
INSERT INTO archetype_features VALUES (NULL, 74, "Wild Magic Surge", "After you cast a 1st level or higher spell, roll a d20, on a 1 roll on the Wild Magic Surge table.", "Your spellcasting can unleash surges of untamed magic. Immediately after you cast a sorcerer spell of 1st level or higher, the DM can have you roll a d20. If you roll a 1, roll on the Wild Magic Surge table to create a random magical effect.", 1); -- 383
INSERT INTO archetype_features VALUES (NULL, 74, "Tides of Chaos", "Once per long rest, gain advantage on one attack roll, ability check, or saving throw. After use before regain, after casting a 1st lvl+ spell the DM can force you to roll a Wild Magic Surge, you then regain this.", "You can manipulate the forces of chance and chaos to gain advantage on one attack roll, ability check, or saving throw. Once you do so, you must finish a long rest before you can use this feature again.<br>Any time before you regain the use of this feature, the DM can have you roll on the Wild Magic Surge table immediately after you cast a sorcerer spell of 1st level or higher. You then regain the use of this feature.", 1); -- 384
INSERT INTO archetype_features VALUES (NULL, 74, "Bend Luck", "Reaction, spend 2 sorcery points, roll 1d4 add or subtract to a creatures attack roll, ability check or saving throw.", "You have the ability to twist fate using your wild magic. When another creature you can see makes an attack roll, an ability check, or a saving throw, you can use your reaction and spend 2 sorcery points to roll 1d4 and apply the number rolled as a bonus or penalty (your choice) to the creature's roll. You can do so after the creature rolls but before any effects of the roll occur.", 6); -- 385
INSERT INTO archetype_features VALUES (NULL, 74, "Controlled Chaos", "Whenever you roll on the Wild Magic Surge table, you can roll twice and use either number.", "You gain a modicum of control over the surges of your wild magic. Whenever you roll on the Wild Magic Surge table, you can roll twice and use either number.", 14); -- 386
INSERT INTO archetype_features VALUES (NULL, 74, "Spell Bombardment", "Once per turn, on dmg roll, if you roll max on any one dice, roll it again and add damage.", "The harmful energy of your spells intensifies. When you roll damage for a spell and roll the highest number possible on any of the dice, choose one of those dice, roll it again and add that roll to the damage. You can use the feature only once per turn.", 18); -- 387
INSERT INTO archetype_features VALUES (NULL, 75, "Divine Magic", NULL, "Your link to the divine allows you to learn spells from the cleric class. When your Spellcasting feature lets you learn or replace a sorcerer cantrip or a sorcerer spell of 1st level or higher, you can choose the new spell from the cleric spell list or the sorcerer spell list. You must otherwise obey all the restrictions for selecting the spell, and it becomes a sorcerer spell for you.<br>In addition, choose an affinity for the source of your divine power: good, evil, law, chaos, or neutrality. You learn an additional spell based on that affinity, as shown below. It is a sorcerer spell for you, but it doesn't count against your number of sorcerer spells known. If you later replace this spell, you must replace it with a spell from the cleric spell list.<br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Affinity</th><th>Spell</th></tr></thead><tbody><tr><td>Good</td><td>Cure Wounds</td></tr><tr><td>Evil</td><td>Inflict Wounds</td></tr><tr><td>Law</td><td>Bless</td></tr><tr><td>Chaos</td><td>Bane</td></tr><tr><td>Neutrality</td><td>Protection from Evil and Good</td></tr></tbody></table>", 1); -- 388
INSERT INTO archetype_features VALUES (NULL, 75, "Favored by the Gods", "Once per rest, if you fail an attack roll or saving throw, roll 2d4 and add it to total.", "Divine power guards your destiny. If you fail a saving throw or miss with an attack roll, you can roll 2d4 and add it to the total, possibly changing the outcome. Once you use this feature, you can't use it again until you finish a short or long rest.", 1); -- 389
INSERT INTO archetype_features VALUES (NULL, 75, "Empowered Healing", "Once per turn, spend 1 sorcery point, allies healing within 5ft choose to reroll any number of the dice once.", "The divine energy coursing through you can empower healing spells. Whenever you or an ally within 5 feet of you rolls dice to determine the number of hit points a spell restores, you can spend 1 sorcery point to reroll any number of those dice once, provided you aren't incapacitated. You can use this feature only once per turn.", 6); -- 390
INSERT INTO archetype_features VALUES (NULL, 75, "Otherworldly Wings", "Bonus action, fly speed of 30ft until incapacitated, dead, or dismissed.", "You can use a bonus action to manifest a pair of spectral wings from your back. While the wings are present, you have a flying speed of 30 feet. The wings last until you're incapacitated, you die, or you dismiss them as a bonus action.<br>The affinity you chose for your Divine Magic feature determines the appearance of the spectral wings: eagle wings for good or law, bat wings for evil or chaos, and dragonfly wings for neutrality.", 14); -- 391
INSERT INTO archetype_features VALUES (NULL, 75, "Unearthly Recovery", "Once per long rest, bonus action if less than half health, regain hit points equal to half max health.", "You gain the ability to overcome grievous injuries. As a bonus action when you have fewer than half of your hit points remaining, you can regain a number of hit points equal to half your hit point maximum.<br>Once you use this feature, you can't use it again until you finish a long rest.", 18); -- 392
INSERT INTO archetype_features VALUES (NULL, 76, "Eyes of the Dark", "Darkvision to 120ft.<br>At 3rd level learn <i>Darkness</i> spell. Can cast with 2 sorcery point or a spell slot, if points you can see through it.", "You have darkvision with a range of 120 feet.<br>When you reach 3rd level in this class, you learn the darkness spell, which doesn't count against your number of sorcerer spells known. In addition, you can cast it by spending 2 sorcery points or by expending a spell slot. If you cast it with sorcery points, you can see through the darkness created by the spell.", 1); -- 393
INSERT INTO archetype_features VALUES (NULL, 76, "Strength of the Grave", "When you reach 0hp (minus radiant or crit) make a CHA save (DC = 5 + dmg), on success drop to 1 and can't use again until after long rest.", "Your existence in a twilight state between life and death makes you difficult to defeat. When damage reduces you to 0 hit points, you can make a Charisma saving throw (DC 5 + the damage taken). On a success, you instead drop to 1 hit point. You can't use this feature if you are reduced to 0 hit points by radiant damage or by a critical hit.<br>After the saving throw succeeds, you can't use this feature again until you finish a long rest.", 1); -- 394
INSERT INTO archetype_features VALUES (NULL, 76, "Hound of Ill Omen", "Bonus action, spend 3 sorcery points, summon hound to target one creature within 120 ft. Hound uses dire wolf stats and:<br><ul><li>Size = Medium, type = monstrosity.</li><li>Temporary hit points = half sorcerer level.</li><li>Can move through creatures and objects as if difficult terrain. Takes 5 force damage if ends turn inside an object.</li><li>Hound automatically knows its location.</li></ul><br>Has own initiative, can opportunity attack target, if hound within 5ft of target has disadvantage on spell saves.", "You gain the ability to call forth a howling creature of darkness to harass your foes. As a bonus action, you can spend 3 sorcery points to magically summon a hound of ill omen to target one creature you can see within 120 feet of you. The hound uses the dire wolf's statistics (see the Monster Manual or appendix C in the Player's Handbook), with the following changes:<br><ul><li>The hound is size Medium, not Large, and it counts as a monstrosity, not a beast.</li><li>It appears with a number of temporary hit points equal to half your sorcerer level.</li><li>It can move through other creatures and objects as if they were difficult terrain. The hound takes 5 force damage if it ends its turn inside an object.</li><li>At the start of its turn, the hound automatically knows its target's location. If the target was hidden, it is no longer hidden from the hound.</li></ul><br>The hound appears in an unoccupied space of your choice within 30 feet of the target. Roll initiative for the hound. On its turn, it can move only toward its target by the most direct route, and it can use its action only to attack its target. The hound can make opportunity attacks, but only against its target. Additionally, while the hound is within 5 feet of the target, the target has disadvantage on saving throws against any spell you cast. The hound disappears if it is reduced to 0 hit points, if its target is reduced to 0 hit points, or after 5 minutes.", 6); -- 395
INSERT INTO archetype_features VALUES (NULL, 76, "Shadow Walk", "In dim light or darkness, bonus action to teleport up to 120ft into dim light or darkness.", "You gain the ability to step from one shadow into another. When you are in dim light or darkness, as a bonus action, you can magically teleport up to 120 feet to an unoccupied space you can see that is also in dim light or darkness.", 14); -- 396
INSERT INTO archetype_features VALUES (NULL, 76, "Umbral Form", "Bonus action, spend 6 sorcery points, for 1 minute (or incapacitated, death, or dismissed) resistance to all damage except force and radiant, can move through creatures and objects as if difficult terrain.", "You can spend 6 sorcery points as a bonus action to magically transform yourself into a shadowy form. In this form, you have resistance to all damage except force and radiant damage, and you can move through other creatures and objects as if they were difficult terrain. You take 5 force damage if you end your turn inside an object.<br>You remain in this form for 1 minute. It ends early if you are incapacitated, if you die, or if you dismiss it as a bonus action.", 18); -- 397
INSERT INTO archetype_features VALUES (NULL, 77, "Wind Speaker", "You can speak, read, and write Primordial. Can understand and be understood by Aquan, Auran, Ignan, and Terran.", "The arcane magic you command is infused with elemental air. You can speak, read, and write Primordial. Knowing this language allows you to understand and be understood by those who speak its dialects: Aquan, Auran, Ignan, and Terran.", 1); -- 398
INSERT INTO archetype_features VALUES (NULL, 77, "Tempestuous Magic", "Bonus action immediately before or after you cast a 1st lvl+ spell to fly upto 10ft without provoking opportunity attacks.", "You can use a bonus action on your turn to cause whirling gusts of elemental air to briefly surround you, immediately before or after you cast a spell of 1st level or higher. Doing so allows you to fly up to 10 feet without provoking opportunity attacks.", 1); -- 399
INSERT INTO archetype_features VALUES (NULL, 77, "Heart of the Storm", "Resistance to lightning and thunder damage.<br>When casting spell 1st lvl+ that deals lightning or thunder, creatures of your choice within 10ft take lightning or thunder damage = half sorcerer level.", "You gain resistance to lightning and thunder damage. In addition, whenever you start casting a spell of 1st level or higher that deals lightning or thunder damage, stormy magic erupts from you. This eruption causes creatures of your choice that you can see within 10 feet of you to take lightning or thunder damage (choose each time this ability activates) equal to half your sorcerer level.", 6); -- 400
INSERT INTO archetype_features VALUES (NULL, 77, "Storm Guide", "Action cause rain to stop around you in 20ft radius sphere.<br>Bonus action each round, change wind direction within 100ft radius sphere.", "You gain the ability to subtly control the weather around you.<br>If it is raining, you can use an action to cause the rain to stop falling in a 20-foot-radius sphere centered on you. You can end this effect as a bonus action.<br>If it is windy, you can use a bonus action each round to choose the direction that the wind blows in a 100-foot-radius sphere centered on you. The wind blows in that direction until the end of your next turn. This feature doesn't alter the speed of the wind.", 6); -- 401
INSERT INTO archetype_features VALUES (NULL, 77, "Storm's Fury", "Reaction, when hit by melee attack, deal lightning dmg = sorcerer level. Target makes STR save, on fail pushed 20ft away.", "When you are hit by a melee attack, you can use your reaction to deal lightning damage to the attacker. The damage equals your sorcerer level. The attacker must also make a Strength saving throw against your sorcerer spell save DC. On a failed save, the attacker is pushed in a straight line up to 20 feet away from you.", 14); -- 402
INSERT INTO archetype_features VALUES (NULL, 77, "Wind Soul", "Immune to lightning and thunder damage.<br>Flying speed of 60ft. Choose to reduce by 30ft (until rest), for 3 + CHA mod creatures to gain flying speed of 30ft for 1 hour.", "You gain immunity to lightning and thunder damage.<br>You also gain a magical flying speed of 60 feet. As an action, you can reduce your flying speed to 30 feet for 1 hour and choose a number of creatures within 30 feet of you equal to 3 + your Charisma modifier. The chosen creatures gain a magical flying speed of 30 feet for 1 hour. Once you reduce your flying speed in this way, you can't do so again until you finish a short or long rest.", 18); -- 403
INSERT INTO archetype_features VALUES (NULL, 78, "Psionic Spells", "You learn additional spells at 1st, 3rd, 5th, 7th, and 9th level. These don't count against the number of sorcerer spells you know.", "You learn additional spells when you reach certain levels in this class, as shown on the Psionic Spells table. Each of these spells counts as a sorcerer spell for you, but it doesn't count against the number of sorcerer spells you know.<br><br>Whenever you gain a sorcerer level, you can replace one spell you gained from this feature with another spell of the same level. The new spell must be a divination or an enchantment spell from the sorcerer, warlock, or wizard spell list.<br><br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Level</th><th>Spells</th></tr></thead><tbody><tr><td>1st</td><td>Arms of Hadar, Dissonant Whispers, Mind Sliver</td></tr><tr><td>3rd</td><td>Calm Emotions, Detect Thoughts</td></tr><tr><td>5th</td><td>Hunger of Hadar, Sending</td></tr><tr><td>7th</td><td>Evard's Black Tentacles, Summon Aberration</td></tr><tr><td>9th</td><td>Rary's Telepathic Bond, Telekinesis</td></tr></tbody></table></div>", 1); -- 404
INSERT INTO archetype_features VALUES (NULL, 78, "Telepathic Speech", "Bonus action choose creature within 30ft, can telepathically communicate while within miles equal to Charisma modifier, for minutes equal to sorcerer level.", "You can form a telepathic connection between your mind and the mind of another. As a bonus action, choose one creature you can see within 30 feet of you. You and the chosen creature can speak telepathically with each other while the two of you are within a number of miles of each other equal to your Charisma modifier (minimum of 1 mile). To understand each other, you each must speak mentally in a language the other knows.<br><br>The telepathic connection lasts for a number of minutes equal to your sorcerer level. It ends early if you are incapacitated or die or if you use this ability to form a connection with a different creature.", 1); -- 405
INSERT INTO archetype_features VALUES (NULL, 78, "Psionic Sorcery", "Can cast spell using number of sorcery points equal to spell level. Doesn't requite verbal, somatic or material components (unless consumed).", "When you cast any spell of 1 st level or higher from your Psionic Spells feature, you can cast it by expending a spell sl()t as normal or by spending a number of sorcery points equal to the spell's level. If you cast the spell using sorcery points, it requires no verbal or somatic components, and it requires no material components, unless they are consumed by the spell.", 6); -- 406
INSERT INTO archetype_features VALUES (NULL, 78, "Psychic Defenses", "Gain resistance to psychic damage.<be>Advantage on saves against being charmed or frightened.", "You gain resistance to psychic damage, and you have advantage on saving throws against being charmed or frightened.", 6); -- 407
INSERT INTO archetype_features VALUES (NULL, 78, "Revelation in Flesh", "Bonus action, spend 1 or more sorcery points to transform for 10 mins. For each point you spend choose one of the following:<br><ul><li>See invisible creatures within 60ft (not behind total cover).</li><li>Flying speed equal to walking speed and can hover.</li><li>Gain swimming speed equal to twice walking speed and can breath underwater.</li><li>Move through 1 inch spaces, spend 5ft movement to escape non-magical restraints and grapples.</li></ul>", "You can unleash the aberrant truth hidden within yourself. As a bonus action, you can spend 1 or more sorcery points to magically transform your body for 10 minutes. For each sorcery point you spend, you can gain one of the following benefits of your choice, the effects of which last until the transformation ends:<br><br><ul><li>You can see any invisible creature within 60 feet of you, provided it isn't behind total cover. Your eyes also turn black or become writhing sensory tendrils.</li><li>You gain a flying speed equal to your walking speed, and you can hover. As you fly, your skin glistens with mucus or shines with an otherworldly light.</li><li>You gain a swimming speed equal to twice your walking speed, and you can breathe underwater. Moreover, gills grow from your neck or fan out from behind your ears, your fingers become webbed, or you grow writhing cilia that extend through your clothing.</li><li>Your body, along with any equipment you are wearing or carrying, becomes slimy and pliable. You can move through any space as narrow as 1 inch without squeezing, and you can spend 5 feet of movement to escape from nonmagical restraints or being grappled.</li></ul>", 14); -- 408
INSERT INTO archetype_features VALUES (NULL, 78, "Warping Implosion", "Once per long rest (or by spending 5 sorcery points), action to teleport to space you see within 120ft, each creature within 30ft of where you teleported from makes a Strength save, on fail taking 3d10 force damage and pulled towards where you teleported. On success takes half damage and is not pulled.", "You can unleash yqur aberrant power as a space-warping anomaly. As an action, you can teleport to an unoccupied space you can see within 120 feet of you. Immediately after you disappear, each creature within 30 feet of the space you left must make a Strength saving throw. On a failed save, a creature takes 3d10 force damage and is pulled straight toward the space you left, ending in an unoccupied space as close to your former space as possible. On a successful save, the creature takes half as much damage and isn't pulled.<br><br>Once you use this feature, you can't do so again until you finish a long rest, unless you spend 5 sorcery points to use it again.", 18); -- 409
INSERT INTO archetype_features VALUES (NULL, 79, "Clockwork Magic", "You learn additional spells at 1st, 3rd, 5th, 7th, and 9th level. These don't count against the number of sorcerer spells you know.", "You learn additional spells when you reach certain levels in this class, as shown on the Clockwork Spells table. Each of these spells counts as a sorcerer spell for you, but it doesn't count against the number of sorcerer spells you know.<br><br>Whenever you gain a sorcerer level, you can replace one spell you gained from this feature with another spell of the same level. The new spell must be an abjuration or a transmutation spell from the sorcerer, warlock, or wizard spell list.<br><br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Level</th><th>Spells</th></tr></thead><tbody><tr><td>1st</td><td>Alarm, Protection from Evil and Good</td></tr><tr><td>3rd</td><td>Aid, Lesser Restoration</td></tr><tr><td>5th</td><td>Dispel Magic, Protection from Energy</td></tr><tr><td>7th</td><td>Freedom of Movement, Summon Construct</td></tr><tr><td>9th</td><td>Greater Restoration, Wall of Force</td></tr></tbody></table></div><br><br>In addition, consult the Manifestations of Order table and choose or randomly determine a way your connection to order manifests while you are casting any of your sorcerer spells.", 1); -- 410
INSERT INTO archetype_features VALUES (NULL, 79, "Restore Balance", "When a creature within 60ft rolls with advantage or disadvantage, use reaction to force a normal roll. You can do this a number of times equal to your proficiency bonus, resets on long rest.", "Your connection to the plane of absolute order allows you to equalize chaotic moments. When a creature you can see within 60 feet of you is about to roll a d20 with advantage or disadvantage, you can use your reaction to prevent the roll from being affected by advantage and disadvantage.<br><br>You can use this feature a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.", 1); -- 411
INSERT INTO archetype_features VALUES (NULL, 79, "Bastion of Law", "Action to expend 1 to 5 sorcery points to ward a creature (or yourself) within 30ft until you finish a long rest or use the feature again. When the warded creature takes damage expend wards and roll a d8 for each one to reduce the damage by the total rolled.", "You can tap into the grand equation of existence to imbue a creature with a shimmering shield of order. As an action, you can expend 1 to 5 sorcery points to create a magical ward around yourself or another creature you can see within 30 feet of you. The ward lasts until you finish a long rest or until you use this feature again.<br><br>The ward is represented by a number of d8s equal to the number of sorcery points spent to create it. When the warded creature takes damage, it can expend a number of those dice, roll them, and reduce the damage taken by the total rolled on those dice.", 6); -- 412
INSERT INTO archetype_features VALUES (NULL, 79, "Trance of Order", "Once per long rest (or by expending 5 sorcery points), bonus action and for 1 min: attack rolls against you can't benefit from advantage, on your attack rolls, checks, and saves any roll below a 10 is a 10.", "You gain the ability to align your consciousness to the endless calculations of Mechanus. As a bonus action, you can enter this state for 1 minute. For the duration, attack rolls against you can't benefit from advantage, and whenever you make an attack roll, an ability check, or a saving throw, you can treat a roll of 9 or lower on the d20 as a 10.<br><br>Once you use this bonus action, you can't use it again until you finish a long rest, unless you spend 5 sorcery points to use it again.", 14); -- 413
INSERT INTO archetype_features VALUES (NULL, 79, "Clockwork Cavalcade", "Once per long rest (or by expending 7 sorcery points), action to summon spirits in 30ft cube around you. The following happens:<br><ul><li>Restore up to 100 HP, divided as you wish.</li><li>Damaged inside the cube are repaired.</li><li>Every spell of 6th level or lower ends on creatures and objects of your choice in the cube.</li></ul>", "You summon spirits of order to expunge disorder around you. As an action, you summon the spirits in a 30-foot cube originating from you. The spirits look like modrons or other constructs of your choice. The spirits are intangible and invulnerable, and they create the following effects within the cube before vanishing:<br><br><ul><li>The spirits restore up to 100 hit points, divided as you choose among any number of creatures of your choice in the cube.</li><li>Any damaged objects entirely in the cube are repaired instantly.</li><li>Every spell of 6th level or lower ends on creatures and objects of your choice in the cube.</li></ul><br><br>Once you use this action, you can't use it again until you finish a long rest, unless you spend 7 sorcery points to use it again.", 18); -- 414
INSERT INTO class_features VALUES (NULL, 11, "Otherworldly Patron", NULL, "You have struck a bargain with an otherworldly being of your choice. Your choice grants you features at 1st level and again at 6th, 10th, and 14th level.", 1, 0); -- 127
INSERT INTO class_sub_features VALUES (NULL, 127, "The Archfey", NULL, "Your patron is a lord or lady of the fey, a creature of legend who holds secrets that were forgotten before the mortal races were born. This being's motivations are often inscrutable, and sometimes whimsical, and might involve a striving for greater magical power or the settling of age-old grudges. Beings of this sort include the Prince of Frost; the Queen of Air and Darkness, ruler of the Gloaming Court; Titania of the Summer Court; her consort Oberon, the Green Lord; Hyrsam, the Prince of Fools; and ancient hags.", 0); -- 137
INSERT INTO class_sub_features VALUES (NULL, 127, "The Fiend", NULL, "You have made a pact with a fiend from the lower planes of existence, a being whose aims are evil, even if you strive against those aims. Such beings desire the corruption or destruction of all things, ultimately including you. Fiends powerful enough to forge a pact include demon lords such as Demogorgon, Orcus, Fraz'Urb-luu, and Baphomet; archdevils such as Asmodeus, Dispater, Mephistopheles, and Belial; pit fiends and balors that are especially mighty; and ultroloths and other lords of the yugoloths.", 0); -- 138
INSERT INTO class_sub_features VALUES (NULL, 127, "The Great Old One", NULL, "Your patron is a mysterious entity whose nature is utterly foreign to the fabric of reality. It might come from the Far Realm, the space beyond reality, or it could be one of the elder gods known only in legends. Its motives are incomprehensible to mortals, and its knowledge so immense and ancient that even the greatest libraries pale in comparison to the vast secrets it holds. The Great Old One might be unaware of your existence or entirely indifferent to you, but the secrets you have learned allow you to draw your magic from it.<br><br>Entities of this type include Ghaunadar, called That Which Lurks; Tharizdun, the Chained God; Dendar, the Night Serpent; Zargon, the Returner; Great Cthulhu; and other unfathomable beings.", 0); -- 139
INSERT INTO class_sub_features VALUES (NULL, 127, "The Celestial", NULL, "Your patron is a powerful being of the Upper Planes. You have bound yourself to an ancient empyrean, solar, ki-rin, unicorn, or other entity that resides in the planes of everlasting bliss. Your pact with that being allows you to experience the barest touch of the holy light that illuminates the multiverse.<br><br>Being connected to such power can cause changes in your behavior and beliefs. You might find yourself driven to annihilate the undead, to defeat fiends, and to protect the innocent. At times, your heart might also be filled with a longing for the celestial realm of your patron, and a desire to wander that paradise for the rest of your days. But you know that your mission is among mortals for now, and that your pact binds you to bring light to the dark places of the world.", 0); -- 140
INSERT INTO class_sub_features VALUES (NULL, 127, "The Hexblade", NULL, "You have made your pact with a mysterious entity from the Shadowfell — a force that manifests in sentient magic weapons carved from the stuff of shadow. The mighty sword Blackrazor is the most notable of these weapons, which have been spread across the multiverse over the ages. The shadowy force behind these weapons can offer power to warlocks who form pacts with it. Many hexblade warlocks create weapons that emulate those formed in the Shadowfell. Others forgo such arms, content to weave the dark magic of that plane into their spellcasting.<br><br>Because the Raven Queen is known to have forged the first of these weapons, many sages speculate that she and the force are one and that the weapons, along with hexblade warlocks, are tools she uses to manipulate events on the Material Plane to her inscrutable ends.", 0); -- 141
INSERT INTO class_sub_features VALUES (NULL, 127, "The Fathomless", NULL, "You have plunged into a pact with the deeps. An entity of the ocean, the Elemental Plane of Water, or another otherworldly sea now allows you to draw on its thalassic power. Is it merely using you to learn about terrestrial realms, or does it want you to open cosmic floodgates and drown the world?<br><br>Perhaps you were born into a generational cult that venerates the Fathomless and its spawn. Or you might have been shipwrecked and on the brink of drowning when your patron's grasp offered you a chance at life. Whatever the reason for your pact, the sea and its unknown depths call to you.<br><br>Entities of the deep that might empower a warlock include krakens, ancient water elementals, godlike hallucinations dreamed into being by kuo-toa, merfolk demigods, and sea hag covens.", 0); -- 142
INSERT INTO class_sub_features VALUES (NULL, 127, "The Genie", NULL, "You have made a pact with one of the rarest kinds of genie, a noble genie. Such entities rule vast fiefs on the Elemental Planes and have great influence over lesser genies and elemental creatures. Noble genies are varied in their motivations, but most are arrogant and wield power that rivals that of lesser deities. They delight in turn,ing the table on mortals, who often bind genies into servitude, and readily enter into pacts that expand their reach.<br><br>You choose your patron's kind or determine it randomly, using the Genie Kind table.", 0); -- 143
INSERT INTO class_features VALUES (NULL, 11, "Pact Magic", "Spell save DC = 8 + prof. + CHA<br>Spell attack modifier = prof. + CHA", "Your arcane research and the magic bestowed on you by your patron have given you facility with spells. See chapter 10 for the general rules of spellcasting and chapter 11 for the warlock spell list.<br><br><b>Cantrips</b><br>You know two cantrips of your choice from the warlock spell list. You learn additional warlock cantrips of your choice at higher levels, as shown in the Cantrips Known column of the Warlock table.<br><br><b>Spell Slots</b><br>The Warlock table shows how many spell slots you have. The table also shows what the level of those slots is; all of your spell slots are the same level. To cast one of your warlock spells of 1st level or higher, you must expend a spell slot. You regain all expended spell slots when you finish a short or long rest.<br>For example, when you are 5th level, you have two 3rd-level spell slots. To cast the 1st-level spell thunderwave, you must spend one of those slots, and you cast it as a 3rd-level spell.<br><br><b>Spells Known of 1st level and Higher</b><br>At 1st level, you know two 1st-level spells of your choice from the warlock spell list.<br>The Spells Known column of the Warlock table shows when you learn more warlock spells of your choice of 1st level and higher. A spell you choose must be of a level no higher than what's shown in the table's Slot level column for your level. When you reach 6th level, for example, you learn a new warlock spell, which can be 1st, 2nd, or 3rd level.<br>Additionally, when you gain a level in this class, you can choose one of the warlock spells you know and replace it with another spell from the warlock spell list, which also must be of a level for which you have spell slots.<br><br><b>Spellcasting Ability</b><br>Charisma is your spellcasting ability for your warlock spells, so you use your Charisma whenever a spell refers to your spellcasting ability. In addition, you use your Charisma modifier when setting the saving throw DC for a warlock spell you cast and when making an attack roll with one.<br>Spell save DC = 8 + proficiency bonus + your Charisma modifier<br>Spell attack modifier = your proficiency bonus + your Charisma modifier<br><br><b>Spellcasting Focus</b><br>You can use an arcane focus (found in chapter 5) as a spellcasting focus for your warlock spells.", 1, 0); -- 128
INSERT INTO class_features VALUES (NULL, 11, "Eldritch Invocations", "In your study of occult lore, you have unearthed eldritch invocations, fragments of forbidden knowledge that imbue you with an abiding magical ability.", "In your study of occult lore, you have unearthed eldritch invocations, fragments of forbidden knowledge that imbue you with an abiding magical ability.<br>At 2nd level, you gain two eldritch invocations of your choice. When you gain certain warlock levels, you gain additional invocations of your choice, as shown in the Invocations Known column of the Warlock table.<br>Additionally, when you gain a level in this class, you can choose one of the invocations you know and replace it with another invocation that you could learn at that level.", 2, 0); -- 129
INSERT INTO class_sub_features VALUES (NULL, 129, "Agonizing Blast", "Eldritch blast cantrip", "When you case Eldritch blast, add your Charisma modifier to the damage it deals on a hit.", 0); -- 144
INSERT INTO class_sub_features VALUES (NULL, 129, "Armour of Shadows", NULL, "You can cast mage armour on yourself at will, without extending a spell slot or material component.", 0); -- 145
INSERT INTO class_sub_features VALUES (NULL, 129, "Ascendant Step", "9th level", "You can cast levitate on yourself at will, without expending a spell slot or material components.", 0); -- 146
INSERT INTO class_sub_features VALUES (NULL, 129, "Aspect of the Moon", "Pack of the Tome feature", "You no longer need to sleep and can't be forced to sleep by any means. To gain the benefits of a long rest, you can spend all 8 hours doing light activity, such as reading you Book of Shadows and keeping watch.", 0); -- 147
INSERT INTO class_sub_features VALUES (NULL, 129, "Beast Speech", NULL, "You can cast speak with animals at will, without expending a spell slot.", 0); -- 148
INSERT INTO class_sub_features VALUES (NULL, 129, "Beguiling Influence", NULL, "You gain proficiency in the Deception and Persuasion skills.", 0); -- 149
INSERT INTO class_sub_features VALUES (NULL, 129, "Bewitching Whispers", "7th level", "You can cast compulsion once using a warlock spell slot. You can't do so again until you finish a long rest.", 0); -- 150
INSERT INTO class_sub_features VALUES (NULL, 129, "Book of Ancient Secrets", "Pact of the Tome feature", "You can now inscribe magical runes in your Book of Shadows. Choose two 1st-level spells that have the ritual tag from any class's spell list (the two needn't be from the same list). The spells appear in the book but don't count against the number of spells you know.<br>With your Book of Shadows in hand, you can cast the chosen spells as rituals. You can't cast the spells except as rituals, unless you've learned them by some other means. You can also cast a warlock spell you know as a ritual if it has the ritual tag.<br>On your adventures, you can add other ritual spells to your Book of Shadows. When you find such a spell, you can add it to the book if the spell's level is equal to or less than half your warlock level (rounded up) and if you can spare the time to transcribe the spell. For each level of the spell, the transcription process takes 2 hours and costs 50gp for the rare inks needed to inscribe it.", 0); -- 151
INSERT INTO class_sub_features VALUES (NULL, 129, "Chains of Carceri", "15th level, Pact of the Chain feature", "You can cast hold monster at will - targeting a celestial, fiend, or elemental - without expending a spell slot or material components. You must finish a long rest before you can use this invocation on the same creature again.", 0); -- 152
INSERT INTO class_sub_features VALUES (NULL, 129, "Cloak of Flies", "5th level", "As a bonus action, you can surround yourself with a magical aura that looks like buzzing flies. The aura extends 5 feet from you in every direction, but not through total cover. It lasts until you're incapacitated or you dismiss it as a bonus action.<br>The aura grants you advantage on Charisma (Intimidation) checks but disadvantage on all other Charisma checks. Any other creature that starts its turn in the aura takes poison damage equal to your Charisma modifier (minimum of 0 damage).<br>Once you use this invocation, you can't use it again until you finish a short or long rest.", 0); -- 153
INSERT INTO class_sub_features VALUES (NULL, 129, "Devil's Sight", NULL, "You can see normally in darkness, both magical and nonmagical, to a distance of 120 feet.", 0); -- 154
INSERT INTO class_sub_features VALUES (NULL, 129, "Dreadful Word", "7th level", "You can cast confusion once using a warlock spell slot. You can't do so again until you finish a long rest.", 0); -- 155
INSERT INTO class_sub_features VALUES (NULL, 129, "Eldritch Sight", NULL, "You can cast detect magic at will, without expending a spell slot.", 0); -- 156
INSERT INTO class_sub_features VALUES (NULL, 129, "Eldritch Smite", "5th level, Pact of the Blade feature", "Once per turn when you hit a creature with your pact weapon, you can expend a warlock spell slot to deal an extra 1d8 force damage to the target, plus another 1d8 per level of the spell slot, and you knock the target prone if it is Huge or Smaller.", 0); -- 157
INSERT INTO class_sub_features VALUES (NULL, 129, "Eldritch Spear", "Eldritch blast cantrip", "When you cast eldritch blast, its range is 300 feet.", 0); -- 158
INSERT INTO class_sub_features VALUES (NULL, 129, "Eyes of the Rune Keeper", NULL, "You can read all writing.", 0); -- 159
INSERT INTO class_sub_features VALUES (NULL, 129, "Fiendish Vigor", NULL, "You can cast false life on yourself at will as a 1st-level spell, without expending a spell slot or material components.", 0); -- 160
INSERT INTO class_sub_features VALUES (NULL, 129, "Gaze of Two Minds", NULL, "You can use your action to touch a willing humanoid and perceive through its senses until the end of your next turn. As long as the creature is on the same plane of existence as you, you can use your action on subsequent turns to maintain this connection, extending the duration until the end of your net turn. While perceiving through the other creature's senses, you benefit from and special senses possessed by that creature, and you are blinded and deafened to your own surroundings.", 0); -- 161
INSERT INTO class_sub_features VALUES (NULL, 129, "Ghostly Gaze", "7th level", "As an action, you gain the ability to see through solid objects to a range of 30 feet. Within that range, you have darkvision if you don't already have it. This special sight lasts for 1 minute or until your concentration ends )as if you were concentrating on a spell). During that time, you perceive objects as ghostly, transparent images.<br>Once you use this invocation, you can't use it again until you finish a short or long rest.", 0); -- 162
INSERT INTO class_sub_features VALUES (NULL, 129, "Gift of the Depths", "5th level", "You can breath underwater, and you gain a swimming speed equal to your walking speed.<br>You can also cast water breathing once without expending a spell slot. You regain the ability to do so when you finish a long rest.", 0); -- 163
INSERT INTO class_sub_features VALUES (NULL, 129, "Gift of the Ever-Living Ones", "Pact of the Chain feature", "Whenever you regain hit points while your familiar is within 100 feet of you, treat any dice rolled to determine the hit points you regain as having rolled their maximum value for you.", 0); -- 164
INSERT INTO class_sub_features VALUES (NULL, 129, "Grasp of Hadar", "Eldritch Blast cantrip", "Once on each of your turns when you hit a creature with your eldritch blast, you can move that creature in a straight line 10 feet closer to you.", 0); -- 165
INSERT INTO class_sub_features VALUES (NULL, 129, "Improved Pact Weapon", "Pact of the Blade feature", "You can use any weapon you summon with you Pact of the Blade feature as a spellcasting focus for your warlock spells.<br>In addition, the weapon gains a +1 bonus to its attack and damage rolls, unless it is a magic weapon that already has a bonus to those rolls.<br>Finally, the weapon you conjure can be a shortbow, longbow, light crossbow, or heavy crossbow.", 0); -- 166
INSERT INTO class_sub_features VALUES (NULL, 129, "Lance of Lethargy", "Eldritch Blast cantrip", "Once on each of your turns when you hit a creature with your eldritch blast, you can reduce that creature's speed by 10 feet until the end of your next turn.", 0); -- 167
INSERT INTO class_sub_features VALUES (NULL, 129, "Lifedrinker", "12th level, Pact of the Blade feature", "When you hit a creature with your pact weapon, the creature takes extra necrotic damage equal to your Charisma modifier (minimum 1).", 0); -- 168
INSERT INTO class_sub_features VALUES (NULL, 129, "Maddening Hex", "5th level, Hex spell or a Warlock feature that curses", "As a bonus action, you cause a psychic disturbance around the target cursed by your hex spell or by a warlock feature of yours, such as Hexblade's Curse or Sign of Ill Omen. When you do so, you deal psychic damage to the cursed target and each creature of your choice that you can see within 5 feet of it. The psychic damages equals your Charisma modifier (minimum of 1 damage). To use this invocation, you much be able to see the cursed target, and it must be within 30 feet of you.", 0); -- 169
INSERT INTO class_sub_features VALUES (NULL, 129, "Mask of Many Faces", NULL, "You can cast disguise self at will, without expending a spell slot.", 0); -- 170
INSERT INTO class_sub_features VALUES (NULL, 129, "Master of Myriad Forms", "15th level", "You can cast alter self at will, without expending a spell slot.", 0); -- 171
INSERT INTO class_sub_features VALUES (NULL, 129, "Minions of Chaos", "9th level", "You can cast conjure elemental once using a warlock spell slot. You can't do so again until you finish a long rest.", 0); -- 172
INSERT INTO class_sub_features VALUES (NULL, 129, "Mire the Mind", "5th level", "You can cast slow once using a warlock spell slot. You can't do so again until you finish a long rest.", 0); -- 173
INSERT INTO class_sub_features VALUES (NULL, 129, "Misty Visions", NULL, "You can cast silent image at will, without expending a spell slot or material components.", 0); -- 174
INSERT INTO class_sub_features VALUES (NULL, 129, "One with Shadows", "5th level", "When you are in an area of dim light or darkness, you can use your action to become invisible until you move or take an action or a reaction.", 0); -- 175
INSERT INTO class_sub_features VALUES (NULL, 129, "Otherworldly Leap", "9th level", "You can cast jump on yourself at will, without expending a spell slot or material components.", 0); -- 176
INSERT INTO class_sub_features VALUES (NULL, 129, "Relentless Hex", "7th level, Hex spell or a Warlock feature that curses", "Your curse creates a temporary bond between you and your target. S a bonus action, you can magically teleport up to 30 feet to an unoccupied space you can see within 5 feet of the target cursed by your hex spell or by a warlock feature of yours, such as Hexblade's Curse or Sign of Ill Omen. To teleport in this way, you must be able to see the cursed target.", 0); -- 177
INSERT INTO class_sub_features VALUES (NULL, 129, "Repelling Blast", "Eldritch blast cantrip", "When you hit a creature with eldritch blast, you can push the creature up to 10 feet away from you in a straight line.", 0); -- 178
INSERT INTO class_sub_features VALUES (NULL, 129, "Sculptor of Flesh", "7th level", "You can cast polymorph once using a warlock spell slot. You can't do so again until you finish a long rest.", 0); -- 179
INSERT INTO class_sub_features VALUES (NULL, 129, "Shroud of Shadow", "15th level", "You can cast invisibility at will, without expending a spell slot.", 0); -- 180
INSERT INTO class_sub_features VALUES (NULL, 129, "Sign of Ill Omen", "5th level", "You can cast bestow curse once using a warlock spell slot. You can't do so again until you finish a long rest.", 0); -- 181
INSERT INTO class_sub_features VALUES (NULL, 129, "Thief of Five Fates", NULL, "You can cast bane once using a warlock spell slot. You can't do so again until you finish a long rest.", 0); -- 182
INSERT INTO class_sub_features VALUES (NULL, 129, "Thirsting Blade", "5th level, Pact of the Blade feature", "You can attack with your pact weapon twice, instead of once, whenever you take the Attack action on your turn.", 0); -- 183
INSERT INTO class_sub_features VALUES (NULL, 129, "Tomb of Levistus", "5th level", "As a reaction when you take damage, you can entomb yourself in ice, which melts away at the end of your next turn. You gain 10 temporary hitpoint per warlock level, which takes as much of the triggering damage as possible. Immediately after you take the damage, you gain invulnerability to fire damage, your speed is reduced to 0, and you are incapacitated. These effects, including any remaining temporary hit points, all end when the ice melts.<br>One you sue this invocation, you can't use it again until you finish a short or long rest.", 0); -- 184
INSERT INTO class_sub_features VALUES (NULL, 129, "Trickster's Escape", "7th level", "You can cast freedom of movement once on yourself without expending a spell slot. You regain the ability to do so when you finish a long rest.", 0); -- 185
INSERT INTO class_sub_features VALUES (NULL, 129, "Visions of Distant Realms", "15th level", "You can cast arcane eye at will, without expending a spell slot.", 0); -- 186
INSERT INTO class_sub_features VALUES (NULL, 129, "Voice of the Chain Master", "Pact of the Chain feature", "You can communicate telepathically with your familiar and perceive through your familiars senses as long as you are on the same plane of existence. Additionally, while perceiving through your familiars senses, you can also speak through your familiar in your own voice, even if your familiar is normally incapable of speech.", 0); -- 187
INSERT INTO class_sub_features VALUES (NULL, 129, "Whispers of the Grave", "9th level", "You can cast speak with dead at will, without expending a spell slot.", 0); -- 188
INSERT INTO class_sub_features VALUES (NULL, 129, "Witch Sight", "15th level", "You can see the true form of any shapechanger or creature concealed by illusion or transmutation magic while the creature is within 30 feet of you and within line of sight.", 0); -- 189
INSERT INTO class_sub_features VALUES (NULL, 129, "Bond of the Talisman", "12th level, Pact of the Talisman feature", "While someone else is wearing your talisman, you can use your action to teleport to the unoccupied space closest to them, provided the two of you are on the same plane of existence. The wearer of your talisman can do the same thing, using their action to teleport to you. The teleportation can be used a number of times equal to your proficiency bonus, and all expended uses are restored when you finish a long rest.", 1); -- 190
INSERT INTO class_sub_features VALUES (NULL, 129, "Eldritch Mind", NULL, "You have advantage on Constitution saving throws that you make to maintain your concentration on a spell.", 1); -- 191
INSERT INTO class_sub_features VALUES (NULL, 129, "Far Scribve", "5th level, Pact of the Tome feature", "A new page appears in your Book of Shadows. With your permission, a creature can use its action to write its name on that page, which can contain a number of names equal to your proficiency bonus.<br><br>You can cast the sending spell, targeting a creature whose name is on the page, without using a spell slot and without using material components. To do so, you must write the message on the page. The target hears the message in their mind, and if the target replies, their message appears on the page, rather than in your mind. The writing disappears after 1 minute.<br><br>As an action, you can magically erase a name on the page by touching it.", 1); -- 192
INSERT INTO class_sub_features VALUES (NULL, 129, "Gift of the Protectors", "9th level, Pact of the Tome feature", "A new page appears in your Book of Shadows. With your permission, a creature can use its action to write its name on that page, which can contain a number of names equal to your proficiency bonus.<br><br>When any creature whose name is on the page is reduced to 0 hit points but not killed outright, the creature magically drops to 1 hit point instead. Once this magic is triggered, no creature can benefit from it until you finish a long rest.<br><br>As an action, you can magically erase a name on the page by touching it.", 1); -- 193
INSERT INTO class_sub_features VALUES (NULL, 129, "Investment of the Chain Master", "Pact of the Chain feature", "When you cast find familiar, you infuse the summoned familiar with a measure of your eldritch power, granting the creature the following benefits:<br><br><ul><li>The familiar gains either a flying speed or a swimming speed (your choice) of 40 feet.</li><li>As a bonus action, you can command the familiar to take the Attack action.</li><li>The familiar's weapon attacks are considered magical for the purpose of overcoming immunity and resistance to nonmagical attacks.</li><li>If the familiar forces a creature to make a saving throw, it uses your spell save DC.</li><li>When the familiar takes damage, you can use your reaction to grant it resistance against that damage.</li></ul>", 1); -- 194
INSERT INTO class_sub_features VALUES (NULL, 129, "Protection of the Talisman", "7th level, Pact of the Talisman feature", "When the wearer of your talisman fails a saving throw, they can add a d4 to the roll, potentially turning the save into a success. This benefit can be used a number of times equal to your proficiency bonus, and all expended uses are restored when you finish a long rest.", 1); -- 195
INSERT INTO class_sub_features VALUES (NULL, 129, "Rebuke of the Talisman", "Pact of the Talisman feature", "When the wearer of your talisman is hit by an attacker you can see within 30 feet of you, you can use your reaction to deal psychic damage to the attacker equal to your proficiency bonus and push it up to 10 feet away from the talisman's wearer.", 1); -- 196
INSERT INTO class_sub_features VALUES (NULL, 129, "Undying Servitude", "5th level", "You can cast animate dead without using a spell slot. Once you do so, you can't cast it in this way again until you finish a long rest.", 1); -- 197
INSERT INTO class_features VALUES (NULL, 11, "Pact Boon", "Your otherworldly patron bestows a gift upon you for your loyal service.", "Your otherworldly patron bestows a gift upon you for your loyal service.", 3, 0); -- 130
INSERT INTO class_sub_features VALUES (NULL, 130, "Pact of the Chain", NULL, "You learn the find familiar spell and can cast it as a ritual. The spell doesn't count against your number of spells known.<br>When you cast the spell, you can choose one of the normal forms for your familiar or one of the following special forms: imp, pseudodragon, quasit, or sprite.<br>Additionally, when you take the Attack action, you can forgo one of your own attacks to allow your familiar to make one attack of its own.", 0); -- 198
INSERT INTO class_sub_features VALUES (NULL, 130, "Pact of the Blade", NULL, "You can use your action to create a pact weapon in your empty hand. You can choose the form that this melee weapon takes each time you create it (see chapter 5 for weapon options). You are proficient with it while you wield it. This weapon counts as magical for the purpose of overcoming resistance and immunity to nonmagical attacks and damage.<br>Your pact weapon disappears if it is more than 5 feet away from you for 1 minute or more. It also disappears if you use this feature again, if you dismiss the weapon (no action required), or if you die.<br>You can transform one magic weapon into your pact weapon by performing a special ritual while you hold the weapon. You perform the ritual over the course of 1 hour, which can be done during a short rest. You can then dismiss the weapon, shunting it into an extradimensional space, and it appears whenever you create your pact weapon thereafter. You can't affect an artifact or a sentient weapon in this way. The weapon ceases being your pact weapon if you die, if you perform the 1-hour ritual on a different weapon, or if you use a 1-hour ritual to break your bond to it. The weapon appears at your feet if it is in the extradimensional space when the bond breaks.", 0); -- 199
INSERT INTO class_sub_features VALUES (NULL, 130, "Pact of the Tomb", NULL, "Your patron gives you a grimoire called a Book of Shadows. When you gain this feature, choose three cantrips from any class's spell list. While the book is on your person, you can cast those cantrips at will. They don't count against your number of cantrips known.<br>If you lose your Book of Shadows, you can perform a 1-hour ceremony to receive a replacement from your patron. This ceremony can be performed during a short or long rest, and it destroys the previous book. The book turns to ash when you die.", 0); -- 200
INSERT INTO class_sub_features VALUES (NULL, 130, "Pact of the Talisman", NULL, "Your patron gives you an amulet, a talisman that can aid the wearer when the need is great. When the wearer fails an ability check, they can add a d4 to the roll, potentially turning the roll into a success. This benefit can be used a number of times equal to your proficiency bonus, and all expended uses are restored when you finish a long rest.<br><br>If you lose the talisman, you can perform a 1-hour ceremony to receive a replacement from your patron. This ceremony can be performed during a short or long rest, and it destroys the previous amulet. The talisman turns to ash when you die.", 1); -- 201
INSERT INTO class_features VALUES (NULL, 11, "Mystic Arcanum", "You gain warlock spells which can be cast without using a spell slot once per long rest.", "At 11th level, your patron bestows upon you a magical secret called an arcanum. Choose one 6th-level spell from the warlock spell list as this arcanum.<br>You can cast your arcanum spell once without expending a spell slot. You must finish a long rest before you can do so again.<br>At higher levels, you gain more warlock spells of your choice that can be cast in this way: one 7th-level spell at 13th level, one 8th-level spell at 15th level, and one 9th-level spell at 17th level. You regain all uses of your Mystic Arcanum when you finish a long rest.", 11, 0); -- 131
INSERT INTO class_features VALUES (NULL, 11, "Eldritch Master", "Once per long rest regain all spent spell slots.", "At 20th level, you can draw on your inner reserve of mystical power while entreating your patron to regain expended spell slots. You can spend 1 minute entreating your patron for aid to regain all your expended spell slots from your Pact Magic feature. Once you regain spell slots with this feature, you must finish a long rest before you can do so again.", 20, 0); -- 132
INSERT INTO class_features VALUES (NULL, 11, "Eldritch Versatility", "When you gain an ASI you can also: replace a cantrip, replace a Pact Boon feature, replace one Mystic Arcanum spell.", "Whenever you reach a level in this class that grants the Ability Score Improvement feature, you can do one of the following, representing a change of focus in your occult studies:<br><br><ul><li>Replace one cantrip you learned from this class's Pact Magic feature with another cantrip from the warlock spell list.</li><li>Replace the option you chose for the Pact Boon feature with one of that feature's other options.</li><li>If you're 12th level or higher, replace one spell from your Mystic Arcanum feature with another warlock spell of the same level.</li></ul><br><br>If this change makes you ineligible for any of your Eldritch Invocations, you must also replace them now, choosing invocations for which you qualify.", 4, 1); -- 133
INSERT INTO archetype_features VALUES (NULL, 80, "Expanded Spell List", "You can choose from an expanded spell list, they are considered warlock spells for you.", "The Archfey lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.<br><br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Spell Level</th><th>Spells</th></tr></thead><tbody><tr><td>1st</td><td>Faerie Fire, Sleep</td></tr><tr><td>2nd</td><td>Calm Emotions, Phantasmal Force</td></tr><tr><td>3rd</td><td>Blink, Plant Growth</td></tr><tr><td>4th</td><td>Dominate Beast, Greater Invisibility</td></tr><tr><td>5th</td><td>Dominate Person, Seeming</td></tr></tbody></table></div>", 1); -- 415
INSERT INTO archetype_features VALUES (NULL, 80, "Fey Presence", "Once per rest, action to cause each creature in 10ft cube Wis save, on fail charmed or frightened until end of your next turn.", "Your patron bestows upon you the ability to project the beguiling and fearsome presence of the fey. As an action, you can cause each creature in a 10-foot cube originating from you to make a Wisdom saving throw against your warlock spell save DC. The creatures that fail their saving throws are all charmed or frightened by you (your choice) until the end of your next turn.<br>Once you use this feature, you can't use it again until you finish a short or long rest.", 1); -- 416
INSERT INTO archetype_features VALUES (NULL, 80, "Misty Escape", "Once per rest, reaction when take damage turn invisible until start of your next turn or your attack and teleport upto 60ft.", "You can vanish in a puff of mist in response to harm. When you take damage, you can use your reaction to turn invisible and teleport up to 60 feet to an unoccupied space you can see. You remain invisible until the start of your next turn or until you attack or cast a spell.<br>Once you use this feature, you can't use it again until you finish a short or long rest.", 6); -- 417
INSERT INTO archetype_features VALUES (NULL, 80, "Beguiling Defenses", "Immune to charm, reaction to reverse a charm against you, Wis save or charmed for 1 min or until takes damage.", "Your patron teaches you how to turn the mind-affecting magic of your enemies against them. You are immune to being charmed, and when another creature attempts to charm you, you can use your reaction to attempt to turn the charm back on that creature. The creature must succeed on a Wisdom saving throw against your warlock spell save DC or be charmed by you for 1 minute or until the creature takes any damage.", 10); -- 418
INSERT INTO archetype_features VALUES (NULL, 80, "Dark Delirium", "Once per rest, Action against creature within 60ft, Wis save on fail charmed or frightened for 1 min while concentrating or until they take damage. Creature thinks it is lost in a misty realm.", "You can plunge a creature into an illusory realm. As an action, choose a creature that you can see within 60 feet of you. It must make a Wisdom saving throw against your warlock spell save DC. On a failed save, it is charmed or frightened by you (your choice) for 1 minute or until your concentration is broken (as if you are concentrating on a spell). This effect ends early if the creature takes any damage.<br>Until this illusion ends, the creature thinks it is lost in a misty realm, the appearance of which you choose. The creature can see and hear only itself, you, and the illusion.<br>You must finish a short or long rest before you can use this feature again.", 14); -- 419
INSERT INTO archetype_features VALUES (NULL, 81, "Expanded Spell List", "You can choose from an expanded spell list, they are considered warlock spells for you.", "The Fiend lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.<br><br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Spell Level</th><th>Spells</th></tr></thead><tbody><tr><td>1st</td><td>Burning Hands, Command</td></tr><tr><td>2nd</td><td>Blindness/Deafness, Scorching Ray</td></tr><tr><td>3rd</td><td>Fireball, Stinking Cloud</td></tr><tr><td>4th</td><td>Fire Shield, Wall of Fire</td></tr><tr><td>5th</td><td>Flame Strike, Hallow</td></tr></tbody></table></div", 1); -- 420
INSERT INTO archetype_features VALUES (NULL, 81, "Dark One's Blessing", "When you kill a creature, gain temp HP = Cha mod + Warlock level.", "When you reduce a hostile creature to 0 hit points, you gain temporary hit points equal to your Charisma modifier + your warlock level (minimum of 1).", 1); -- 421
INSERT INTO archetype_features VALUES (NULL, 81, "Dark One's Own Luck", "Once per rest, on ability check or saving throw add 1d10 before you know the result.", "You can call on your patron to alter fate in your favor. When you make an ability check or a saving throw, you can use this feature to add a d10 to your roll. You can do so after seeing the initial roll but before any of the roll's effects occur.<br>Once you use this feature, you can't use it again until you finish a short or long rest.", 6); -- 422
INSERT INTO archetype_features VALUES (NULL, 81, "Fiendish Resilience", "Once per rest, gain resistance to a damage type (exc silver and magical).", "You can choose one damage type when you finish a short or long rest. You gain resistance to that damage type until you choose a different one with this feature. Damage from magical weapons or silver weapons ignores this resistance.", 10); -- 423
INSERT INTO archetype_features VALUES (NULL, 81, "Hurl Through Hell", "Once per long rest, when you hit a creature they dissapear until the end of your next turn, non-fiends take 10d10 psychic damage.", "When you hit a creature with an attack, you can use this feature to instantly transport the target through the lower planes. The creature disappears and hurtles through a nightmare landscape.<br>At the end of your next turn, the target returns to the space it previously occupied, or the nearest unoccupied space. If the target is not a fiend, it takes 10d10 psychic damage as it reels from its horrific experience.<br>Once you use this feature, you can't use it again until you finish a long rest.", 14); -- 424
INSERT INTO archetype_features VALUES (NULL, 82, "Expanded Spell List", "You can choose from an expanded spell list, they are considered warlock spells for you.", "The Great Old One lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.<br><br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Spell Level</th><th>Spells</th></tr></thead><tbody><tr><td>1st</td><td>Dissonant Whispers, Tasha's Hideous Laughter</td></tr><tr><td>2nd</td><td>Detect Thoughts, Phantasmal Force</td></tr><tr><td>3rd</td><td>Clairvoyance, Sending</td></tr><tr><td>4th</td><td>Dominate Beast, Evard's Black Tentacles</td></tr><tr><td>5th</td><td>Dominate Person, Telekinesis</td></tr></tbody></table></div", 1); -- 425
INSERT INTO archetype_features VALUES (NULL, 82, "Awakened Mind", "You can speak telepathically to a creature within 30ft. that can understand at least one language.", "Starting at 1st level, your alien knowledge gives you the ability to touch the minds of other creatures. You can communicate telepathically with any creature you can see within 30 feet of you. You don't need to share a language with the creature for it to understand your telepathic utterances, but the creature must be able to understand at least one language.", 1); -- 426
INSERT INTO archetype_features VALUES (NULL, 82, "Entropic Ward", "Once per short rest use your reaction to make an attack against you have disadvantage.<br>If the attack misses your next attack roll against the creature has advantage.", "At 6th level, you learn to magically ward yourself against attack and to turn an enemy's failed strike into good luck for yourself. When a creature makes an attack roll against you, you can use your reaction to impose disadvantage on that roll. If the attack misses you, your next attack roll against the creature has advantage if you make it before the end of your next turn.<br>Once you use this feature, you can't use it again until you finish a short or long rest.", 6); -- 427
INSERT INTO archetype_features VALUES (NULL, 82, "Thought Shield", "Thoughts can't be read by telepathy or other means unless you allow it.<br>Resistance to psychic damage.<br>When a creature deals you psychic damage it take the same damage you do.", "Starting at 10th level, your thoughts can't be read by telepathy or other means unless you allow it. You also have resistance to psychic damage, and whenever a creature deals psychic damage to you, that creature takes the same amount of damage that you do.", 10); -- 428
INSERT INTO archetype_features VALUES (NULL, 82, "Create Thrall", "Use your action to charm an incapacitated humanoid.", "At 14th level, you gain the ability to infect a humanoid's mind with the alien magic of your patron. You can use your action to touch an incapacitated humanoid. That creature is then charmed by you until a remove curse spell is cast on it, the charmed condition is removed from it, or you use this feature again.<br>You can communicate telepathically with the charmed creature as long as the two of you are on the same plane of existence.", 14); -- 429
INSERT INTO archetype_features VALUES (NULL, 83, "Expanded Spell List", "You can choose from an expanded spell list, they are considered warlock spells for you.", "The Celestial lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.<br><br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Spell Level</th><th>Spells</th></tr></thead><tbody><tr><td>1st</td><td>Cure Wounds, Guiding Bolt</td></tr><tr><td>2nd</td><td>Flaming Sphere, Lesser Restoration</td></tr><tr><td>3rd</td><td>Daylight, Revivify</td></tr><tr><td>4th</td><td>Guardian of Faith, Wall of Fire</td></tr><tr><td>5th</td><td>Flame Strike, Greater Restoration</td></tr></tbody></table></div", 1); -- 430
INSERT INTO archetype_features VALUES (NULL, 83, "Bonus Cantrips", "Learn <i>light</i> and <i>sacred flame</i> cantrips.", "You learn the <i>light</i> and <i>sacred flame</i> cantrips. They count as warlock cantrips for you, but they don't count against your number of cantrips known.", 1); -- 431
INSERT INTO archetype_features VALUES (NULL, 83, "Healing Light", "Pool of 1 + Warlock level d6s, regain on long rest. Bonus action heal one creature within 60ft, upto Cha mod dice.", "You gain the ability to channel celestial energy to heal wounds. You have a pool of d6s that you spend to fuel this healing. The number of dice in the pool equals 1 + your warlock level.<br>As a bonus action, you can heal one creature you can see within 60 feet of you, spending dice from the pool. The maximum number of dice you can spend at once equals your Charisma modifier (minimum of one die). Roll the dice you spend, add them together, and restore a number of hit points equal to the total.<br>Your pool regains all expended dice when you finish a long rest.", 1); -- 432
INSERT INTO archetype_features VALUES (NULL, 83, "Radiant Soul", "Resistance to radiant damage, when you cast a spell that deal radiant or fire add your Cha mod against one of the targets.", "Your link to the Celestial allows you to serve as a conduit for radiant energy. You have resistance to radiant damage, and when you cast a spell that deals radiant or fire damage, you can add your Charisma modifier to one radiant or fire damage roll of that spell against one of its targets.", 6); -- 433
INSERT INTO archetype_features VALUES (NULL, 83, "Celestial Resilience", "Per short rest you gain temp HP = warlock lvl + Cha mod. 5 allies gain temp HP = Warlock lvl / 2 + Cha mod.", "You gain temporary hit points whenever you finish a short or long rest. These temporary hit points equal your warlock level + your Charisma modifier. Additionally, choose up to five creatures you can see at the end of the rest. Those creatures each gain temporary hit points equal to half your warlock level + your Charisma modifier.", 10); -- 434
INSERT INTO archetype_features VALUES (NULL, 83, "Searing Vengeance", "Once per long rest, when you make a death save regain HP = half max and stand up. Each creature you choose within 30ft takes 1d8 + Cha mod radiant damage and is blinded until end of the current turn.", "The radiant energy you channel allows you to resist death. When you have to make a death saving throw at the start of your turn, you can instead spring back to your feet with a burst of radiant energy. You regain hit points equal to half your hit point maximum, and then you stand up if you so choose. Each creature of your choice that is within 30 feet of you takes radiant damage equal to 2d8 + your Charisma modifier, and it is blinded until the end of the current turn.<br>Once you use this feature, you can't use it again until you finish a long rest.", 14); -- 435
INSERT INTO archetype_features VALUES (NULL, 84, "Expanded Spell List", "You can choose from an expanded spell list, they are considered warlock spells for you.", "The Hexblade lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.<br><br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Spell Level</th><th>Spells</th></tr></thead><tbody><tr><td>1st</td><td>Shield, Wrathful Smite</td></tr><tr><td>2nd</td><td>Blur, Branding Smite</td></tr><tr><td>3rd</td><td>Blink, Elemental Weapon</td></tr><tr><td>4th</td><td>Phantasmal Killer, Staggering Smite</td></tr><tr><td>5th</td><td>Banishing Smite, Cone of Cold</td></tr></tbody></table></div", 1); -- 436
INSERT INTO archetype_features VALUES (NULL, 84, "Hexblade's Curse", "Once per rest, bonus action choose creature you see in 30ft. Cursed for 1 min, benefits:<br><ul><li>Add proficiency bonus to dmg rolls.</li><li>Critical hit on a roll of 19 or 20.</li><li>If target dies, regain HP = Warlock level + Cha mod (min 1).</li></ul>", "You gain the ability to place a baleful curse on someone. As a bonus action, choose one creature you can see within 30 feet of you. The target is cursed for 1 minute. The curse ends early if the target dies, you die, or you are incapacitated. Until the curse ends, you gain the following benefits:<br><ul><li>You gain a bonus to damage rolls against the cursed target. The bonus equals your proficiency bonus.</li><li>Any attack roll you make against the cursed target is a critical hit on a roll of 19 or 20 on the d20.</li><li>If the cursed target dies, you regain hit points equal to your warlock level + your Charisma modifier (minimum of 1 hit point).</li></ul><br>You can't use this feature again until you finish a short or long rest.", 1); -- 437
INSERT INTO archetype_features VALUES (NULL, 84, "Hex Warrior", "Once per long rest, touch one single-handed weapon, when you attack use Cha mod rather than Str or Dex.<br>Prof with medium armour, shields and martial weapons.", "You acquire the training necessary to effectively arm yourself for battle. You gain proficiency with medium armor, shields, and martial weapons.<br>The influence of your patron also allows you to mystically channel your will through a particular weapon. Whenever you finish a long rest, you can touch one weapon that you are proficient with and that lacks the two-handed property. When you attack with that weapon, you can use your Charisma modifier, instead of Strength or Dexterity, for the attack and damage rolls. This benefit lasts until you finish a long rest. If you later gain the Pact of the Blade feature, this benefit extends to every pact weapon you conjure with that feature, no matter the weapon's type.", 1); -- 438
INSERT INTO archetype_features VALUES (NULL, 84, "Accursed Specter", "Once per long rest, raise specter from humanoid you kill, with temp HP = Warlock lvl / 2. Bonus to attk rolls = Cha mod.", "You can curse the soul of a person you slay, temporarily binding it to your service. When you slay a humanoid, you can cause its spirit to rise from its corpse as a specter, the statistics for which are in the Monster Manual. When the specter appears, it gains temporary hit points equal to half your warlock level. Roll initiative for the specter, which has its own turns. It obeys your verbal commands, and it gains a special bonus to its attack rolls equal to your Charisma modifier (minimum of +0).<br>The specter remains in your service until the end of your next long rest, at which point it vanishes to the afterlife.<br>Once you bind a specter with this feature, you can't use the feature again until you finish a long rest.", 6); -- 439
INSERT INTO archetype_features VALUES (NULL, 84, "Armor of Hexes", "If target of your Hexblade Curse hits you, reaction to roll d6, on 4 or higher attack misses you.", "Your hex grows more powerful. If the target cursed by your Hexblade's Curse hits you with an attack roll, you can use your reaction to roll a d6. On a 4 or higher, the attack instead misses you, regardless of its roll.", 10); -- 440
INSERT INTO archetype_features VALUES (NULL, 84, "Master of Hexes", "When your Cursed creature dies apply the curse to a different creature within 30ft, but you don't regain HP from killing the cursed creature.", "You can spread your Hexblade's Curse from a slain creature to another creature. When the creature cursed by your Hexblade's Curse dies, you can apply the curse to a different creature you can see within 30 feet of you, provided you aren't incapacitated. When you apply the curse in this way, you don't regain hit points from the death of the previously cursed creature.", 14); -- 441
INSERT INTO archetype_features VALUES (NULL, 85, "Expanded Spell List", "You can choose from an expanded spell list, they are considered warlock spells for you.", "The Fathomless lets you choose from an expanded list of spells when you learn a warlock spell. The following spells are added to the warlock spell list for you.<br><br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Spell Level</th><th>Spells</th></tr></thead><tbody><tr><td>1st</td><td>Create or Destroy Water, Thunderwave</td></tr><tr><td>2nd</td><td>Gust of Wind, Silence</td></tr><tr><td>3rd</td><td>Lightning Bolt, Sleet Storm</td></tr><tr><td>4th</td><td>Control Water, Summon Elemental</td></tr><tr><td>5th</td><td>Bigby's Hand, Cone of Cold</td></tr></tbody></table></div", 1); -- 442
INSERT INTO archetype_features VALUES (NULL, 85, "Tentacle of the Deeps", "Bonus action to create 10ft long tentacle at a point within 60ft for 1 min (or until you use this again). Make a melee spell attack (range 10ft), deals 1d8 cold damage (2d8 at 10th level). Bonus action to move tentable 30ft and repeat attack. Use this ability number of times equal to proficiency bonus, reset on long rest.", "You can magically summon a spectral tentacle that strikes at your foes. As a bonus action, you create a 10-foot-long tentacle at a point you can see within 60 feet of you. The tentacle lasts for 1 minute or until you use this feature to create another tentacle.<br><br>When you create the tentacle, you can make a melee spell attack against one creature within 10 feet of it. On a hit, the target takes ld8 cold damage, and its speed is reduced by 10 feet until the. start of your next turn. When you reach 10th level in this class, the damage increases to 2d8.<br><br>As a bonus action on your turn, you can move the tentacle up to 30 feet and repeat the attack.<br><br>You can summon the tentacle a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.", 1); -- 443
INSERT INTO archetype_features VALUES (NULL, 85, "Gift of the Sea", "You gain a swimming speed of 40 feet, and you can breathe underwater.", "You gain a swimming speed of 40 feet, and you can breathe underwater.", 1); -- 444
INSERT INTO archetype_features VALUES (NULL, 85, "Oceanic Soul", "Resistance to cold damage. When submerged can understand speech, and creature can understand you.", "You are now even more at home in the depths. You gain resistance to cold damage. In addition, when you are fully sub.merged, any creature that is also fully submerged can understand your speech, and you can understand theirs.", 6); -- 445
INSERT INTO archetype_features VALUES (NULL, 85, "Guardian Coil", "When you or a creature take damage within 10ft of your Tentacle use reaction to choose one creature and reduce damage by 1d8 (2d8 at 10th level).", "Your Tentacle of the Deeps can defend you and others, interposing itself between them and harm. When you or a creature you can see takes damage while within 10 feet of the tentacle, you can use your reaction to choose one of those creatures and reduce the damage to that creature by ld8. When you reach 10th level in this class, the damage reduced by the tentacle increases to 2d8.", 6); -- 446
INSERT INTO archetype_features VALUES (NULL, 85, "Grasping Tentacles", "You learn Evard's Black Tentacles, don't count against the number of spells known. Cast once per long rest without concentration.<br>After casting gain warlock level temporary HP.", "You learn the spell Evard's black tentacles. It counts as a warlock spell for you, but it doesn't count against the number of spells you know. You can also cast it once without a spell slot, and you regain the ability to do so when you finish a long rest.<br><br>Whenever you cast this spell, your patron's magic bolsters you, granting you a number of temporary hit points equal to your warlock level. Moreover, damage can't break your concentration on this spell.", 10); -- 447
INSERT INTO archetype_features VALUES (NULL, 85, "Fathomless Plunge", "Once per rest, action to teleport you and upto 5 creatures within 30ft to a body of water you've seen within 1 mile.", "You can magically open temporary conduits to watery destinations. As an action, you can teleport yourself and up to five other willing creatures that you can see within 30 feet of you. Amid a whirl of tentacles, you all vanish and then reappear up to 1 mile away in a body of water you've seen (pond size or larger) or within 30 feet of it, each of you appearing in an unoccupied space within 30 feet of the others.", 14); -- 448
INSERT INTO archetype_features VALUES (NULL, 86, "Expanded Spell List", "You can choose from an expanded spell list, they are considered warlock spells for you.", "The Genie lets you choose from an expanded list of spells when you learn a warlock spell. The Genie Expanded Spells table shows the genie spells that are added to the warlock spell list for you, along with the spells associated in the table with your patron's kind: dao, djinni, efreeti, or marid.<br><br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Spell Level</th><th>Genie Spells</th><th>Dao Spells</th><th>Djinni Spells</th><th>Efreeti Spells</th><th>Marid Spells</th></tr></thead><tbody><tr><td>1st</td><td>Detect Evil and Good</td><td>Sanctuary</td><td>Thunderwave</td><td>Burning Hands</td><td>Fog Cloud</td></tr><tr><td>2nd</td><td>Phantasmal Force</td><td>Spike Growth</td><td>Gust of Wind</td><td>Scorching Ray</td><td>Blur</td></tr><tr><td>3rd</td><td>Create Food and Water</td><td>Meld into Stone</td><td>Wind Wall</td><td>Fireball</td><td>Sleet Storm</td></tr><tr><td>4th</td><td>Phantasmal Killer</td><td>Stone Shape</td><td>Greater Invisibility</td><td>Fire Shield</td><td>Control Weather</td></tr><tr><td>5th</td><td>Creation</td><td>Wall of Stone</td><td>Seeming</td><td>Flame Strike</td><td>Cone of Cold</td></tr><tr><td>9th</td><td>Wish</td><td>-</td><td>-</td><td>-</td><td>-</td></tr></tbody></table></div", 1); -- 449
INSERT INTO archetype_features VALUES (NULL, 86, "Genie's Vessel", "Your patron gifts you a Tiny magic vessel which you can use as a spell focus. AC = Spell save DC, HP = Warlock level + proficiency bonus, immune to poison and psychic. Additionally while touching the vessel you can use it to:<br><strong>Bottled Respite.</strong> once per long rest use an action to enter vessel (hours upto twice proficiency bonus).<br><strong><br>Genie's Wrath.</strong> once per turn when you hit with an attack roll, deal extra damage equal to proficiency bonus (dao = bludgeoning, djinni = thunder, efreeti = fire, marid = cold).", "Your patron gifts you a magical vessel that grants you a measure of the genie's power. The vessel is a Tiny object, and you can use it as a spellcasting focus for your warlock spells. You decide what the object is, or you can determine what it is randomly by rolling on the Genie's Vessel table.<br><br>While you are touching the vessel, you can use it in the following ways:<br><br><strong>Bottled Respite.</strong> As an action, you can magically vanish and enter your vessel, which remains in the space you left. The interior of the vessel is an extradimensional space in the shape of a 20-foot-radius cylinder, 20 feet high, and resembles your vessel. The interior is appointed with cushions and low tables and is a comfortable temperature. While inside, you can hear the area around your vessel as if you were in its space. You can remain inside the vessel up to a number of hours equal to twice your proficiency bonus. You exit the vessel early if you use a bonus action to leave, if you die, or if the vessel is destroyed. When you exit the vessel, you appear in the unoccupied space closest to it. Any objects left in the vessel remain there until carried out, and if the vessel is destroyed, every object stored there harmlessly appears in the unoccupied spaces closest to the vessel's former space. Once you enter the vessel, you can't enter again until you finish a long rest.<br><strong><br>Genie's Wrath.</strong> Once during each of your turns when you hit with an attack roll, you can deal extra damage to the target equal to your proficiency bonus. The type of this damage is determined by your patron: bludgeoning (dao), thunder (djinni), fire (efreeti), or cold (marid).<br><br>The vessel's AC equals your spell save DC. Its hit points equal your warlock level plus your proficiency bonus, and it is immune to poison and psychic damage.<br><br>If the vessel is destroyed or you lose it, you can perform a 1-hour ceremony to receive a replacement from your patron. This ceremony can be performed during a short or long rest, and the previous vessel is destroyed if it still exists. The vessel vanishes in a flare of elemental power when you die.", 1); -- 450
INSERT INTO archetype_features VALUES (NULL, 86, "Elemental Gift", "Gain resistance to damage: bludgeoning (dao), thunder (djinni), fire (efreeti), or cold (marid).<br>Bonus action to gain 30ft fly speed for 10mins, use number of time equal to proficiency bonus, reset on long rest.", "You begin to take on characteristics of your patron's kind. You now have resistance to a damage type determined by your patron's kind: bludgeoning (dao), thunder (djinni), fire (efreeti), or cold (marid).<br><br>In addition, as a bonus action, you can give yourself a flying speed of 30 feet that lasts for 10 minutes, during which you can hover. You can use this bonus action a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.", 6); -- 451
INSERT INTO archetype_features VALUES (NULL, 86, "Sanctuary Vessel", "When you enter your vessel via Bottled Respite, choose upto five willing ceratures within 30ft to enter with you.<br>Anyone who spends 10 mins in the vessel gain the benefits of a short rest and can add their proficiency bonus to any hit die rolled.", "When you enter your Genie's Vessel via the Bottled Respite feature, you can now choose up to five willing creatures that you can see within 30 feet of you, and the chosen creatures are drawn into the vessel with you.<br><br>As a bonus action, you can eject any number of creatures from the vessel, and everyone is ejected if you leave or die or if the vessel is destroyed.<br><br>In addition, anyone (including you) who remains within the vessel for at least 10 minutes gains the benefit of finishing a short rest, and anyone can add your proficiency bonus to the number of hit points they regain if they spend any Hit Dice as part of a short rest there.", 10); -- 452
INSERT INTO archetype_features VALUES (NULL, 86, "Limited Wish", "Action to cast spell of 6th level or lower and cast time of 1 action. You don't need components, and it can come from any class.<br>After using this feature you can't do so again until you finish 1d4 long rests.", "You entreat your patron to grant you a small wish. As an action, you can speak your desire to your Genie's Vessel, requesting the effect of one spell that is 6th level or lower and has a casting time of 1 action. The spell can be from any class's spell list, and you don't need to meet the requirements in that spell, including costly components; the spell simply takes effect as part of this action.<br><br>Once you use this feature, you can't use it again until you finish ld4 long rests.", 14); -- 453
INSERT INTO class_features VALUES (NULL, 12, "Spellcasting", "Spell save DC = 8 + prof. + INT<br>Spell attack modifier = prof. + INT", "As a student of arcane magic, you have a spellbook containing spells that show the first glimmerings of your true power. See chapter 10 for the general rules of spellcasting and chapter 11 for the wizard spell list.<br><br><b>Cantrips</b><br>You know three cantrips of your choice from the Wizard spell list. You learn additional Wizard cantrips of your choice at higher levels, as shown in the Cantrips Known column of the Wizard table.<br><br><b>Spell Slots</b><br>At 1st level, you have a spellbook containing six 1st-level wizard spells of your choice.<br><br><b>Preparing and Casting Spells</b><br>The Wizard table shows how many spell slots you have to cast your spells of 1st level and higher. To cast one of these spells, you must expend a slot of the spell's level or higher. You regain all expended spell slots when you finish a long rest.<br>You prepare the list of wizard spells that are available for you to cast. To do so. choose a number of wizard spells from your spellbook equal to your Intelligence modifier + your wizard level (minimum of one spell). The spells must be of a level for which you have spell slots.<br>For example, if you're a 3rd-level wizard, you have four 1st-level and two 2nd-level spell slots. With an Intelligence of 16, your list of prepared spells can include six spells of 1st or 2nd level, in any combination, chosen from your spellbook. If you prepare the 1st-level spell magic missile, you can cast it using a 1st-level or a 2nd-level slot. Casting the spell doesn't remove it from your list of prepared spells.<br>You can change your list of prepared spells when you finish a long rest. Preparing a new list of wizard spells requires time spent studying your spellbook and memorizing the incantations and gestures you must make to cast the spell: at least 1 minute per spell level for each spell on your list.<br><br><b>Spellcasting Ability</b><br>Intelligence is your spellcasting ability for your Wizard spells, since you learn your spells through dedicated study and memorization. You use your Intelligence whenever a spell refers to your spellcasting ability. In addition, you use your Intelligence modifier when setting the saving throw DC for a Wizard spell you cast and when making an attack roll with one.<br>Spell save DC = 8 + proficiency bonus + your Intelligence modifier<br>Spell attack modifier = your proficiency bonus + your Intelligence modifier<br><br><b>Ritual Casting</b><br>You can cast any Wizard spell as a ritual if that spell has the ritual tag.<br><br><b>Spellcasting Focus</b><br>You can use an arcane focus (found in chapter 5) as a spellcasting focus for your Wizard spells.<br><br><b>Learning Spells of 1st level and Higher</b><br>Each time you gain a wizard level, you can add two wizard spells of your choice to your spellbook. Each of these spells must be of a level for which you have spell slots, as shown on the Wizard table. On your adventures, you might find other spells that you can add to your spellbook (see the “Your Spellbook” sidebar).", 1, 0); -- 134
INSERT INTO class_features VALUES (NULL, 12, "Arcane Recovery", "Once per rest recover spell slots (total level) = Wizard lvl / 2 rounded up, maximum 5th level.", "You have learned to regain some of your magical energy by studying your spellbook. Once per day when you finish a short rest, you can choose expended spell slots to recover. The spell slots can have a combined level that is equal to or less than half your wizard level (rounded up), and none of the slots can be 6th level or higher.<br>For example, if you're a 4th-level wizard, you can recover up to two levels worth of spell slots. You can recover either a 2nd-level spell slot or two 1st-level spell slots.", 1, 0); -- 135
INSERT INTO class_features VALUES (NULL, 12, "Arcane Tradition", NULL, "You choose an arcane tradition, shaping your practice of magic. Your choice grants you features at 2nd level and again at 6th, 10th, and 14th level.", 2, 0); -- 136
INSERT INTO class_sub_features VALUES (NULL, 136, "School of Abjuration", NULL, "The School of Abjuration emphasizes magic that blocks, banishes, or protects. Detractors of this school say that its tradition is about denial, negation rather than positive assertion. You understand, however, that ending harmful effects, protecting the weak, and banishing evil influences is anything but a philosophical void. It is a proud and respected vocation.<br><br>Called abjurers, members of this school are sought when baleful spirits require exorcism, when important locations must be guarded against magical spying, and when portals to other planes of existence must be closed.", 0); -- 202
INSERT INTO class_sub_features VALUES (NULL, 136, "School of Conjuration", NULL, "As a conjurer, you favor spells that produce objects and creatures out of thin air. You can conjure billowing clouds of killing fog or summon creatures from elsewhere to fight on your behalf. As your mastery grows, you learn spells of transportation and can teleport yourself across vast distances, even to other planes of existence, in an instant.", 0); -- 203
INSERT INTO class_sub_features VALUES (NULL, 136, "School of Divination", NULL, "The counsel of a diviner is sought by royalty and commoners alike, for all seek a clearer understanding of the past, present, and future. As a diviner, you strive to part the veils of space, time, and consciousness so that you can see clearly. You work to master spells of discernment, remote viewing, supernatural knowledge, and foresight.", 0); -- 204
INSERT INTO class_sub_features VALUES (NULL, 136, "School of Enchantment", NULL, "As a member of the School of Enchantment, you have honed your ability to magically entrance and beguile other people and monsters. Some enchanters are peacemakers who bewitch the violent to lay down their arms and charm the cruel into showing mercy. Others are tyrants who magically bind the unwilling into their service. Most enchanters fall somewhere in between.", 0); -- 205
INSERT INTO class_sub_features VALUES (NULL, 136, "School of Evocation", NULL, "You focus your study on magic that creates powerful elemental effects such as bitter cold, searing flame, rolling thunder, crackling lightning, and burning acid. Some evokers find employment in military forces, serving as artillery to blast enemy armies from afar. Others use their spectacular power to protect the weak, while some seek their own gain as bandits, adventurers, or aspiring tyrants.", 0); -- 206
INSERT INTO class_sub_features VALUES (NULL, 136, "School of Illusion", NULL, "You focus your studies on magic that dazzles the senses, befuddles the mind, and tricks even the wisest folk. Your magic is subtle, but the illusions crafted by your keen mind make the impossible seem real. Some illusionists — including many gnome wizards — are benign tricksters who use their spells to entertain. Others are more sinister masters of deception, using their illusions to frighten and fool others for their personal gain.", 0); -- 207
INSERT INTO class_sub_features VALUES (NULL, 136, "School of Necromancy", NULL, "The School of Necromancy explores the cosmic forces of life, death, and undeath. As you focus your studies in this tradition, you learn to manipulate the energy that animates all living things. As you progress, you learn to sap the life force from a creature as your magic destroys its body, transforming that vital energy into magical power you can manipulate.<br><br>Most people see necromancers as menacing, or even villainous, due to the close association with death. Not all necromancers are evil, but the forces they manipulate are considered taboo by many societies.", 0); -- 208
INSERT INTO class_sub_features VALUES (NULL, 136, "School of Transmutation", NULL, "You are a student of spells that modify energy and matter. To you, the world is not a fixed thing, but eminently mutable, and you delight in being an agent of change. You wield the raw stuff of creation and learn to alter both physical forms and mental qualities. Your magic gives you the tools to become a smith on reality's forge.<br><br>Some transmuters are tinkerers and pranksters, turning people into toads and transforming copper into silver for fun and occasional profit. Others pursue their magical studies with deadly seriousness, seeking the power of the gods to make and destroy worlds.", 0); -- 209
INSERT INTO class_sub_features VALUES (NULL, 136, "War Magic", NULL, "A variety of arcane colleges specialize in training wizards for war. The tradition of War Magic blends principles of evocation and abjuration, rather than specializing in either of those schools. It teaches techniques that empower a caster's spells, while also providing methods for wizards to bolster their own defenses.<br><br>Followers of this tradition are known as war mages. They see their magic as both a weapon and armor, a resource superior to any piece of steel. War mages act fast in battle, using their spells to seize tactical control of a situation. Their spells strike hard, while their defensive skills foil their opponents' attempts to counterattack. War mages are also adept at turning other spellcasters' magical energy against them.<br><br>In great battles, a war mage often works with evokers, abjurers, and other types of wizards. Evokers, in particular, sometimes tease war mages for splitting their attention between offense and defense. A war mage's typical response: “What good is being able to throw a mighty fireball if I die before I can cast it?”", 0); -- 210
INSERT INTO class_sub_features VALUES (NULL, 136, "Bladesinging", NULL, "Bladesingers master a tradition of wizardry that incorporates swordplay and dance. Originally created by elves, this tradition has been adopted by non-elf practitioners, who honor and expand on the elven ways.<br><br>In combat, a bladesinger uses a series of intricate, elegant maneuvers that fend off harm and allow the bladesinger to channel magic into devastating attacks and a cunning defense. Many who have observed a bladesinger at work remember the display as one of the more beautiful experiences in their life, a glorious dance accompanied by a singing blade.", 0); -- 211
INSERT INTO class_sub_features VALUES (NULL, 136, "Order of Scribes", NULL, "Magic of the book-that's what many folk call wizardry. The name is apt, given how much time wizards spend poring over tomes and penning theories about the nature of magic. It's rare to see wizards traveling without books and scrolls sprouting from their bags, and a wizard would go to great lengths to plumb an archive of ancient knowledge.<br><br>Among wizards, the Order of Scribes is the most bookish. It takes many forms in different worlds, but its primary mission is the same everywhere: recording magical discoveries so that wizardry can flourish. And while all wizards value spellbooks, a wizard in the Order of Scribes magically awakens their book, turning it into a trusted companion. All wizards study books, but a wizardly scribe talks to theirs!", 0); -- 212
INSERT INTO class_features VALUES (NULL, 12, "Spell Mastery", "Choose a 1st and 2nd level spell that can be cast as a Cantrip (at lowest level).", "You have achieved such mastery over certain spells that you can cast them at will. Choose a 1st-level wizard spell and a 2nd-level wizard spell that are in your spellbook. You can cast those spells at their lowest level without expending a spell slot when you have them prepared. If you want to cast either spell at a higher level, you must expend a spell slot as normal.<br>By spending 8 hours in study, you can exchange one or both of the spells you chose for different spells of the same levels.", 18, 0); -- 137
INSERT INTO class_features VALUES (NULL, 12, "Signature Spells", "Choose two 3rd lvl spells to be always prepared and not count against the total, cast each as a Cantrip once per rest.", "You gain mastery over two powerful spells and can cast them with little effort. Choose two 3rd-level wizard spells in your spellbook as your signature spells. You always have these spells prepared, they don't count against the number of spells you have prepared, and you can cast each of them once at 3rd level without expending a spell slot. When you do so, you can't do so again until you finish a short or long rest.<br>If you want to cast either spell at a higher level, you must expend a spell slot as normal.", 20, 0); -- 138
INSERT INTO class_features VALUES (NULL, 12, "Cantrip Formulas", "Replace one wizard cantrip after a long rest.", "You have scribed a set of arcane formulas in your spellbook that you can use to formulate a cantrip in your mind. Whenever you finish a long rest and consult those formulas in your spellbook, you can replace one wizard cantrip you know with another cantrip from the wizard spell list.", 3, 1); -- 139
INSERT INTO archetype_features VALUES (NULL, 87, "Abjuration Savant", "Gold and time to copy spells is halved.", "The gold and time you must spend to copy an Abjuration spell into your spellbook is halved.", 2); -- 454
INSERT INTO archetype_features VALUES (NULL, 87, "Arcane Ward", "Once per long rest, create ward when you cast an Abjuration spell 1st lvl+ that lasts until you finish a long rest. Ward has HP = Wizard lvl + INT mod. When you cast a 1st lvl+ Abjuration spell ward regainst HP = 2 x spell level.", "You can weave magic around yourself for protection. When you cast an abjuration spell of 1st level or higher, you can simultaneously use a strand of the spell's magic to create a magical ward on yourself that lasts until you finish a long rest. The ward has hit points equal to twice your wizard level + your Intelligence modifier. Whenever you take damage, the ward takes the damage instead. If this damage reduces the ward to 0 hit points, you take any remaining damage.<br>While the ward has 0 hit points, it can't absorb damage, but its magic remains. Whenever you cast an abjuration spell of 1st level or higher, the ward regains a number of hit points equal to twice the level of the spell.<br>Once you create the ward, you can't create it again until you finish a long rest.", 2); -- 455
INSERT INTO archetype_features VALUES (NULL, 87, "Projected Ward", "Reaction to use ward to absorb damage against creature within 30ft.", "When a creature that you can see within 30 feet of you takes damage, you can use your reaction to cause your Arcane Ward to absorb that damage. If this damage reduces the ward to 0 hit points, the warded creature takes any remaining damage.", 6); -- 456
INSERT INTO archetype_features VALUES (NULL, 87, "Improved Abjuration", "Add prof bonus to ability check as part of an anjuration spell.", "When you cast an abjuration spell that requires you to make an ability check as a part of casting that spell (as in counterspell and dispel magic), you add your proficiency bonus to that ability check.", 10); -- 457
INSERT INTO archetype_features VALUES (NULL, 87, "Spell Resistance", "You have advantage on saving throws against spells.<br>Furthermore, you have resistance against the damage of spells.", "You have advantage on saving throws against spells.<br>Furthermore, you have resistance against the damage of spells.", 14); -- 458
INSERT INTO archetype_features VALUES (NULL, 88, "Conjuration Savant", "Gold and time to copy spells is halved.", "The gold and time you must spend to copy a Conjuration spell into your spellbook is halved.", 2); -- 459
INSERT INTO archetype_features VALUES (NULL, 88, "Minor Conjuration", "Action to conjure inanimate object within 10ft, radiats dim light. Lasts 1 hour or until damaged.", "You can use your action to conjure up an inanimate object in your hand or on the ground in an unoccupied space that you can see within 10 feet of you. This object can be no larger than 3 feet on a side and weigh no more than 10 pounds, and its form must be that of a nonmagical object that you have seen. The object is visibly magical, radiating dim light out to 5 feet.<br>The object disappears after 1 hour, when you use this feature again, or if it takes any damage.", 2); -- 460
INSERT INTO archetype_features VALUES (NULL, 88, "Benign Transposition", "Once per long rest, or per cast 1st lvl+ conjuration spell, Action teleport up to 30ft. Or swap places with a willing creature", "You can use your action to teleport up to 30 feet to an unoccupied space that you can see. Alternatively, you can choose a space within range that is occupied by a Small or Medium creature. If that creature is willing, you both teleport, swapping places.<br>Once you use this feature, you can't use it again until you finish a long rest or you cast a conjuration spell of 1st level or higher.", 6); -- 461
INSERT INTO archetype_features VALUES (NULL, 88, "Focused Conjuration", "Concentration on conjuration spells isn't broken by dmg.", "While you are concentrating on a conjuration spell, your concentration can't be broken as a result of taking damage.", 10); -- 462
INSERT INTO archetype_features VALUES (NULL, 88, "Durable Summons", "Creatures summoned via conjuration have temp HP = 30.", "Any creature that you summon or create with a conjuration spell has 30 temporary hit points.", 14); -- 463
INSERT INTO archetype_features VALUES (NULL, 89, "Divination Savant", "Gold and time to copy spells is halved.", "The gold and time you must spend to copy a Divination spell into your spellbook is halved.", 2); -- 464
INSERT INTO archetype_features VALUES (NULL, 89, "Portent", "After a long rest, roll 2d20 record the values. Replace any roll made by you or a creature you can see.", "Glimpses of the future begin to press in on your awareness. When you finish a long rest, roll two d20s and record the numbers rolled. You can replace any attack roll, saving throw, or ability check made by you or a creature that you can see with one of these foretelling rolls. You must choose to do so before the roll, and you can replace a roll in this way only once per turn.<br>Each foretelling roll can be used only once. When you finish a long rest, you lose any unused foretelling rolls.", 2); -- 465
INSERT INTO archetype_features VALUES (NULL, 89, "Expert Divination", "When casting divination spell 2nd lvl+ regain spell slot of a lower level than the spell cast.", "Casting divination spells comes so easily to you that it expends only a fraction of your spellcasting efforts. When you cast a divination spell of 2nd level or higher using a spell slot, you regain one expended spell slot. The slot you regain must be of a level lower than the spell you cast and can't be higher than 5th level.", 6); -- 466
INSERT INTO archetype_features VALUES (NULL, 89, "The Third Eye", "Once per rest, action to gain one benefit:<br><b>Darkvision</b>. You gain darkvision to 60ft.<br><b>Ethereal Sight</b>. See into Ethereal Plane within 60ft.<br><b>Greater Comprehension</b>. Read any language.<br><b>See Invisibility</b>. See invisible creatures and objects within 10ft within line of sight.", "You can use your action to increase your powers of perception. When you do so, choose one of the following benefits, which lasts until you are incapacitated or you take a short or long rest. You can't use the feature again until you finish a rest.<br><b>Darkvision</b>. You gain darkvision out to a range of 60 feet, as described in chapter 8.<br><b>Ethereal Sight</b>. You can see into the Ethereal Plane within 60 feet of you.<br><b>Greater Comprehension</b>. You can read any language.<br><b>See Invisibility</b>. You can see invisible creatures and objects within 10 feet of you that are within line of sight.", 10); -- 467
INSERT INTO archetype_features VALUES (NULL, 89, "Greater Portent", "Roll 3d20 for Portent feature.", "The visions in your dreams intensify and paint a more accurate picture in your mind of what is to come. You roll three d20s for your Portent feature, rather than two.", 14); -- 468
INSERT INTO archetype_features VALUES (NULL, 90, "Enchantment Savant", "Gold and time to copy spells is halved.", "The gold and time you must spend to copy an Enchantment spell into your spellbook is halved.", 2); -- 469
INSERT INTO archetype_features VALUES (NULL, 90, "Hypnotic Gaze", "Action against creature within 5ft, Wis save or charmed until end of next turn. Charmed creature speed = 0, incapacitated and dazed. On subsequent turns, use action to maintain effect. Ends if you move more than 5ft from the creature or it takes damage.", "Your soft words and enchanting gaze can magically enthrall another creature. As an action, choose one creature that you can see within 5 feet of you. If the target can see or hear you, it must succeed on a Wisdom saving throw against your wizard spell save DC or be charmed by you until the end of your next turn. The charmed creature's speed drops to 0, and the creature is incapacitated and visibly dazed.<br>On subsequent turns, you can use your action to maintain this effect, extending its duration until the end of your next turn. However, the effect ends if you move more than 5 feet away from the creature, if the creature can neither see nor hear you, or if the creature takes damage.<br>Once the effect ends, or if the creature succeeds on its initial saving throw against this effect, you can't use this feature on that creature again until you finish a long rest.", 2); -- 470
INSERT INTO archetype_features VALUES (NULL, 90, "Instinctive Charm", "When creature within 30ft attacks you, reaction make Wis save, on fail must target closest creature (not yourself).", "When a creature you can see within 30 feet of you makes an attack roll against you, you can use your reaction to divert the attack, provided that another creature is within the attack's range. The attacker must make a Wisdom saving throw against your wizard spell save DC. On a failed save, the attacker must target the creature that is closest to it, not including you or itself. If multiple creatures are closest, the attacker chooses which one to target. On a successful save, you can't use this feature on the attacker again until you finish a long rest.<br>You must choose to use this feature before knowing whether the attack hits or misses. Creatures that can't be charmed are immune to this effect.", 6); -- 471
INSERT INTO archetype_features VALUES (NULL, 90, "Split Enchantment", "When casting encharment 1st lvl+ with a single target target another creature.", "When you cast an enchantment spell of 1st level or higher that targets only one creature, you can have it target a second creature.", 10); -- 472
INSERT INTO archetype_features VALUES (NULL, 90, "Alter Memories", "When an enchantment spell charms a creature, is unaware of being charmed. Once before the spell ends, Int save, on fail forget at most 1 + Cha mod hours of memory.", "You gain the ability to make a creature unaware of your magical influence on it. When you cast an enchantment spell to charm one or more creatures, you can alter one creature's understanding so that it remains unaware of being charmed.<br>Additionally, once before the spell expires, you can use your action to try to make the chosen creature forget some of the time it spent charmed. The creature must succeed on an Intelligence saving throw against your wizard spell save DC or lose a number of hours of its memories equal to 1 + your Charisma modifier (minimum 1). You can make the creature forget less time, and the amount of time can't exceed the duration of your enchantment spell.", 14); -- 473
INSERT INTO archetype_features VALUES (NULL, 91, "Evocation Savant", "Gold and time to copy spells is halved.", "The gold and time you must spend to copy a Evocation spell into your spellbook is halved.", 2); -- 474
INSERT INTO archetype_features VALUES (NULL, 91, "Sculpt Spells", "Can choose 1 + spell level creatures to automatically suceed on save against evocation spell.", "You can create pockets of relative safety within the effects of your evocation spells. When you cast an evocation spell that affects other creatures that you can see, you can choose a number of them equal to 1 + the spell's level. The chosen creatures automatically succeed on their saving throws against the spell, and they take no damage if they would normally take half damage on a successful save.", 2); -- 475
INSERT INTO archetype_features VALUES (NULL, 91, "Potent Cantrip", "On success against cantrip, target takes half damage (but no status effects).", "Your damaging cantrips affect even creatures that avoid the brunt of the effect. When a creature succeeds on a saving throw against your cantrip, the creature takes half the cantrip's damage (if any) but suffers no additional effect from the cantrip.", 6); -- 476
INSERT INTO archetype_features VALUES (NULL, 91, "Empowered Evocation", "Add Int mod to evocation spell damage.", "You can add your Intelligence modifier to the damage roll of any wizard evocation spell you cast.", 10); -- 477
INSERT INTO archetype_features VALUES (NULL, 91, "Overchannel", "When casting a spell of 5th lvl or less, deal maximum damage. If you do this more than once per long rest, take 2d12 x spell level necrotic damage. Increases by 1d12 each time past first.", "You can increase the power of your simpler spells. When you cast a wizard spell of 5th level or lower that deals damage, you can deal maximum damage with that spell.<br>The first time you do so, you suffer no adverse effect. If you use this feature again before you finish a long rest, you take 2d12 necrotic damage for each level of the spell, immediately after you cast it. Each time you use this feature again before finishing a long rest, the necrotic damage per spell level increases by 1d 12. This damage ignores resistance and immunity.", 14); -- 478
INSERT INTO archetype_features VALUES (NULL, 92, "Illusion Savant", "Gold and time to copy spells is halved.", "The gold and time you must spend to copy an Illusion spell into your spellbook is halved.", 2); -- 479
INSERT INTO archetype_features VALUES (NULL, 92, "Improved Minor Illusion", "Learn <i>minor illusion</i>. Doesn't count against Cantrips known. Can create both a soung and image effect.", "You learn the minor illusion cantrip. If you already know this cantrip, you learn a different wizard cantrip of your choice. The cantrip doesn't count against your number of cantrips known.<br>When you cast minor illusion, you can create both a sound and an image with a single casting of the spell.", 2); -- 480
INSERT INTO archetype_features VALUES (NULL, 92, "Malleable Illusions", "Action change nature of the illusion when illusion spell duration 1min+.", "When you cast an illusion spell that has a duration of 1 minute or longer, you can use your action to change the nature of that illusion (using the spell's normal parameters for the illusion), provided that you can see the illusion.", 6); -- 481
INSERT INTO archetype_features VALUES (NULL, 92, "Illusory Self", "Once per rest, when attacked use reaction to mitigate attack.", "You can create an illusory duplicate of yourself as an instant, almost instinctual reaction to danger. When a creature makes an attack roll against you, you can use your reaction to interpose the illusory duplicate between the attacker and yourself. The attack automatically misses you, then the illusion dissipates.<br>Once you use this feature, you can't use it again until you finish a short or long rest.", 10); -- 482
INSERT INTO archetype_features VALUES (NULL, 92, "Illusory Reality", "On illusion spell 1st lvl+, bonus action make inanimate object part of illusion real.", "You have learned the secret of weaving shadow magic into your illusions to give them a semireality. When you cast an illusion spell of 1st level or higher, you can choose one inanimate, nonmagical object that is part of the illusion and make that object real. You can do this on your turn as a bonus action while the spell is ongoing. The object remains real for 1 minute. For example, you can create an illusion of a bridge over a chasm and then make it real long enough for your allies to cross.<br>The object can't deal damage or otherwise directly harm anyone.", 14); -- 483
INSERT INTO archetype_features VALUES (NULL, 93, "Necromancy Savant", "Gold and time to copy spells is halved.", "The gold and time you must spend to copy a Necromancy spell into your spellbook is halved.", 2); -- 484
INSERT INTO archetype_features VALUES (NULL, 93, "Grim Harvest", "Once per turn, when you kill a, non construct or undead, creature with a 1st lvl+ spell regain HP = 2 x spell level (3 x for necromancy spells).", "You gain the ability to reap life energy from creatures you kill with your spells. Once per turn when you kill one or more creatures with a spell of 1st level or higher, you regain hit points equal to twice the spell's level, or three times its level if the spell belongs to the School of Necromancy. You don't gain this benefit for killing constructs or undead.", 2); -- 485
INSERT INTO archetype_features VALUES (NULL, 93, "Undead Thralls", "When casting <i>animate dead</i> create extra undead. In addition:<br><ul><li>HP maximum is increased by your wizard level.</li><li>Adds your proficiency bonus to weapon damage.</li></ul>", "You add the <i>animate dead</i> spell to your spellbook if it is not there already. When you cast <i>animate dead</i>, you can target one additional corpse or pile of bones, creating another zombie or skeleton, as appropriate.<br>Whenever you create an undead using a necromancy spell, it has additional benefits:<br><ul><li>The creature's hit point maximum is increased by an amount equal to your wizard level.</li><li>The creature adds your proficiency bonus to its weapon damage rolls.</li></ul>", 6); -- 486
INSERT INTO archetype_features VALUES (NULL, 93, "Injured to Undeath", "Resistance to necrotic damage. Max HP can't be reduced.", "You have resistance to necrotic damage, and your hit point maximum can't be reduced. You have spent so much time dealing with undead and the forces that animate them that you have become inured to some of their worst effects.", 10); -- 487
INSERT INTO archetype_features VALUES (NULL, 93, "Command Undead", "Action choose one undead within 60ft, Cha save, on fail becomes friendly and obeys your commands. If Int > 7 has adv on save.", "You can use magic to bring undead under your control, even those created by other wizards. As an action, you can choose one undead that you can see within 60 feet of you. That creature must make a Charisma saving throw against your wizard spell save DC. If it succeeds, you can't use this feature on it again. If it fails, it becomes friendly to you and obeys your commands until you use this feature again.<br>Intelligent undead are harder to control in this way. If the target has an Intelligence of 8 or higher, it has advantage on the saving throw. If it fails the saving throw and has an Intelligence of 12 or higher, it can repeat the saving throw at the end of every hour until it succeeds and breaks free.", 14); -- 488
INSERT INTO archetype_features VALUES (NULL, 94, "Transmutation Savant", "Gold and time to copy spells is halved.", "The gold and time you must spend to copy a Transmutation spell into your spellbook is halved.", 2); -- 489
INSERT INTO archetype_features VALUES (NULL, 94, "Minor Alchemy", "Change wood, stone, iron, copper or siler into another material listed. 1 cubic foot / 10 mins. Reverts after 1 hour.", "You can temporarily alter the physical properties of one nonmagical object, changing it from one substance into another. You perform a special alchemical procedure on one object composed entirely of wood, stone (but not a gemstone), iron, copper, or silver, transforming it into a different one of those materials. For each 10 minutes you spend performing the procedure, you can transform up to 1 cubic foot of material. After 1 hour, or until you lose your concentration (as if you were concentrating on a spell), the material reverts to its original substance.", 2); -- 490
INSERT INTO archetype_features VALUES (NULL, 94, "Transmuter's Stone", "Spend 8 hours create transumuter stone, choose benefit:<br><ul><li>Darkvision 60ft</li><li>Speed + 10ft</li><li>Proficiency in Con saves</li><li>Resistance to acid, cold, fire, lightning, or thunder damage</li></ul><br>Can change effect when you cast a transmutation spell 1st lvl+.", "You can spend 8 hours creating a transmuter's stone that stores transmutation magic. You can benefit from the stone yourself or give it to another creature. A creature gains a benefit of your choice as long as the stone is in the creature's possession. When you create the stone, choose the benefit from the following options:<br><ul><li>Darkvision out to a range of 60 feet, as described in chapter 8</li><li>An increase to speed of 10 feet while the creature is unencumbered</li><li>Proficiency in Constitution saving throws</li><li>Resistance to acid, cold, fire, lightning, or thunder damage (your choice whenever you choose this benefit)</li></ul><br>Each time you cast a transmutation spell of 1st level or higher, you can change the effect of your stone if the stone is on your person.<br>If you create a new transmuter's stone, the previous one ceases to function.", 6); -- 491
INSERT INTO archetype_features VALUES (NULL, 94, "Shapechanger", "Learn <i>polymorph</i>. Once per rest cast it without expending a spell slot to change to beast with challenge 1 or less.", "You add the <i>polymorph</i> spell to your spellbook, if it is not there already. You can cast <i>polymorph</i> without expending a spell slot. When you do so, you can target only yourself and transform into a beast whose challenge rating is 1 or lower.", 10); -- 492
INSERT INTO archetype_features VALUES (NULL, 94, "Master Transmuter", "Action to consume transmuter stone to achieve an effect:<br><b>Major Transmuter.</b> 10 mins transmute 5-foot cube object into another nonmagical object of similar size, mass and value.<br><b>Panacea.</b> Rremove all curses, diseases, and poisons affecting a creature. Creature regains all HP.<br><b>Restore Life.</b> Cast <i>raise dead</i> on a creature without expending a spell slot.<br><b>Restore Youth.</b> Creature's apparent age is reduced by 3d10 years (min 13 years old). Doesn't extend the creature's lifespan.", "You can use your action to consume the reserve of transmutation magic stored within your transmuter's stone in a single burst. When you do so, choose one of the following effects. Your transmuter's stone is destroyed and can't be remade until you finish a long rest.<br><br><b>Major Transmuter.</b> You can transmute one nonmagical object—no larger than a 5-foot cube—into another nonmagical object of similar size and mass and of equal or lesser value. You must spend 10 minutes handling the object to transform it.<br><b>Panacea.</b> You remove all curses, diseases, and poisons affecting a creature that you touch with the transmuter's stone. The creature also regains all its hit points.<br><b>Restore Life.</b> You cast the raise dead spell on a creature you touch with the transmuter's stone, without expending a spell slot or needing to have the spell in your spellbook.<br><b>Restore Youth.</b> You touch the transmuter's stone to a willing creature, and that creature's apparent age is reduced by 3d10 years, to a minimum of 13 years. This effect doesn't extend the creature's lifespan.", 14); -- 493
INSERT INTO archetype_features VALUES (NULL, 95, "Arcane Deflection", "When you are hit or fail a save, reaction to gain +2 AC or +4 bonus to saveing throw. Can't cast spells other than Cantrips until end of next turn.", "You have learned to weave your magic to fortify yourself against harm. When you are hit by an attack or you fail a saving throw, you can use your reaction to gain a +2 bonus to your AC against that attack or a +4 bonus to that saving throw.<br>When you use this feature, you can't cast spells other than cantrips until the end of your next turn.", 2); -- 494
INSERT INTO archetype_features VALUES (NULL, 95, "Tactical Wit", "Add Int mod to initiative.", "Your keen ability to assess tactical situations allows you to act quickly in battle. You can give yourself a bonus to your initiative rolls equal to your Intelligence modifier.", 2); -- 495
INSERT INTO archetype_features VALUES (NULL, 95, "Power Surge", "Store Int mod power surges. When you finish a long rest resets to 1 charge. When you end a spell with dispel magic or counterspell, gain one surge. If you end a rest with no surges, gain 1 surge.<br>Once per turn spend a surge to deal force damage =  wizard level.", "You can store magical energy within yourself to later empower your damaging spells. In its stored form, this energy is called a power surge.<br>You can store a maximum number of power surges equal to your Intelligence modifier (minimum of one). Whenever you finish a long rest, your number of power surges resets to one. Whenever you successfully end a spell with dispel magic or counterspell, you gain one power surge, as you steal magic from the spell you foiled. If you end a short rest with no power surges, you gain one power surge.<br>Once per turn when you deal damage to a creature or object with a wizard spell, you can spend one power surge to deal extra force damage to that target. The extra damage equals half your wizard level.", 6); -- 496
INSERT INTO archetype_features VALUES (NULL, 95, "Durable Magic", "While concentrating, gain +2 AC and saves.", "The magic you channel helps ward off harm. While you maintain concentration on a spell, you have a +2 bonus to AC and all saving throws.", 10); -- 497
INSERT INTO archetype_features VALUES (NULL, 95, "Deflecting Shroud", "When you use Arcane Deflection, choose three creatures within 60ft to take fource damage = wizard lvl / 2.", "Your Arcane Deflection becomes infused with deadly magic. When you use your Arcane Deflection feature, you can cause magical energy to arc from you. Up to three creatures of your choice that you can see within 60 feet of you each take force damage equal to half your wizard level.", 14); -- 498
INSERT INTO archetype_features VALUES (NULL, 96, "Training in War and Song", "Gain proficiency with light armor, one one-handed melee weapon, and Performance skill.", "You gain proficiency with light armor, and you gain proficiency with one type of one-handed melee weapon of your choice.<br><br>You also gain proficiency in the Performance skill if you don't already have it.", 2); -- 499
INSERT INTO archetype_features VALUES (NULL, 96, "Bladesong", "Bonus action to Bladesong for 1 min (while not wearing medium or heavy armor, using a shield, or making a two-handed attack). While active you gain the following:<br><ul><li>Add Intelligence modifier (min 1) to AC.</li><li>+10ft walking speed.</li><li>Advantage on Acrobatics checks.</li><li>Add Intelligence modifier (min 1) to concentration checks.</li></ul><br>Use this a number of times equal to proficiency bonus, reset on long rest.", "You can invoke an elven magic called the Bladesong, provided that you aren't wearing medium or heavy armor or using a shield. It graces you with supernatural speed, agility, and focus.<br><br>You can use a bonus action to start the Bladesong, which lasts for 1 minute. It ends early if you are incapacitated, if you don medium or heavy armor or a shield, or if you use two hands to make an attack with a weapon. You can also dismiss the Bladesong at any time (no action required).<br><br>While your Bladesong is active, you gain the following benefits:<br><br><ul><li>You gain a bonus to your AC equal to your Intelligence modifier (minimum of +1).</li><li>Your walking speed increases by 10 feet.</li><li>You have advantage on Dexterity (Acrobatics) checks.</li><li>You gain a bonus to any Constitution saving throw you make to maintain your concentration on a spell. The bonus equals your Intelligence modifier (minimum of + 1).</li></ul><br><br>You can use this feature a number of times equal to your proficiency bonus, and you regain all expended uses of it when you finish a long rest.", 2); -- 500
INSERT INTO archetype_features VALUES (NULL, 96, "Extra Attack", "Attack twice per Attack action. Can replace an attack with a cantrip.", "You can attack twice, instead of once, whenever you take the Attack action on your turn. Moreover, you can cast one of your cantrips in place of one of those attacks.", 6); -- 501
INSERT INTO archetype_features VALUES (NULL, 96, "Song of Defense", "While in Bladesong, when you take damage use reaction to expend one spell slot to reduce damage by 5 x spell slot level.", "You can direct your magic to absorb damage while your Bladesong is active. When you take damage, you can use your reaction to expend one spell slot and reduce that damage to you by an amount equal to five times the spell slot's level.", 10); -- 502
INSERT INTO archetype_features VALUES (NULL, 96, "Song of Victory", "While in Bladesong, add Intelligence modifier (min 1) to melee weapon attacks.", "You can add your Intelligence modifier (minimum of + 1) to the damage of your melee weapon attacks while your Bladesong is active.", 14); -- 503
INSERT INTO archetype_features VALUES (NULL, 97, "Wizardly Quill", "Bonus action to summon Tiny quill in free hand. Has the following properties:<br><ul><li>Doesn't require ink, choose colour.</li><li>Spend 2 mins per spell level to copy spells.</li><li>Bonus action erase text you've written within 5ft.</li></ul>", "As a bonus action, you can magically create a Tiny quill in your free hand. The magic quill has the following properties:<br><br><ul><li>The quill doesn't require ink. When you write with it, it produces ink in a color of your choice on the writing surface.</li><li>The time you must spend to copy a spell into your spellbook equals 2 minutes per spell level if you use the quill for the transcription.</li><li>You can erase anything you write with the quill if you wave the feather over the text as a bonus action, provided the text is within 5 feet of you.</li></ul><br><br>This quill disappears if you create another one or if you die.", 2); -- 504
INSERT INTO archetype_features VALUES (NULL, 97, "Awakened Spellbook", "While holding your spellbook you gain the following benefits:<br><ul><li>Book is a focus for wizard spells.</li><li>When you cast a spell of 1st level or higher, replace damage with damage type of another spell you know at the same level.</li><li>Once per long rest, cast ritual using normal cast time.</li></ul>", "Using specially prepared inks and ancient incantations passed down by your wizardly order, you have awakened an arcane sentience within your spell book.<br><br>While you are holding the book, it grants you the following benefits:<br><br><ul><li>You can use the book as a spellcasting focus for your wizard spells.</li><li>When you cast a wizard spell with a spell slot, you can temporarily replace its damage type with a type that appears in another spell in your spellbook, which magically alters the spell's formula for this casting only. The latter spell must be of the same level as the spell slot you expend.</li><li>When you cast a wizard spell as a ritual, you can use the spell's normal casting time, rather than adding 10 minutes to it. Once you use this benefit, you can't do so again until you finish a long rest.</li></ul><br><br>If necessary, you can replace the book over the course of a short rest by using your Wizardly Quill to write arcane sigils in a blank book or a magic spell book to which you're attuned. At the end of the rest, your spellbook's consciousness is summoned into the new book, which the consciousness transforms into your spellbook, along with all its spells. If the previous book still existed somewhere, all the spells vanish from its pages.", 2); -- 505
INSERT INTO archetype_features VALUES (NULL, 97, "Manifest Mind", "Once per long rest, bonus action conjure Awakened Spellbook spirit within 60ft. Sheds dim light within 10ft.<br>Spectral Mind can hear and see, 60ft darkvision, telepathic bond (share what it sees and hears).<br>Can cast wizard spells from it's space, number of times equal to proficiency bonus, reset long rest.<br>Bonus action to move up to 30ft. Stops manifesting if more than 300ft away, it is dispelled, or you die.", "You can conjure forth the mind of your Awakened Spellbook. As a bonus action while the book is on your person, you can cause the mind to manifest as a Tiny spectral object, hovering in an unoccupied space of your choice within 60 feet of you. The spectral mind is intangible and doesn't occupy its space, and it sheds dim light in a 10-foot radius. It looks like a ghostly tome, a cascade of text, or a scholar from the past (your choice).<br><br>While manifested, the spectral mind can hear and see, and it has darkvision with a range of 60 feet. The mind can telepathically share with you what it sees and hears (no action required).<br><br>Whenever you cast a wizard spell on your turn, you can cast it as if you were in the spectral mind's space, instead of your own, using its senses. You can do so a number of times per day equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.<br><br>As a bonus action, you can cause the spectral mind to hover up to 30 feet to an unoccupied space that you or it can see. It can pass through creatures but not objects.<br><br>The spectral mind stops manifesting if it is ever more than 300 feet away from you, if someone casts dispel magic on it, if the Awakened Spellbook is destroyed, if you die, or if you dismiss the spectral mind as a bonus action.<br><br>Once you conjure the mind, you can't do so again until you finish a long rest, unless you expend a spell slot of any level to conjure it again.", 6); -- 506
INSERT INTO archetype_features VALUES (NULL, 97, "Master Scrivener", "After a long rest, create a magic scroll of 1st or 2nd level with a cast time of 1 action. The spell counts as 1 level higher than normal, and is cast using an action.<br>Gold and time spent on making scrolls is halved.", "Whenever you finish a long rest, you can create one magic scroll by touching your Wizardly Quill to a blank piece of paper or parchment and causing one spell from your Awakened Spellbook to be copied onto the scroll. The spellbook must be within 5 feet of you when you make the scroll.<br><br>The chosen spell must be of 1st or 2nd level and must have a casting time of 1 action. Once in the scroll, the spell's power is enhanced, counting as one level higher than normal. You can cast the spell from the scroll by reading it as an action. The scroll is unintelligible to anyone else, and the spell vanishes from the scroll when you cast it or when you finish your next long rest.<br><br>You are also adept at crafting spell scrolls, which are described in the treasure chapter of the Dungeon Master's Guide. The gold and time you must spend to make such a scroll are halved if you use your Wizardly Quill.", 10); -- 507
INSERT INTO archetype_features VALUES (NULL, 97, "One with the Word", "Advantage on all Arcana checks.<br>Once per long rest, use reaction to prevent all damage while Manifest Mind is active. Roll 3d6, the spellbook temporarily loses (for 1d6 long rests) spells that you choose where the sum of their levels equals the sum of the number rolled.", "Your connection to your Awakened Spellbook has become so profound that your soul has become entwined with it. While the book is on your person, you have advantage on all Intelligence (Arcana) checks, as the spellbook helps you remember magical lore.<br><br>Moreover, if you take damage while your spellbook's mind is manifested, you can prevent all of that damage to you by using your reaction to dismiss the spectral mind, using its magic to save yourself. Then roll 3d6. The spellbook temporarily loses spells of your choice that have a combined spell level equal to that roll or higher. For example, if the rail's total is 9, spells vanish from the book that have a combined level of at least 9, which could mean one 9th-level spell, three 3rd-level spells, or some other combination. If there aren't enough spells in the book to cover this cost, you drop to 0 hit points.<br><br>Until you finish ld6 long rests, you are incapable of casting the lost spells, even if you find them on a scroll or in another spellbook. After you finish the required number of rests, the spells reappear in the spellbook.<br><br>Once you use this reaction, you can't do so again
INSERT INTO class_features VALUES (NULL, 13, "Magical Tinkering", "Action to touch one Tiny nonmagical object give it one of the following:<br><ul><li>Emit bright light 5ft, dim light additional 5ft.</li><li>When tapped emit 6 second recorded message.</li><li>Emit continuous nonverbal sound within 10ft.</li><li>Static visual effect on one surface (image, 25 words).</li></ul><br>Lasts indefinitely, can apply to Intelligence modifier (min 1) objects at once.", "You've learned how to invest a spark of magic into mundane objects. To use this ability, you must have thieves' tools or artisan's tools in hand. You then touch a Tiny nonmagical object as an action and give it one of the following magical properties of your choice:<br><br><ul><li>The object sheds bright light in a 5-foot radius and dim light for an additional 5 feet.</li><li>Whenever tapped by a creature, the object emits a recorded message that can be heard up to 10 feet away. You utter the message when you bestow this property on the object, and the recording can be no more than 6 seconds long.</li><li>The object continuously emits your choice of an odor or a nonverbal sound (wind, waves, chirping, or the like). The chosen phenomenon is perceivable up to 10 feet away.</li><li>A static visual effect appears on one of the object's surfaces. This effect can be a picture, up to 25 words of text, lines and shapes, or a mixture of these elements, as you like.</li></ul><br><br>The chosen property lasts indefinitely. As an action, you can touch the object and end the property early.<br><br>You can bestow magic on multiple objects, touching one object each time you use this feature, though a single object can only bear one property at a time. The maximum number of objects you can affect with this feature at one time is equal to your Intelligence modifier (minimum of one object). If you try to exceed your maximum, the oldest property immediately ends, and then the new property applies.", 1, 0); -- 140
INSERT INTO class_features VALUES (NULL, 13, "Spellcasting", "Spell save DC = 8 + proficiency bonus + Intelligence modifier<br>Spell attack modifier = proficiency bonus + Intelligence modifier<br>You can cast spells as rituals (add 10 mins to the cast time).<br>Prepare a number of spells equal to Intelligence modifier + half your artificer level (rounded down).<br>Change your prepared spells on a long rest.", "You've studied the workings of magic and how to cast spells, channeling the magic through objects. To observers, you don't appear to be casting spells in a conventional way; you appear to produce wonders from mundane items and outlandish inventions.<br><br><strong>Tools Required</strong><br>You produce your artificer spell effects through your tools. You must have a spellcasting focus-specifically thieves' tools or some kind of artisan's tool-in hand when you cast any spell with this Spellcasting feature (meaning the spell has an 'M' component when you cast it). You must be proficient with the tool to use it in this way. See the equipment chapter in the Player's Handbook for descriptions of these tools.<br><br>After you gain the Infuse Item feature at 2nd level, you can also use any item bearing one of your infusions as a spellcasting focus.<br><br><strong>Cantrips (0-Level Spells)</strong><br>You know two cantrips of your choice from the artificer spell list. At higher levels, you learn additional artificer cantrips of your choice, as shown in the Cantrips Known column of the Artificer table.<br><br>When you gain a level in this class, you can replace one of the artificer can trips you know with another cantrip from the artificer spell list.<br><br><strong>Preparing and Casting Spells</strong><br>The Artificer table shows how many spell slots you have to cast your artificer spells. To cast one of your artificer spells of 1st level or higher, you must expend a slot of the spell's level or higher. You regain all expended spell slots when you finish a long rest.<br><br>You prepare the list of artificer spells that are available for you to cast, choosing from the artificer spell list. When you do so, choose a number of artificer spells equal to your Intelligence modifier + half your artificer level, rounded down (minimum of one spell). The spells must be of a level for which you have spell slots.<br><br>For example, if you are a 5th-level artificer, you have four 1 st-level and two 2nd-level spell slots. With an Intelligence of 14, your list of prepared spells can include four spells of 1st or 2nd level, in any combination. If you prepare the 1 st-level spell cure wounds, you can cast it using a 1st-level or a 2nd-level slot. Casting the spell doesn't remove it from your list of prepared spells.<br><br>You can change your list of prepared spells when you finish a long rest. Preparing a new list of artificer spells requires time spent tinkering with your spellcasting focuses: at least 1 minute per spell level for each spell on your list.<br><br><strong>Spellcasting Ability</strong><br>Intelligence is your spellcasting ability for your artificer spells; your understanding of the theory behind magic allows you to wield these spells with superior skill. You use your Intelligence whenever an artificer spell refers to your spellcasting ability. In addition, you use your Intelligence modifier when setting the saving throw DC for an artificer spell you cast and when making an attack roll with one.<br><br><strong>Spell save DC</strong> = 8 + your proficiency bonus + your Intelligence modifier<br><br><strong>Spell attack modifier</strong> = your proficiency bonus + your Intelligence modifier<br><br><strong>Ritual Casting</strong><br>You can cast an artificer spell as a ritual if that spell has the ritual tag and you have the spell prepared.<br><br>", 1, 0); -- 141
INSERT INTO class_features VALUES (NULL, 13, "Infuse Item", "You learn a number of infusions, after a long rest touch nonmagical item to imbue it. Infusions last indefinitely (unless you die).<br>Can infuse 2 items at 2nd level, 3 at 6th, 4 at 10th, 5 at 14th, and 6 at 18th. Each infusion can be infused once at a time, cannot infuse multiple into one object.", "You've gained the ability to imbue mundane items with certain magical infusions, turning those objects into magic items.<br><br><strong>Infusions Known</strong><br>When you gain this feature, pick four artificer infusions to learn, choosing from the 'Artificer Infusions' section at the end of the class's description. You learn additional infusions of your choice when you reach certain levels in this class, as shown in the Infusions Known column of the Artificer table.<br><br>Whenever you gain a level in this class, you can replace one of the artificer infusions you learned with a new one.<br><br><strong>Infusing and Item</strong><br>Whenever you finish a long rest, you can touch a nonmagical object and imbue it with one of your artificer infusions, turning it into a magic item. An infusion works on only certain kinds of objects, as specified in the infusion's description. If the item requires attunement, you can attune yourself to it the instant you infuse the item. If you decide to attune to the item later, you must do so using the normal process for attunement (see the attunement rules in the Dungeon Master's Guide).<br><br>Your infusion remains in an item indefinitely, but when you die, the infusion vanishes after a number of days equal to your Intelligence modifier (minimum of 1 day). The infusion also vanishes if you replace your knowledge of the infusion.<br><br>You can infuse more than one nonmagical object at the end of a long rest; the maximum number of objects appears in the Infused Items column of the Artificer table. You must touch each of the objects, and each of your infusions can be in only one object at a time. Moreover, no object can bear more than one of your infusions at a time. If you try to exceed your maximum number of infusions, the oldest infusion ends, and then the new infusion applies.<br><br>If an infusion ends on an item that contains other things, like a bag of holding, its contents harmlessly appear in and around its space.", 2, 0); -- 142
INSERT INTO class_sub_features VALUES (NULL, 142, "Arcane Propulsion Armor", "14th level, a suit of armour (requires attunement)", "The wearer of this armor gains these benefits:<br><br><ul><li>The wearer's walking speed increases by 5 feet.</li><li>The armor includes gauntlets, each of which is a magic melee weapon that can be wielded only when the hand is holding nothing. The wearer is proficient with the gauntlets, and each one deals ld8 force damage on a hit and has the thrown property, with a normal range of 20 feet and a long range of 60 feet. When thrown, the gauntlet detaches and flies at the attack's target, then immediately returns to the wearer and reattaches.</li><li>The armor can't be removed against the wearer's will.</li><li>If the wearer is missing any limbs, the armor replaces those limbs-hands, arms, feet, legs, or similar appendages. The replacements function identically to the body parts they replace.</li></ul>", 0); -- 213
INSERT INTO class_sub_features VALUES (NULL, 142, "Armor of Magical Strength", "A suit of armor (requires attunement)", "This armor has 6 charges. The wearer can expend the armor's charges in the following ways:<br><br><ul><li>When the wearer makes a Strength check or a Strength saving throw, it can expend 1 charge to add a bonus to the roll equal to its Intelligence modifier.</li><li>If the creature would be knocked prone, it can use its reaction to expend 1 charge to avoid being knocked prone.</li></ul><br><br>The armor regains ld6 expended charges daily at dawn.", 0); -- 214
INSERT INTO class_sub_features VALUES (NULL, 142, "Boots of the Winding Path", "6th level, a pair of boots (requires attunement)", "While wearing these boots, a creature can teleport up to 15 feet as a bonus action to an unoccupied space the creature can see. The creature must have occupied that space at some point during the current turn.", 0); -- 215
INSERT INTO class_sub_features VALUES (NULL, 142, "Enhanced Arcane Focus", "A rod, staff, or wand (requires attunement)", "While holding this item, a creature gains a + 1 bonus to spell attack rolls. In addition, the creature ignores half cover when making a spell attack.<br><br>The bonus increases to +2 when you reach 10th level in this class.", 0); -- 216
INSERT INTO class_sub_features VALUES (NULL, 142, "Enhanced Defense", "A suit of armor or a shield", "A creature gains a +1 bonus to Armor Class while wearing (armor) or wielding (shield) the infused item.<br><br>The bonus increases to +2 when you reach 10th level in this class.", 0); -- 217
INSERT INTO class_sub_features VALUES (NULL, 142, "Enhanced Weapon", "A simple or martial weapon", "This magic weapon grants a +l bonus to attack and damage rolls made with it.<br><br>The bonus increases to +2 when you reach 10th level in this class.", 0); -- 218
INSERT INTO class_sub_features VALUES (NULL, 142, "Helm of Awareness", "10th level, a helmet (requires attunement)", "While wearing this helmet, a creature has advantage on initiative rolls. In addition, the wearer can't be surprised, provided it isn't incapacitated.", 0); -- 219
INSERT INTO class_sub_features VALUES (NULL, 142, "Homunculus Servant", "A gem or crystal worth at least 100gp", "You learn intricate methods for magically creating a special homunculus that serves you. The item you infuse serves as the creature's heart, around which the creature's body instantly forms.<br><br>You determine the homunculus's appearance. Some artificers prefer mechanical-looking birds, whereas some like winged vials or miniature, animate cauldrons.<br><br>The homunculus is friendly to you and your companions, and it obeys your commands. See this creature's game statistics in the Homunculus Servant stat block, which uses your proficiency bonus (PB) in several places.<br><br>In combat, the homunculus shares your initiative count, but it takes its turn immediately after yours. It can move and use its reaction on its own, but the only action it takes on its turn is the Dodge action, unless you take a bonus action on your turn to command it to take another action. That action can be one in its stat block or some other action. If you are incapacitated, the homunculus can take any action of its choice, not just Dodge.<br><br>The homunculus regains 2d6 hit points if the mending spell is cast on it. If you or the homunculus dies, it vanishe􀎼. leaving its heart in its space.", 0); -- 220
INSERT INTO class_sub_features VALUES (NULL, 142, "Mind Sharpener", "A suit of armor or robes", "The infused item can send a jolt to the wearer to refocus their mind. The item has 4 charges. When the wearer fails a Constitution saving throw to maintain concentration on a spell, the wearer can use its reaction to expend 1 of the item's charges to succeed instead. The item regains ld4 expended charges daily at dawn.", 0); -- 221
INSERT INTO class_sub_features VALUES (NULL, 142, "Radiant Weapon", "6th level, a simple or martial weapon (requires attunement)", "This magic weapon grants a + 1 bonus to attack and damage rolls made with it. While holding it, the wielder can take a bonus action to cause it to shed bright light in a 30-foot radius and dim light for an additional 30 feet. The wielder can extinguish the light as a bonus action.<br><br>The weapon has 4 charges. As a reaction immediately after being hit by an attack, the wielder can expend 1 charge and cause the attacker to be blinded until the end of the attacker's next turn, unless the attacker succeeds on a Constitution saving throw against your spell save DC. The weapon regains ld4 expended charges daily at dawn.", 0); -- 222
INSERT INTO class_sub_features VALUES (NULL, 142, "Repeating Shot", "A simple or martial weapon with the ammunition property (requires attunement)", "This magic weapon grants a + 1 bonus to attack and damage rolls made with it when it's used to make a ranged attack, and it ignores the loading property if it has it.<br><br>If you load no ammunition in the weapon, it produces its own, automatically creating one piece of magic ammunition when you make a ranged attack with it. The ammunition created by the weapon vanishes the instant after it hits or misses a target.", 0); -- 223
INSERT INTO class_sub_features VALUES (NULL, 142, "Replicate Magic Item", NULL, "Using this infusion, you replicate a particular magic item. You can learn this infusion multiple times; each time you do so, choose a magic item that you can make with it, picking from the Replicable Items tables. A table's title tells you the level you must be in the class to choose an item from the table. Alternatively, you can choose the magic item from among the common magic items in the game, not including potions or scrolls.<br><br>In the tables, an item's entry tells you whether the item requires attunement. See the item's description in the Dungeon Master's Guide for more information about it, including the type of object required for its making.", 0); -- 224
INSERT INTO class_sub_features VALUES (NULL, 142, "Repulsion Shield", "6th level, a chield (requires attunement)", "A creature gains a +1 bonus to Armor Class while wielding this shield.<br><br>The shield has 4 charges. While holding it, the wielder can use a reaction immediately after being hit by a melee attack to expend 1 of the shield's charges and push the attacker up to 15 feet away. The shield regains ld4 expended charges daily at dawn.", 0); -- 225
INSERT INTO class_sub_features VALUES (NULL, 142, "Resistant Armor", "6th level, a suit of armor (requires attunement)", "While wearing this armor, a creature has resistance to one of the following damage types, which you choose when you infuse the item: acid, cold, fire, force, lightning, necrotic, poison, psychic, radiant, or thunder.", 0); -- 226
INSERT INTO class_sub_features VALUES (NULL, 142, "Returning Weapon", "A simple or martial weapon with the property", "This magic weapon grants a + 1 bonus to attack and damage rolls made with it, and it returns to the wielder's hand immediately after it is used to make a ranged attack.", 0); -- 227
INSERT INTO class_sub_features VALUES (NULL, 142, "Spell-Refueling Ring", "6th level, a ring (requires attunement)", "While wearing this ring, the creature can recover one expended spell slot as an action. The recovered slot can be of 3rd level or lower. Once used, the ring can't be used again until the next dawn.", 0); -- 228
INSERT INTO class_features VALUES (NULL, 13, "Artificer Specialist", NULL, "Choose the type of specialist you are: Alchemist, Armorer, Artillerist, or Battle Smith, each of which is detailed after the class's description. Your choice grants you features at 5th level and again at 9th and 1 5th level.", 3, 0); -- 143
INSERT INTO class_sub_features VALUES (NULL, 143, "Alchemist", NULL, "An Alchemist is an expert at combining reagents to produce mystical effects. Alchemists use their creations to give life and to leech it away. Alchemy is the oldest of artificer traditions, and its versatility has long been valued during times of war and peace.", 0); -- 229
INSERT INTO class_sub_features VALUES (NULL, 143, "Armorer", NULL, "An artificer who specializes as an Armorer modifies armor to function almost like a second skin. The armor is enhanced to hone the artificer's magic, unleash potent attacks, and generate a formidable defense. The artificer bonds with this armor, becoming one with it even as they experiment with it and refine its magical capabilities.", 0); -- 230
INSERT INTO class_sub_features VALUES (NULL, 143, "Artillerist", NULL, "An Artillerist specializes in using magic to hurl energy, projectiles, and explosions on a battlefield. This destructive power is valued by armies in the wars on many different worlds. And when war passes, some members of this specialization seek to build a more peaceful world by using their powers to fight the resurgence of strife. The world-hopping gnome artificer Vi has been especially vocal about making things right: 'It's about time we fixed things instead of blowing them all to hell.'", 0); -- 231
INSERT INTO class_sub_features VALUES (NULL, 143, "Battle Smith", NULL, "Armies require protection, and someone has to put things back together if defenses fail. A combination of protector and medic, a Battle Smith is an expert at defending others and repairing both materiel and personnel. To aid in their work, Battle Smiths are accompanied by a steel defender, a protective companion of their own creation. Many soldiers tell stories of nearly dying before being saved by a Battle Smith and a steel defender.<br><br>In the world of Eberron, Battle Smiths played a key role in House Cannith's work on battle constructs and the original warforged, and after the Last War, these artificers led efforts to aid those who were injured in the war's horrific battles.", 0); -- 232
INSERT INTO class_features VALUES (NULL, 13, "The Right Too for the Job", "Holding thieves' tools or artisan's tools spend 1hr to create one set of artisan's tools within 5ft. Using this feature again causes the initial tools to vanish.", "You've learned how to produce exactly the tool you need: with thieves' tools or artisan's tools in hand, you can magically create one set of artisan's tools in an unoccupied space within 5 feet of you. This creation requires 1 hour of uninterrupted work, which can coincide with a short or long rest. Though the product of magic, the tools are nonmagical, and they vanish when you use this feature again.", 3, 0); -- 144
INSERT INTO class_features VALUES (NULL, 13, "Tool Expertise", "Your proficiency bonus is now doubled for any ability check you make that uses your proficiency with a tool.", "Your proficiency bonus is now doubled for any ability check you make that uses your proficiency with a tool.", 6, 0); -- 145
INSERT INTO class_features VALUES (NULL, 13, "Flash of Genius", "When you or creature within 30ft makes ability check or save, reaction to add your Intelligence modifier. Use times equal to Intelligence modifier, reset on long rest.", "You've gained the ability to come up with solutions under pressure. When you or another creature you can see within 30 feet of you makes an ability check or a saving throw, you can use your reaction to add your Intelligence modifier to the roll.<br><br>You can use this feature a number of times equal to your Intelligence modifier (minimum of once). You regain all expended uses when you finish a long rest.", 7, 0); -- 146
INSERT INTO class_features VALUES (NULL, 13, "Magic Item Adept", "Attune to 4 items.<br>Crafting common or uncommon magic items casts half and takes quarter of time.", "You've achieved a profound understanding of how to use and make magic items:<br><br><ul><li>You can attune to up to four magic items at once.</li><li>If you craft a magic item with a rarity of common or uncommon, it takes you a quarter of the normal time, and it costs you half as much of the usual gold.</li></ul>", 10, 0); -- 147
INSERT INTO class_features VALUES (NULL, 13, "Spell-Storing Item", "After a long rest store 1st or 2nd level, 1 action cast time, spell in a weapon. Action to produce spell effect, number of uses equal to twice your Intelligence modifier or you use this feature again.", "You can now store a spell in an object. Whenever you finish a long rest, you can touch one simple or martial weapon or one item that you can use as a spellcasting focus, and you store a spell in it, choosing a 1st or 2nd level spell from the artificer spell list that requires 1 action to cast (you needn't have it prepared).<br><br>While holding the object, a creature can take an action to produce the spell's effect from it, using your spellcasting ability modifier. If the spell requires concentration, the creature must concentrate. The spell stays in the object until it's been used a number of times equal to twice your Intelligence modifier (minimum of twice) or until you use this feature again to store a spell in an object.", 11, 0); -- 148
INSERT INTO class_features VALUES (NULL, 13, "Magic Item Savant", "Attune to 5 items at once.<br>Ignore all requirements on attunement or magic item use.", "Your skill with magic items deepens:<br><br><ul><li>You can attune to up to five magic items at once.</li><li>You ignore all class, race, spell, and level requirements on attuning to or using a magic item.</li></ul>", 14, 0); -- 149
INSERT INTO class_features VALUES (NULL, 13, "Magic Item Master", "Attune to 6 items at once.", "You can now attune to up to six magic items at once.", 18, 0); -- 150
INSERT INTO class_features VALUES (NULL, 13, "Soul of Artifice", "+1 bonus to saves per attunement.<br>If reduced to 0 HP, end attunement to drop to 1 HP.", "You have developed a mystical connection to your magic items, which you can draw on for protection:<br><br><ul><li>You gain a +1 bonus to all saving throws per magic item you are currently attuned to.</li><li>If you're reduced to 0 hit points but not killed outright, you can use your reaction to end one of your artificer infusions, causing you to drop to 1 hit point instead of 0.</li></ul>", 20, 0); -- 151
INSERT INTO archetype_features VALUES (NULL, 98, "Tool Proficiency", "Proficient with Alchemist's Supplies (or other).", "You gain proficiency with alchemist's supplies. If you already have this proficiency, you gain proficiency with one other type of artisan's tools of your choice.", 3); -- 509
INSERT INTO archetype_features VALUES (NULL, 98, "Alchemist Spells", "You learn additional spells that are always prepared, and don't count against the number of spells you can prepare.", "You always have certain spells prepared after you reach particular levels in this class, as shown in the Alchemist Spells table. These spells count as artificer spells for you, but they don't count against the number of artificer spells you prepare.<br><br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Level</th><th>Spells</th></tr></thead><tbody><tr><td>3rd</td><td>Healing Word, Ray of Sickness</td></tr><tr><td>5th</td><td>Flaming Sphere, Melf's Acid Arrow</td></tr><tr><td>9th</td><td>Gaseous Form, Mass Healing Word</td></tr><tr><td>13th</td><td>Blight, Death Ward</td></tr><tr><td>17th</td><td>Cloudkill, Raise Dead</td></tr></tbody></table></div>", 3); -- 510
INSERT INTO archetype_features VALUES (NULL, 98, "Experimental Elixir", "After a long rest produce 1 experimental elixir (2 at 6th, 3 at 15th), additional elixirs can be made by expending 1st level or higher spells slots for each one. Last until end of next long rest.<br>Action to consume or administer.", "Whenever you finish a long rest, you can magically produce an experimental elixir in an empty flask you touch. Roll on the Experimental Elixir table for the elixir's effect, which is triggered when someone drinks the elixir. As an action, a creature can drink the elixir or administer it to an incapacitated creature.<br><br>You can create additional experimental elixirs by expending a spell slot of 1 st level or higher for each one. When you do so, you use your action to create the elixir in an empty flask you touch, and you choose the elixir's effect from the Experimental Elixir table.<br><br>Creating an experimental elixir requires you to have alchemist supplies on your person, and any elixir you create with this feature lasts until it is drunk or until the end of your next long rest.<br><br>When you reach certain levels in this class, you can make more elixirs at the end of a long rest: two at 6th level and three at 1 5th level. Roll for each elixir's effect separately. Each elixir requires its own flask.<br><br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>d6</th><th>Effect</th></tr></thead><tbody><tr><td>1</td><td><strong>Healing.</strong> The drinker regains a number of hit points equal to 2d4 + your Intelligence modifier.</td></tr><tr><td>2</td><td><strong>Swiftness. </strong>The drinker's walking speed increases by 10 feet for 1 hour.</td></tr><tr><td>3</td><td><strong>Resilience. </strong>The drinker gains a +1 bonus to AC for 10 minutes.</td></tr><tr><td>4</td><td><strong>Boldness. </strong>The drinker can roll a d4 and add the number rolled to every attack roll and saving throw they make for the next minute.</td></tr><tr><td>5</td><td><strong>Flight. </strong>The drinker gainst a flying speed of 10 feet for 10 minutes.</td></tr><tr><td>6</td><td><strong>Transformation. </strong>The drinker's body is transformed as if by the alter self spell. The drinker determines the transformation caused by the spell, the effects of which last 10 minutes.</td></tr></tbody></table></div>", 3); -- 511
INSERT INTO archetype_features VALUES (NULL, 98, "Alchemical Savant", "When you cast a spell that restores HP or damages (with acid, fire, necrotic, or poison) add Intelligence modifier (min 1).", "You've developed masterful command of magical chemicals, enhancing the healing and damage you create through them. Whenever you cast a spell using your alchemist's supplies as the spellcasting focus, you gain a bonus to one roll of the spell. That roll must restore hit points or be a damage roll that deals acid, fire, necrotic, or poison damage, and the bonus equals your Intelligence modifier (minimum of +l).", 5); -- 512
INSERT INTO archetype_features VALUES (NULL, 98, "Restorative Reagents", "When a creature drinks an experimental elixir they gain 2d6 + Intelligence modifier (min 1) temporary HP.<br>Cast lesser restoration without expending a spell slot, number of times equal to Intelligence modifier (reset long rest).", "You can incorporate restorative reagents into some of your works:<br><br><ul><li>Whenever a creature drinks an experimental elixir you created, the creature gains temporary hit points equal to 2d6 + your Intelligence modifier (minimum of 1 temporary hit point).</li><li>You can cast lesser restoration without expending a spell slot and without preparing the spell, provided you use alchemist's supplies as the spellcasting focus. You can do so a number of times equal to your Intelligence modifier (minimum of once), and you regain all expended uses when you finish a long rest.</li></ul>", 9); -- 513
INSERT INTO archetype_features VALUES (NULL, 98, "Chemical Mastery", "Resistant to acid and poison, immune to poisoned condition.<br>Once per long rest cast greater restoration or heal without expending a spell slot (or preperation and material components).", "You have been exposed to so many chemicals that they pose little risk to you, and you can use them to quickly end certain ailments:<br><br><ul><li>You gain resistance to acid damage and poison damage, and you are immune to the poisoned condition.</li><li>You can cast greater restoration and heal without expending a spell slot, without preparing the spell, and without material components, provided you use alchemist's supplies as the spellcasting focus. Once you cast ·either spell with this feature, you can't cast that spell with it again until you finish a long rest.</li></ul>", 15); -- 514
INSERT INTO archetype_features VALUES (NULL, 99, "Tools of the Trade", "Proficient with heavy armour, smith's tools (or other of your choice).", "You gain proficiency with heavy armor. You also gain proficiency with smith's tools. If you already have this tool proficiency, you gain proficiency with one other type of artisan's tools of your choice.", 3); -- 515
INSERT INTO archetype_features VALUES (NULL, 99, "Armorer Spells", "You learn additional spells that are always prepared, and don't count against the number of spells you can prepare.", "You always have certain spells prepared after you reach particular levels in this class, as shown in the Alchemist Spells table. These spells count as artificer spells for you, but they don't count against the number of artificer spells you prepare.<br><br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Level</th><th>Spells</th></tr></thead><tbody><tr><td>3rd</td><td>Magic Missile, Thunderwave</td></tr><tr><td>5th</td><td>Mirror Image, Shatter</td></tr><tr><td>9th</td><td>Hypnotic Pattern, Lightning Bolt</td></tr><tr><td>13th</td><td>Fire Shield, Greater Invisibility</td></tr><tr><td>17th</td><td>Passwall, Wall of Force</td></tr></tbody></table></div>",3); -- 516
INSERT INTO archetype_features VALUES (NULL, 99, "Arcane Armor", "Action turn armour you're wearing into arcane armour, gain the following:<br><ul><li>Ignore Strength requirement.</li><li>Counts as spellcasting focus.</li><li>Can't be removed against your will. Retract or deploy the helmet as a bonus action. Replaces any missing limbs.</li><li>Doff or don the armor as an action.</li></ul>", "Your metallurgical pursuits have led to you making armor a conduit for your magic. As an action, you can turn a suit of armor you are wearing into Arcane Armor, provided you have smith's tools in hand.<br><br>You gain the following benefits while wearing this armor:<br><br><ul><li>If the armor normally has a Strength requirement, the arcane armor lacks this requirement for you.</li><li>You can use the arcane armor as a spellcasting focus for your artificer spells.</li><li>The armor attaches to you and can't be removed against your will. It also expands to cover your entire body, although you can retract or deploy the helmet as a bonus action. The armor replaces any missing limbs, functioning identically to a limb it replaces.</li><li>You can doff or don the armor as an action.</li></ul><br><br>The armor continues to be Arcane Armor until you don another suit of armor or you die.", 3); -- 517
INSERT INTO archetype_features VALUES (NULL, 99, "Armor Model", "Once per rest set Arcane Armor model: Guardian or Infiltrator.<br>Model special weapon uses Intelligence modifier (rather than Strength or Dexterity).", "You can customize your Arcane Armor. When you do so, choose one of the following armor models: Guardian or Infiltrator. The model you choose gives you special benefits while you wear it.<br><br>Each model includes a special weapon. When you attack with that weapon, you can add your Intelligence modifier, instead of Strength or Dexterity, to the attack and damage rolls.<br><br>You can change the armor's model whenever you finish a short or long rest, provided you have smith's tools in hand.<br><br><strong>Guardian.</strong> You design your armor to be in the front line of conflict. It has the following features:<br><br><ul><li><strong>Thunder Gauntlets.</strong> Each of the armor's gauntlets counts as a simple melee weapon while you aren't holding anything in it, and it deals ld8 thunder damage on a hit. A creature hit by the gauntlet has disadvantage on attack rolls against targets other than you until the start of your next turn, as the armor magically emits a distracting pulse when the creature attacks someone else.</li><li><strong>Defensive Field.</strong> As a bonus action, you can gain temporary hit points equal to your level in this class, replacing any temporary hit points you already have. You lose these temporary hit points if you doff the armor. You can use this bonus action a number of times equal to your proficiency bonus, and you regain all expended uses when you finish a long rest.</li></ul><br><br><strong>Infiltrator.</strong> You customize your armor for subtle undertakings. It has the following features:<br><br><ul><li><strong>Lightning Launcher.</strong> A gemlike node appears on one of your armored fists or on the chest (your choice). It counts as a simple ranged weapon, with a normal range of 90 feet and a long range of 300 feet, and it deals ld6 lightning damage on a hit. Once on each of your turns when you hit a creature with it, you can deal an extra ld6 lightning damage to that target.</li><li>Powered Steps. Your walking speed increases by 5 feet.</li><li>Dampening Field. You have advantage on Dexterity (Stealth) checks. If the armor normally imposes disadvantage on such checks, the advantage and disadvantage cancel each other, as normal.</li></ul>", 3); -- 518
INSERT INTO archetype_features VALUES (NULL, 99, "Exra Attack", "You can attack twice, rather than once, whenever you take the Attack action on your turn.", "You can attack twice, rather than once, whenever you take the Attack action on your turn.", 5); -- 519
INSERT INTO archetype_features VALUES (NULL, 99, "Armor Modifications", "Arcane Armor pieces count seperately for infusions (chest piece, boots, helmet, special weapon).<br>Can infuse 2 Arcane Armor pieces at once.", "You learn how to use your artificer infusions to specially modify your Arcane Armor. That armor now counts as separate items for the purposes of your Infuse Items feature: armor (the chest piece), boots, helmet, and the armor's special weapon. Each of those items can bear one of your infusions, and the infusions transfer over if you change your armor's model with the Armor Model feature. In addition, the maximum number of items you can infuse at once increases by 2, but those extra items must be part of your Arcane Armor.", 9); -- 520
INSERT INTO archetype_features VALUES (NULL, 99, "Perfected Armor", "<strong>Guardian.</strong> reaction target creature within 30ft, Strength save (spell save DC) pulling 30ft towards you on fail, make melee attack if within 5ft. Use times equal to proficiency bonus, reset long rest.<br><strong>Infiltrator.</strong> Creatures hit with Lightning Lure shed dim light for 5ft, has disadvantage on attacks against you, next attack roll against target has advantage, deals extra 1d6 lightning damage.", "Your Arcane Armor gains additional benefits based on its model, as shown below.<br><br><strong>Guardian.</strong> When a Huge or smaller creature you can see ends its turn within 30 feet of you, you can use your reaction to magically force the creature to make a Strength saving throw against your spell save DC, pulling the creature up to 30 feet toward you to an unoccupied space. If you pull the target to a space within 5 feet of you, you can make a melee weapon attack against it as part of this reaction.<br>You can use this reaction a number of times equal to your proficiency bonus, and you regain all expended uses of it when you finish a long rest.<br><br><strong>Infiltrator.</strong> Any creature that takes lightning damage from your Lightning Launcher glimmers with magical light until the start of your next turn. The glimmering creature sheds dim light in a 5-foot radius, and it has disadvantage on attack rolls against you, as the light jolts it if it attacks you. In addition, the next attack roll against it has advantage, and if that attack hits, the target takes an extra ld6 lightning damage.", 15); -- 521
INSERT INTO archetype_features VALUES (NULL, 100, "Tool Proficiency", "Proficient in woodcarver's tools (or another of your choice).", "You gain proficiency with woodcarver's tools. If you already have this proficiency, you gain proficiency with one other type of artisan's tools of your choice.", 3); -- 522
INSERT INTO archetype_features VALUES (NULL, 100, "Artillerist Spells", "You learn additional spells that are always prepared, and don't count against the number of spells you can prepare.", "You always have certain spells prepared after you reach particular levels in this class, as shown in the Alchemist Spells table. These spells count as artificer spells for you, but they don't count against the number of artificer spells you prepare.<br><br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Level</th><th>Spells</th></tr></thead><tbody><tr><td>3rd</td><td>Shield, Thunderwave</td></tr><tr><td>5th</td><td>Scorching Ray, Shatter</td></tr><tr><td>9th</td><td>Fireball, Wind Wall</td></tr><tr><td>13th</td><td>Ice Storm, Wall of Fire</td></tr><tr><td>17th</td><td>Cone of Cold, Wall of Force</td></tr></tbody></table></div>", 3); -- 523
INSERT INTO archetype_features VALUES (NULL, 100, "Eldritch Cannon", "Once per long rest (or expending a spell slot) use action to create Small or Tiny eldritch cannon.<br>AC = 18, HP = 5 x artificer level, immune poison and psychic.<br>Bonus action to activate the cannon (if within 60ft), and move it up to 15ft.", "You've learned how to create a magical cannon. Using woodcarver's tools or smith's tools, you can take an action to magically create a Small or Tiny eldritch cannon in an unoccupied space on a horizontal surface within 5 feet of you. A Small eldritch cannon occupies its space, and a Tiny one can be held in one hand.<br><br>Once you create a cannon, you can't do so again until you finish a long rest or until you expend a spell slot to create one. You can have only one cannon at a time and can't create one while your cannon is present.<br><br>The cannon is a magical object. Regardless of size, the cannon has an AC of 18 and a number of hit points equal to five times your artificer level. It is immune to poison damage and psychic damage. If it is forced to make an ability check or a saving throw, treat all its ability scores as 10 (+O). If the mending spell is cast on it, it regains 2d6 hit points. It disappears if it is reduced to 0 hit points or after 1 hour. You can dismiss it early as an action.<br><br>When you create the cannon, you determine its appearance and whether it has legs. You also decide which type it is, choosing from the options on the Eldritch Cannons table. On each of your turns, you can take a bonus action to cause the cannon to activate if you are within 60 feet of it. As part of the same bonus action, you can direct the cannon to walk or climb up to 1 5 feet to an unoccupied space, provided it has legs.<br><br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Cannon</th><th>Activation</th></tr></thead><tbody><tr><td>Flamethrower</td><td>The cannon exhales fire in an adjacent 15-foot cone that you designate. Each creature in that area must make a Dexterity saving throw against your spell save DC, taking 2d8 fire damage on a failed save or half as much damage on a successful one. The fire ignites any flammable objects in the area that aren't being worn or carried.</td></tr><tr><td>Force Ballista</td><td>Make a ranged spell attack, originating from the cannon, at one creature or object with in 120 feet of it. On a hit, the target takes 2d8 force damage, and if the target is a creature, it is pushed up to 5 feet away from the cannon.</td></tr><tr><td>Protector</td><td>The cannon emits a burst of positive energy that grants itself and each creature of your choice within 10 feet of it a number of temporary hit points equal to ld8 + your Intelligence modifier (minimum of +l).</td></tr></tbody></table></div>", 3); -- 524
INSERT INTO archetype_features VALUES (NULL, 100, "Arcane Firearm", "After a long rest turn wand, staff, or rod into Arcane Firearm. Use a spellcasting focus, when you cast a spell roll a d8 add result to one of the spell's damage rolls.", "You know how to turn a wand, staff, or rod into an arcane firearm, a conduit for your destructive spells. When you finish a long rest, you can use woodcarver's tools to carve special sigils into a wand, staff, or rod and thereby turn it into your arcane firearm. The sigils disappear from the object if you later carve them on a different item. The sigils otherwise last indefinitely.<br><br>You can use your arcane firearm as a spellcasting focus for your artificer spells. When you cast an artificer spell through the firearm, roll a d8, and you gain a bonus to one of the spell's damage rolls equal to the number rolled.", 5); -- 525
INSERT INTO archetype_features VALUES (NULL, 100, "Explosive Cannon", "Eldritch Cannon damage is 1d8. Action cause Cannon to detonate (if within 60ft), creatures within 20ft make Dexterity save, taking 3d8 force damage on fail, half on success.", "Every eldritch cannon you create is now more destructive:<br><br><ul><li>The cannon's damage rolls all increase by ld8.</li><li>As an action, you can command the cannon to detonate if you are within 60 feet of it. Doing so destroys the cannon and forces each creature within 20 feet of it to make a Dexterity saving throw against your spell save DC, taking 3d8 force damage on a failed save or half as much damage on a successful one.</li></ul>", 9); -- 526
INSERT INTO archetype_features VALUES (NULL, 100, "Fortified Position", "Eldritch Cannon conveys the following: allies have half cover while within 10ft, you can have two cannons at once (created and activated at the same time).", "You're a master at forming well-defended emplacements using your Eldritch Cannon:<br><br><ul><li>You and your allies have half cover while within 10 feet of a cannon you create with Eldritch Cannon, as a result of a shimmering field of magical protection that the cannon emits.</li><li>You can now have two cannons at the same time. You can create two with the same action (but not the same spell slot), and you can activate both of them with the same bonus action. You determine whether the cannons are identical to each other or different. You can't create a third cannon while you have two.</li></ul>", 15); -- 527
INSERT INTO archetype_features VALUES (NULL, 101, "Tool Proficiency", "Proficient with smith's tools (or another of your choice).", "You gain proficiency with smith's tools. If you already have this proficiency, you gain proficiency with
INSERT INTO archetype_features VALUES (NULL, 101, "Battle Smith Spells", "You learn additional spells that are always prepared, and don't count against the number of spells you can prepare.", "You always have certain spells prepared after you reach particular levels in this class, as shown in the Alchemist Spells table. These spells count as artificer spells for you, but they don't count against the number of artificer spells you prepare.<br><br><div class='table-responsive'><table class='table table-sm table-hover no-select'><thead class='thead-light'><tr><th>Level</th><th>Spells</th></tr></thead><tbody><tr><td>3rd</td><td>Heroism, Shield</td></tr><tr><td>5th</td><td>Branding Smite, Warding Bond</td></tr><tr><td>9th</td><td>Aura of Vitality, Conjure Barrage</td></tr><tr><td>13th</td><td>Aura of Purity, Fire Shield</td></tr><tr><td>17th</td><td>Banishing Smite, Mass Cure Wounds</td></tr></tbody></table></div>", 3); -- 529
INSERT INTO archetype_features VALUES (NULL, 101, "Battle Ready", "Proficient with martial weapons.<br>Use Intelligence modifier rather than Strength or Dexterity for attack and damage rolls with magic weapons.", "Your combat training and your experiments with magic have paid off in two ways:<br><br><ul><li>You gain proficiency with martial weapons.</li><li>When you attack with a magic weapon, you can use your Intelligence modifier, instead of Strength or Dexterity modifier, for the attack and damage rolls.</li></ul>", 3); -- 530
INSERT INTO archetype_features VALUES (NULL, 101, "Steel Defender", "Once per long rest make a Steel Defender.<br>Shares your initiative and takes its turn immediately after yours. Bonus action to command it to take an action, otherwise it takes the Dodge action.<br>Mending spell cures 2d6 HP.", "Your tinkering has borne you a companion, a steel defender. It's friendly to you and your companions, and it obeys your commands. See its game statistics in the Steel Defender stat block, which uses your proficiency bonus (PB) in several places. You determine the creature's appearance and whether it has two legs or four; your choice has no effect on its game statistics.<br><br>In combat, the defender shares your initiative count, but it takes its turn immediately after yours. It can move and use its reaction on its own, but the only action it takes on its turn is the Dodge action, unless you take a bonus action on your turn to command it to take another action. That action can be one in its stat block or some other action. If you are incapacitated, the defender can take any action of its choice, not just Dodge.<br><br>If the mending spell is cast on the defender, it regains 2d6 hit points. If it has died within the last hour, you can use your smith's tools as an action to revive it, provided you are within 5 feet of it and you expend a spell slot of 1 st level or higher. The defender returns to life after 1 minute with all its hit points restored.<br><br>At the end of a long rest, you can create a new steel defender if you have smith's tools with you. If you already have a defender from this feature, the first one immediately perishes. The defender also perishes if you die.", 3); -- 531
INSERT INTO archetype_features VALUES (NULL, 101, "Extra Attack", "You can attack twice, rather than once, whenever you take the Attack action on your turn.", "You can attack twice, rather than once, whenever you take the Attack action on your turn.", 5); -- 532
INSERT INTO archetype_features VALUES (NULL, 101, "Arcane Jolt", "Once per turn when you hit with a magical weapon or steel defender: deal extra 2d6 force damage, choose creature or object in 30ft restore 2d6 HP.<br>Use feature number of times equal to Intelligence modifier (min 1), reset on long rest.", "You've learned new ways to channel arcane energy to harm or heal. When either you hit a target with a magic weapon attack or your steel defender hits a target, you can channel magical energy through the strike to create one of the following effects:<br><br><ul><li>The target takes an extra 2d6 force damage.</li><li>Choose one creature or object you can see within 30 feet of the target. Healing energy flows into the chosen recipient, restoring 2d6 hit points to it.</li></ul><br><br>You can use this energy a number of times equal to your Intelligence modifier (minimum of once), but you can do so no more than once on a turn. You regain all expended uses when you finish a long rest.", 9); -- 533
INSERT INTO archetype_features VALUES (NULL, 101, "Improved Defender", "Arcane Jolt and Steel Defender gain benefits:<br><ul><li>Arcane Jolt damage is 4d6.</li><li>Steel Defender gains +2 AC.</li><li>When Steel Defender uses its Deflect Attack, attacker takes ld4 + Intelligence modifier force damage.</li></ul>", "Your Arcane jolt and steel defender have become more powerful:<br><br><ul><li>The extra damage and the healing of your Arcane Jolt both increase to 4d6.</li><li>Your steel defender gains a +2 bonus to Armor Class.</li><li>Whenever your steel defender uses its Deflect Attack, the attacker takes force damage equal to ld4 + your Intelligence modifier.</li></ul>", 15); -- 534
